		}
	}

	p = vec3(0,0,0);
	translate(p, vec3(7,7,0));
	rotate(p, vec3(0,0,1)*(u_time/2.));
	for (float i=0.; i<6.; i++) {
		//Материал шара
		Material material;
		if (mod(i,3.)==0.) material = red_reflect;
		if (mod(i,3.)==1.) material = green_reflect;
		if (mod(i,3.)==2.) material = blue_reflect;
		rotate(p, vec3(0,1,0)*(PI/3.));
		sphere = Sphere(p, 2.);
		d = intersect(ray, sphere);
		if (mapdata.distance > d) {
			mapdata.distance 	= d;
			mapdata.sphere 		= sphere;
			mapdata.material 	= material;
			mapdata.id 			= 1;
		}
	}

	p = vec3(0,0,0);
	translate(p, vec3(0,-7,7));
	rotate(p, vec3(1,0,0)*(u_time/2.));
	for (float i=0.; i<6.; i++) {
		//Материал шара
		Material material;
		if (mod(i,3.)==0.) material = red_mat;
		if (mod(i,3.)==1.) material = green_mat;
		if (mod(i,3.)==2.) material = blue_mat;
		rotate(p, vec3(0,1,0)*(PI/3.));
		sphere = Sphere(p, 2.);
		d = intersect(ray, sphere);
		if (mapdata.distance > d) {
			mapdata.distance 	= d;
			mapdata.sphere 		= sphere;
			mapdata.material 	= material;
			mapdata.id 			= 1;
		}
	}

	//Плоскость
	p = vec3(0,0,0);
	translate(p, vec3(0,4,0));
	plane = Plane(p, vec3(0,1,0));
    d = intersect(ray, plane);
	if (mapdata.distance > d) {
		mapdata.distance 	= d;
		mapdata.plane 		= plane;
		mapdata.material 	= metal_reflect;
		mapdata.id = 2;
    }

	return mapdata.distance;
}
//Нормаль в точке пересечения
vec3 mapNormal (Ray ray) {
	if (ray.mapdata.id==1) {
		//Сфера
		return (ray.position - ray.mapdata.sphere.center)/ray.mapdata.sphere.radius;
	} else if (ray.mapdata.id==2) {
		//Плоскость
//		return ray.mapdata.plane.normal;

		//Нормаль поверхности воды
		float lambda = 5.0;
		float ptx = ray.position.x - cos(ray.position.y );
		float ptz = ray.position.z - sin(ray.position.z );
		return normalize(vec3(  
			0.020 * sin( ptx * lambda * 0.5 + u_time * 7.1) +  
			0.010 * sin( (ptx + ptz * 0.3) * lambda + u_time * 8.0) + 
			0.005 * sin( (ptx - ptz * 0.9) * lambda*2.1 + u_time * 5.2),
			1.0, 
			0.020 * cos( ptz * lambda * 0.5 + u_time * 7.6) + 
			0.010 * cos( ptz * lambda + u_time * 8.0) + 
			0.005 * cos( ptz * lambda *1.9 + u_time * 5.6)
		));
	}
}
//Поиск пересечения луча с поверхностью
void rayMarch(inout Ray ray){
    ray.origin += ray.near * ray.direction;
	//Флаг успешности
	ray.hit		 = false;
	//Нормаль
	ray.normal	 = vec3(0);
	//Дистанция
    ray.distance = map(ray, ray.mapdata);
	//Точка пересечения
	ray.position = ray.origin + ray.distance * ray.direction;

	if (ray.distance>=ray.near && ray.distance<ray.far) {
		ray.hit			= true;
		//Определение нормали
		ray.normal = mapNormal(ray);
	}
}
//Освещение
vec3 lighting (vec3 p, vec3 normal, vec3 camPos, Light light, Material material, float shadow) {

    vec3 toLightDir = normalize(light.position - p);
    vec3 toCamDir 	= normalize(camPos - p);
    vec3 toLightDir_reflect = reflect (-toLightDir, normal);

	float diffuse 	= max(0., dot(toLightDir, normal));
	float spcFac 	= max(0., dot(toLightDir_reflect, toCamDir));
	float specular 	= pow(spcFac, material.factorSpecular);

    vec3 color = vec3(0.);
    color += material.colorAmbient;
	color += diffuse * material.colorDiffuse * (1. - shadow);
	color += specular * material.colorSpecular * (1. - shadow);
    color *= light.color;
	return color;
}

float softShadow(vec3 p, Light light, float k) {
	float res=1.;
	const float N=10.;
	const float eps = 0.005;
	float L = distance(p, light.position);
	for (float i=0.; i<N; i++) {
		float angle = 2.*PI/N*i;
		vec3 dir = normalize(vec3(sin(angle), 0., cos(angle)));
		vec3 pos = p + 0.5 * dir;
		float l = distance(pos, light.position);
		Ray ray1;
		{
			ray1.origin 	= pos;
			ray1.direction 	= normalize(light.position - pos);
			ray1.near 		= eps;
			ray1.far		= L;
		}
		rayMarch(ray1);

		if (ray1.hit) {
			res -= clamp(k * (L-l)/N, 0., 1.);
		}
	}
	return clamp(res, 0., 1.);
}

