//        cam.origin  = vec3(0, 8, 40. + mouse.z);
        cam.origin  = vec3(0, 8, 40.);

        rotate(cam.origin, vec3(1,1,1)*u_time/6.);

        cam.target  = vec3(0.,0.,0.);
        cam.up 		= vec3(0,1,0);
    }

    Ray ray = lookAt(uv, cam);
    {
        ray.near 	= 0.001;
        ray.far  	= Infinity;
    }

    //Фон
    vec3 color = clamp(vec3(0.45, 0.76, 0.98) * sin(u_time/6.), 0., 1.) ;

    //Общий фактор отражения
    float reflectFactor = 1.0;

    for (int i=0; i<3; i++) {
        //Расчет пересечения луча
        rayMarch(ray);

        if (ray.hit) {
/*
            //Расчет тени
            Ray ray1 = ray;
            {
                ray1.origin 	= ray.position;
                ray1.direction 	= normalize(light.position - ray.position);
            }
            rayMarch(ray1);

