            if (ray1.hit) {
                shadow = (ray.mapdata.id==2) ? (0.2) : (0.7); //Поверхность воды - 0.2
            }
*/
            float shadow = 1. - softShadow(ray.position, light, 4.0);
            //Освещение
            vec3 col = lighting(ray.position, ray.normal, ray.origin, light, ray.mapdata.material, shadow);
            //Добавляем отраженный свет
            color += col*(1.0 - ray.mapdata.material.reflectFactor)*reflectFactor;
            //Гамма-коррекия
            color *= pow(col,vec3(1./2.2));
            //Учитываем отражение материалы
            reflectFactor *= ray.mapdata.material.reflectFactor;
            if (reflectFactor < 0.1 ) break;
            //Отражаем луч от поверхности
            ray.origin 		= ray.position;
            ray.direction 	= reflect(ray.direction, ray.normal);
        }
    }

    GL_FragColor = vec4(color, 1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float progress = mod(iTime, 1.0) - 0.5; // (-0.5, 0.5]

    // Calculate fragment's coordinates in the NDC space
    vec2 centeredFragCoord = fragCoord.xy - iResolution.xy / 2.0;
    vec2 ndc = vec2(
        centeredFragCoord.x / iResolution.x,
        centeredFragCoord.y / iResolution.y) * 2.0;

    vec2 centerPosition = iMouse.xy - iResolution.xy / 2.0;
    vec2 centerNdc = vec2(
        centerPosition.x / iResolution.x,
        centerPosition.y / iResolution.y) * 2.0;

    // Center
    ndc = vec2(ndc.x - centerNdc.x, centerNdc.y - ndc.y);

    // ------------------    

    vec2 rotNdc = ndc;

    progress = pow(abs(progress), 3.0);

    // Calculate tapering along each arm
    float taperX = pow(100000.0 * abs(rotNdc.x), 1.6) * progress;
    float taperY = pow(100000.0 * abs(rotNdc.y), 1.6) * progress;

    // Calculate width along arm
    float sx = max(0.0, (1.0-rotNdc.x * rotNdc.x * taperY));
    float sy = max(0.0, (1.0-rotNdc.y * rotNdc.y * taperX));
    float alpha = (sx + sy) / 2.0;

    fragColor = vec4(alpha, alpha, alpha, 1.0);
}

