vec2 rot2D( vec2 v, float a)
{
    float c = cos(a); // rotation selon un axe
	float s = sin(a);

    return mat2(c,-s,s,c)*v;
}




float expoRapide(float a,float n)
{ //exponentiation rapide pour la spéculaire
    float s = 1.;

    while(n > 0.)
    {
        if(mod(n,2.) == 0.)
        {	
            a = a*a;
        	n /= 2.;
        }
        else
        {
            s = s*a;
            n -= 1.;
        }
    }

    return s;



}





