// objets dans la scene
float bb8(vec3 p, out vec2 uv, out int idLoc)
{
    float d1 = sphere( p, .5);
    float d2 = sphere( p - vec3(0.,.49,0.),.3);



    float d3 = sphere(p - vec3(0.,0.05,0.), .5);

    float d4 = sphere(p- vec3(0.,0.30,0.),.3);

    float d5 = sphere(p - vec3(0.,.59,0.25),0.05);

    float d6 = sphere(p - vec3(0.1,.51,0.27),0.03);

    float d7 = cube(p - vec3(0.1,.60,0.),vec3(0.015,0.5,0.015));


    float d =  max( -d3,  d2);

    d = max(d, -d4);

    d = min(d, d5);
    d = min(d, d6);
    d = min(d, d7);

    d = min(d1,d);



    if(d == d1)
    {   
        uv.x = p.x;
        uv.y = p.y;

        uv = rot2D(uv, -iTime); // uv pour les textures

        uv *= 2.;

        uv += 1.;

        idLoc = 0;

        //uv.x = uv.x*uv.x*uv.x;
        //uv.y = uv.y*uv.y*uv.y;
    }
    else if( d == d2)
    {
        uv.x = p.x;
        uv.y = p.y;
        uv *= 2.;

        idLoc = 0;

    }
    else if( (d == d5) || (d == d6))
    {
        idLoc = 1;
    }

    else
    {
        uv = vec2(0.);
    }




    return d;
}






float map(  vec3 p, out int id, out vec2 uv) // carte des différents objets
{
    float d1 = sphere( p - vec3( 2.,0.,0.), .5);

    vec2 uv2 = vec2(0.);

    vec3 pbb8 = p - vec3(0.,-.18,0.);


    pbb8.xz = rot2D(pbb8.xz, -iTime/2. + 1.5);

    pbb8 = pbb8 - vec3(1.,0.,0.);

    pbb8.xz = rot2D(pbb8.xz, -1.5);


    int idBb8 = 0;

    float d2 = bb8(pbb8, uv2, idBb8);


    vec3 posPlan1 =p - vec3(0.,-.68,0.);
    float d3 = plan(posPlan1);



    float mini = min(d2,d3);

    if(mini == d3)
    {
        vec2 uv3 = posPlan1.xz;


    	uv3.x = mod(uv3.x, 3.);
    	uv3.y = mod(uv3.y, 3.);

    	uv3 /= 3.;

        uv = uv3;

        id = 2;
    }
    else
    {
        uv = uv2;
        id = 1 + idBb8*10;
    }



    return min( d2 , d3 );
}





vec3 intersect( vec3 ro, vec3 rd, out int id, out float nbSteps, out bool inter, out vec2 uv)
{
    float t = 0.;

    float d = 0.; // calcul d'intersection

    nbSteps = maxSteps;
    for(float i = 0.0; i <= maxSteps ; i += 1.)
    {
        d = map(ro + t*rd, id, uv);

        if (d < epsilon)
        {
            nbSteps = i;
            inter = true;

            break;
        }


        t += d;
    }



 return ro + t*rd;   
}


vec3 grad( vec3 p) // calcul du gradient approché
{
    vec3 a = vec3(1.,0.,0.);
    vec3 b = vec3(0.,1.,0.);
    vec3 c = vec3(0.,0.,1.);
    vec2 uv = vec2(0.);

    vec3 g = vec3(0.);
    int id = 0;

    g.x += map( p + epsilon*a, id, uv) - map(p - epsilon*a, id, uv);
    g.y += map( p + epsilon*b, id, uv) - map(p - epsilon*b, id, uv);
    g.z += map( p + epsilon*c, id, uv) - map(p - epsilon*c, id, uv);

	return g;
}




float ombreDouce(vec3 ro, vec3 rd, float mint, float maxt, float k)
{ 

    float res = 1.;
    float d = 0.;
    int id = 0;
    vec2 uv = vec2(0.);

    for(float t = mint; t <= maxt; t += d)
    {
        d = map( ro + t*rd, id, uv);

        if(d <= epsilon)
        {
            return 0.;
        }

        res = min(res, k*d/t);
    }

    return res;
}












vec3 shader(vec3 ro, vec3 pImp, int id, float nbSteps, bool inter, vec2 uv)
{
    vec3 ambCol = 1.*vec3(0.2,0.,1.);


    if(!inter)
        return vec3(0.,0.,0.2);

    /// eclairage ambiant

    if(id == 2)
        {
            ambCol.x *= texture(iChannel0, uv).x;
            ambCol.y *= texture(iChannel0, uv).y;
            ambCol.z *= texture(iChannel0, uv).z;

        }
        else if(id == 1)
        {

            ambCol.x *= texture(iChannel1, uv).x;
            ambCol.y *= texture(iChannel1, uv).y;
            ambCol.z *= texture(iChannel1, uv).z;

        }


    ambCol *= 100./(100.+nbSteps);










    /// eclairage par les lumieres


    vec3 n = normalize(grad(pImp));

    vec3 col = vec3(0.);

    vec3 lPosActu = vec3(0.);
    vec3 lIntActu = vec3(0.);

    vec3 rc = normalize(ro - pImp);




    for( int i = 0; i < nbLight; i++)
    {
    	lPosActu = lPos[i];
    	lIntActu = lInt[i];

        vec3 rl = lPosActu - pImp;
        float dl = length(rl);
        rl = rl/dl;

        dl += .1;

    	///Diff///
    	vec3 diff = vec3(0.);

        diff += max(0., dot(rl,n)) * lIntActu / (dl*dl);


        ///Spec///
        vec3 spec = vec3(0.);

        vec3 sRefl = rl - 2.*dot(rl,n)*n;
        sRefl = - sRefl;

        if(id == 1)
        {

        spec += expoRapide( max(0.,dot(sRefl,rc)) , 64.)*.1  * lIntActu;
        }
        else
        {
            spec += expoRapide( max(0.,dot(sRefl,rc)) , 16.)*.1  * lIntActu;
        }

     	///Shadow///



        if (dot(n,rl) > 0.)
        {
        	float shadow = ombreDouce(pImp, rl, 0.01, length(lPosActu - pImp), 20.);
            diff *= shadow;
            spec *= shadow;
        }

        //// Textures ///

        if(id == 2)
        {
            diff.x *= texture(iChannel0, uv).x;
            diff.y *= texture(iChannel0, uv).y;
            diff.z *= texture(iChannel0, uv).z;

            spec.x *= texture(iChannel0, uv).x;
            spec.y *= texture(iChannel0, uv).y;
            spec.z *= texture(iChannel0, uv).z;
        }
        else if(id == 01)
        {



            diff.x *= texture(iChannel1, uv).x;
            diff.y *= texture(iChannel1, uv).y; // textures
            diff.z *= texture(iChannel1, uv).z;

            spec.x *= .8*texture(iChannel1, uv).x;
            spec.y *= .8*texture(iChannel1, uv).y;
            spec.z *= .8*texture(iChannel1, uv).z;

            //diff = vec3(1.,1.,1.);
        }
        else if(id == 11)
        {
         diff = vec3(0.);

         float Blink = (max(sin(5.*iTime),0.));

         Blink = 50.*expoRapide(Blink,200.);

         diff = vec3(Blink,Blink,Blink*.05);
        }

        col += diff;
        col += spec;
            }

    col += ambCol;

    return col;
        }



vec3 Reflsuiv(vec3 p, vec3 posCam, out int id, out float nbSteps, out bool inter, out vec2 uv)
{
    vec3 n = normalize(grad(p));

    vec3 rc = normalize( posCam - p);

    vec3 rRefl = rc - 2.*dot(rc,n)*n;
    rRefl = - rRefl;

    vec3 ro = p + 0.01*rRefl;

    return intersect(ro,rRefl, id, nbSteps, inter,uv);
}















void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - .5;

    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.,0.1,3.);

    vec3 rd = normalize( vec3(uv.xy,-1.));

    float a = iTime/2.;

  	rd.xz = rot2D(rd.xz,a);
    ro.xz = rot2D(ro.xz,a);

    int id = 0;
    float nbSteps = 0.;
    bool inter = false;
    vec2 uvTex = vec2(0.);

    vec3 pImp = intersect(ro,rd,id,nbSteps,inter,uv);

    vec3 col = shader(ro,pImp, id, nbSteps, inter,uv);



    if (inter && id == 1)
    {

    bool interRef = false;
    vec3 pImpR = Reflsuiv(pImp,ro,id,nbSteps,interRef,uvTex);            
    vec3 colRefl = shader(pImp,pImpR, id, nbSteps, interRef,uvTex);

    float fresnel = max(0.,1. - dot( normalize(ro - pImp), normalize(grad(pImp))));

    col += colRefl*  fresnel*fresnel*  (1.+texture(iChannel1, uv).x)/1.3;

    }



    col = 1. - 1./(col + 1.);

    //sortie vers l'ecran
    fragColor = vec4(col,1.0);
}

#define MAX_ITER 250

vec2 complex_sq(in vec2 p)
{
    vec2 res = vec2(p.x*p.x - p.y*p.y, p.x*p.y + p.y*p.x);
    return res;
}

vec3 palette(float t)
{
    vec3 a = vec3(0.1, 0.4, 0.7);
    vec3 b = vec3(1, 0.7, 0);
    vec3 c = vec3(1, 0, 1);
    vec3 d = vec3(0, 0, 0.4);

    if (t < 0.333)
    {
        return mix(a, b, 3.0*t);
    }
    else if (t < 0.666)
    {	
        return mix(b, c, 3.0*(t - 0.3333));
    }
    else
    {
        return mix(c, d, 3.0*(t - 0.6666));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // here are the interesting zoom points I found. You can try them out.
    vec2 zoomp1 = vec2(1.502, 0.000097);
    vec2 zoomp2 = vec2(0.747, 0.1229993);
    vec2 zoomp3 = vec2(0.7449985, 0.1230036);
    vec2 zoomp4 = vec2(-0.303, 0.0214);

    float factor = pow(2.0, 0.8*iTime);
    vec2 c = uv / factor - zoomp3; // plug in your zoom points.
    vec2 z = vec2(0);
    int iter = 0;
    for (int i = 0; i < MAX_ITER; ++i)
    {
        z = complex_sq(z) + c;
        iter = i;
        if (dot(z,z) > 4.0) break;
    }

    float t = float(iter) / float(MAX_ITER);

    vec3 col = palette(t);
    fragColor = vec4(col, 1.0);
}

#define exact

//circle centers can be anywhere in this
float crescent(vec2 p, vec2 p0, vec2 p1, float r0, float r1, float sign0, float sign1) {

    p -= p0;
    p1 -= p0;
#ifdef exact
    float d = length(p1);
    vec2 n = normalize(p1);
    vec2 nt = vec2(n.y,-n.x);
    p += -nt*max(dot(p,nt),0.0)*2.0;

    float a = (r0*r0 - r1*r1 + d*d) / (2.0 * d);

    if (a < r0) {
        float b = sqrt(r0*r0-a*a);
        vec2 corner = a*n-b*nt;
        vec2 q = p-corner;

        vec2 n0 = d*(corner-p1);
        n0 = vec2(n0.y,-n0.x);

        vec2 n1 = d*corner;
        n1 = vec2(-n1.y,n1.x);

        if (min(sign0*dot(q,n0),sign1*dot(q,n1)) > 0.0) {
            return length(q);
        }
	}
#endif
    float len = sign0*(length(p)-r0);
    len = max(len,sign1*(length(p-p1)-r1));
    return len;
}

//simplified version by iq
float crescent2(vec2 p, float r0, float r1, float d, float sign0, float sign1)
{
    float a = (r0*r0 - r1*r1 + d*d) / (2.0 * d);

    if( a < r0)
    {
        p.y = abs(p.y);
        float b = sqrt(r0*r0-a*a);
        float k = p.y*a - p.x*b;
        float h = min(d*sign0*(d*(p.y-b)-k ),
                      d*sign1*k);
        if (h>0.0)
        {
            return length(p-vec2(a,b));
        }
    }

    return max(sign0*(length(p          )-r0),
               sign1*(length(p-vec2(d,0))-r1));
}

float crescent(vec2 p, float r0, float r1, float d, float sign0, float sign1) {

    //taken from:
    //https://stackoverflow.com/questions/3349125/circle-circle-intersection-points
    float a = (r0*r0 - r1*r1 + d*d) / (2.0 * d);

#if 1
    if (a < r0) {
    	p.y = abs(p.y);

        float b = sqrt(r0*r0-a*a);
        vec2 corner = vec2(a,b);
        vec2 q = p-corner;

        vec2 n0 = d*(corner-vec2(d,0));
        n0 = vec2(n0.y,-n0.x);

        vec2 n1 = d*corner;
        n1 = vec2(-n1.y,n1.x);

        //uses dot product to determine which side of the lines the pixel is in
        if (min(sign0*dot(q,n0),sign1*dot(q,n1)) > 0.0) {
            return length(q);
        }

    }
#endif

    return max(sign0*(length(p)-r0),sign1*(length(p-vec2(d,0))-r1));
}

float line( vec2 pa, vec2 ba) 
{
    float h = max( dot(pa,ba)/dot(ba,ba), 0.0 );
    return length( pa - ba*h );
}

float visualize(vec2 p, float r0, float r1, float d, float sign0, float sign1) {

    //taken from:
    //https://stackoverflow.com/questions/3349125/circle-circle-intersection-points
    float a = (r0*r0 - r1*r1 + d*d) / (2.0 * d);

    float len = abs(length(p)-r0);
    len = min(len,abs(length(p-vec2(d,0))-r1));

    if (a < r0) {
    	p.y = abs(p.y);
        float b = sqrt(r0*r0-a*a);
        vec2 corner = vec2(a,b);
        vec2 q = p-corner;

        vec2 n0 = normalize((corner-vec2(d,0)));
        //n0 = vec2(n0.y,-n0.x);

        vec2 n1 = normalize(corner);
        //n1 = vec2(-n1.y,n1.x);

        len = min(len,abs(line(q,sign1*n0*10.0)));
        len = min(len,abs(line(q,sign0*n1*10.0)));
    }

    return max(0.5-len*iResolution.y/16.0,0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
