// Random normalized vectors with numbers from random.org (changable)
const vec2 x0y0 = vec2 (0.632267, -0.774750);
const vec2 x1y0 = vec2 (-0.697355, 0.716726);
const vec2 x0y1 = vec2 (0.849160, 0.528136);
const vec2 x1y1 = vec2 (0.316228, -0.948683);

// Ease function
float ease (float p) 
{
    return p * p * p * (p * (p * 6.0 - 15.0) + 10.0); 
}

// Creates perlin noise for 
float perlin (float x, float y)
{
    x = abs (mod (x, 2.0) - 1.0);
    y = abs (mod (y, 2.0) - 1.0);

    vec2 xy00 = vec2 (x, y);
    vec2 xy10 = vec2 (x - 1.0, y);
    vec2 xy01 = vec2 (x, y - 1.0);
    vec2 xy11 = vec2 (x - 1.0, y - 1.0);

    float s = dot (x0y0, xy00);
    float t = dot (x1y0, xy10);
    float u = dot (x0y1, xy01);
    float v = dot (x1y1, xy11);

    float sx = ease (x);
    float sy = ease (y);

    float a = s + sx * (t - s);
    float b = u + sx * (v - u);

    return (a + sy * (b - a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec2 uvt = (uv - 0.5) * iTime * 3.0;

    float f = max (0.0, perlin (uvt.x * 5.0, uvt.y * 2.0) - perlin (uvt.y * 3.0, uvt.x * 2.0)) * 2.0;
    vec4 c = vec4 (f, f, f, f);

    // Time varying pixel color
    vec4 col = vec4 (0.5 + 0.5 * cos (iTime+uv.xyx + vec3 (0.0, 2.0, 4.0)), 1.0);

    // Output to screen
    fragColor = col * (1.0 - intensity * 2.0) + (c * intensity * 2.0);
}

#define MAXITER 128
#define PI 3.1415926
float sh = 0.;
struct Ray {
   vec3 o;
   vec3 d;
