mat2 rot2D(float arc){
   float s = sin(arc);    
   float c = cos(arc);    
   return mat2(c,s,-s,c);
}

vec3 getCol(float v){
  float r = cos((v-0.75)*PI);
  float g = cos((v-0.55)*PI);  
  float b = cos((v-0.25)*PI);

  return vec3(r,g,b);  
}

vec3 ground(vec3 p ,vec3 c){
  p-=c;
  return vec3(p.y+2.-exp(p.z*0.01),3,0);  
}

vec3 sphere(vec3 p ,vec3 c){
   p-=c;

   float d = length(p);
   float r = 1.; 
   return vec3(d-r,0,d<r+0.01?1:0);  
}

vec3 box(vec3 p ,vec3 c){
   p-=c;

   p=abs(p); 

   float r = 1.01; 
   float d = max(p.z,max(p.x,p.y))-r;

   return vec3(d,1,d<r+0.01?1:0);  
}

vec3 obj1(vec3 p ,vec3 c){
  p-=c;

  float s = sin(p.x*6.+iTime*7.)*sin(p.y*6.)*sin(p.z*6.)*0.05;  
  return vec3(length(p)-1.+s,2,0);  
}

void cmp(inout vec3 a,vec3 b){
    a = a.x<b.x?a:b;
}
