vec3 map(vec3 p){
   vec3 ref = vec3(1000.0);

   cmp(ref,sphere(p,vec3(-3,0,0)));
   cmp(ref,box(p,vec3(0,0,5)));

   cmp(ref,obj1(p,vec3(3,sh*2.,0.)));

   cmp(ref,ground(p,vec3(0)));

   return ref; 
}

vec3 grad(vec3 p){
   vec2 e = vec2(1,0)*0.001;
   float m = map(p).x;

   return -normalize(vec3(m-map(p+e.xyy).x,m-map(p+e.yxy).x,m-map(p+e.yyx).x));
}


float shadow(vec3 p,vec3 ld){
  vec3 o =p;
  vec3 d = ld;
  vec3 np = vec3(0);
  vec3 ret = vec3(0);
  float t =0.1;  

  float m =100.0;
  for (int i=0;i<32;i++){

      np=o+t*d;
      ret = map(np);
      m = min(m,ret.x*15./t); 
      if(ret.x<=0.000||t>=32.){

           return smoothstep(0.,1.,m);
      }

      t+=ret.x;
   }
}

void shading(inout Ray r,inout vec3 col,vec3 p,vec3 lp,float depth,float id,float isin,float bt){
   vec3 n = grad(p);
   vec3 ld =  normalize(lp-p);
   vec3 rd = reflect(r.d,n); 
   float nl = smoothstep(0.,1.0,dot(ld,n)); 
   float hv = dot(normalize(-r.d+ld),n);
   float rv = 0.4+0.6*pow((1.-hv),5.);
   nl+=pow(nl,50000.)*5.;


   if(id==0.){
      col+=getCol(0.2)*(nl+rv)/bt;

   }else if(id==1.){

      col+=getCol(0.6)*(nl+rv)/(bt*depth);

   }else if(id==2.){

      col+=getCol(0.95)*(nl+rv)/bt;

   }else if(id==3.){
      vec2 muv = mod(floor(p.xz),2.); 
      float v = (muv.x>0. && muv.y>0.)?0.5:1.;
      col+=getCol(.21)*(nl+1.)*v;

   }  

   float s=shadow(p,ld);

   s = smoothstep(-0.6,1.,s);

   col/=depth*0.22;

   col*=s;

   if(id==0.||id==1.){
        if(isin==1.){
            rd = refract(r.d,n,0.6); 

        }else{
            rd = refract(r.d,n,1.5); 

        }

    } 

    r.o = p;
    r.d = rd;

}

void rayTrace(Ray r,vec3 lp,inout vec3 col){
   float t   = 0.;
   float bt  = 0.; 

   vec3  ret = vec3(0);
   vec3  p = vec3(0);

   for (int i=0;i<MAXITER;i++){
      p=r.o+t*r.d;

      ret = map(p);

       if(t>=64.||bt>=8.){

         return;
      }
      if(ret.x<=0.0005){
         bt++; 
         shading(r,col,p,lp,t,ret.y,ret.z,bt);

      }else{
         t+=ret.x;
      } 
   }

}
void setCamera(inout Ray r,vec3 eye,vec3 tar,vec2 uv){
   vec3 look  = normalize(tar-eye);
   vec3 right = normalize(cross(vec3(0,1,0),look)); 
   vec3 up    = normalize(cross(look,right));

   r.o = eye;
   r.d = normalize(uv.x*right+uv.y*up+look);
}

