struct bezier_segment
{
    vec2[4] p;
};

vec2 bezier(vec2[4] p, float t)
{    
    return pow(1.0-t,3.0)*p[0] + 3.0*pow(1.0-t,2.0)*t*p[1] + 3.0*(1.0-t)*t*t*p[2] + pow(t,3.0)*p[3];
}

//t can go beyond 1
vec2 multibezier(bezier_segment[2] curves, float t)
{
    return bezier(curves[int(floor(t))].p, t-floor(t));
}


