// Raymarch anything #3 - using one of IQs raymarchers :)
//
// RayMarch + Shadow base code from here: https://www.shadertoy.com/view/lsKcDD
//------------------------------------------------------------------

float sdCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float pMod1(inout float p, float size)
{
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

float hash(vec2 p)  // replace this by something better
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return fract( p.x*p.y*(p.x+p.y) );
}


float smin( float a, float b, float k )
{
	float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}

mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}

float objID = 0.0;
float svobjID = 0.0;

#define	TAU 6.28318

//------------------------------------------------------------------



float map(vec3 p)
{
    float time=iTime;
    float zo = time*6.75;
    float modz = mod(zo,44.0);

    float t2 = fract(time*1.1) * TAU;
    float t3 = fract(time*0.7) * TAU;
    float t4 = fract(time*0.14)* TAU;

    float m = 0.5+sin(t2+p.z*0.4)*0.5;
    m += 0.5+cos(p.z*0.4+t3+p.x*0.4)*0.5;

    p.xy *= rotate(p.z * .08 + t4);

	p.z += modz;
    float c1 = pMod1(p.z,44.0);

	float dist = 3.5 -abs(p.y)+m;

    vec3 p2 = p;
	float cz = pMod1(p2.z,4.0);
	float cx = pMod1(p2.x,4.0);

    float r = hash(vec2(cz+(cz*0.31),cx+(cx*0.61)));

    if (abs(cx)<1.0)
        r=0.0;

    if (iMouse.z < 0.5)
    {
        if (r>0.75)
        {
            float d2 = sdCylinder(p2,vec2(0.7,5.5));         
            dist = smin(dist,d2,1.1);
        }
    }
    else
    {
        // nipple mix
        if (r>0.875)
        {
            float d2 = sdSphere(p2+vec3(0.0,3.2+m,0.0),0.8);
            float d3 = sdSphere(p2-vec3(0.0,3.2+m,0.0),0.8);
            dist = smin(dist,d2,1.1);
            dist = smin(dist,d3,1.1);
        }
    }


    objID = abs(p.z)/44.0;
    return dist;
}


