vec4 iRoundedCone( in vec3  ro, in vec3  rd, 
                   in vec3  pa, in vec3  pb, 
                   in float ra, in float rb,
                   out float v )
{
    vec3  ba = pb - pa;
	vec3  oa = ro - pa;
    float rr = ra - rb;

    float m0 = dot(ba,ba);
    float m1 = dot(ba,oa);
    float m2 = dot(ba,rd);
    float m3 = dot(rd,oa);
    float m5 = dot(oa,oa);

    float d2 = m0 - rr*rr;

    float k2 = d2    - m2*m2;
    float k1 = d2*m3 - m1*m2 + m2*rr*ra;
    float k0 = d2*m5 - m1*m1 + m1*rr*ra*2.0 - m0*ra*ra;

	float h = k1*k1 - k0*k2;
	if(h < 0.0) return vec4(-1.0);
    float t = (-sqrt(h)-k1)/k2;

    // body
    float y = m1 - ra*rr + t*m2;
    if( y>0.0 && y<d2 ) 
    {
        v = y/d2;
        return vec4(t, normalize(oa+t*rd-ba*v) );
    }

	// base cap
	h = m3*m3 - m5 + ra*ra;
	if( h>0.0 )
    {
        v = 0.0;
        t = -m3 - sqrt( h );
	    return vec4( t, (oa+t*rd)/ra );
    }
    return vec4(-1.0);
}

