#define AA 2

const int num = 80; // make this bigger for more precision curve


vec3 path( in float h )
{
    return 0.5*sin(6.2831*h*vec3(3.0,2.0,5.0)+vec3(0.0,1.0,3.0)+0.5*iTime*vec3(1.1,1.3,1.7));
}

float size( in float h )
{
    return 0.04 + 0.03*sin(6.2831*h*8.0+1.3);
}

vec4 intersect( in vec3 ro, in vec3 rd, out float h )
{
    vec3  oce = path(0.0);
    float ora = size(0.0);
    vec4 tnor = vec4(1e20);
    float hm = 0.0;
    for( int i=1; i<=num; i++ )
    {
        float h = float(i)/float(num);
        vec3  ce = path(h);
        float ra = size(h);

        vec4 tmp; float tmpv;
        tmp = iRoundedCone( ro, rd, oce, ce, ora, ra, tmpv );
        if( tmp.x>0.0 && tmp.x<tnor.x ) { tnor = tmp; hm=h+tmpv/float(num); }

        oce = ce;
        ora = ra;
    }

    h = hm;
    return tnor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.01*iTime;
	vec3 ro = vec3( 1.0*sin(an), 0.4, 1.0*cos(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );


        // raytrace
        float hm;
        vec4 tnor = intersect( ro, rd, hm );
        float t = tnor.x;

        // backround        
        vec3 col = vec3(0.015)*(1.0-0.15*dot(p,p));

        // tubes
        if( t<1e19 )
        {
            // material
            vec3 mate = 0.5 + 0.5*cos(2.0*hm*6.2831+vec3(0.0,2.0,3.0));

            // lighting
            vec3 pos = ro + t*rd;
            vec3 nor = tnor.yzw;
            const vec3 lig = vec3(0.8,0.6,0.0);
            vec3 hal = normalize(lig-rd);
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            float spe = clamp(dot(nor,hal),0.0,1.0);
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            float kk; float sha = (intersect( pos+nor*0.001, lig, kk ).x<1e19) ? 0.0 : 1.0;
            float fre = clamp(1.0+dot(rd,nor),0.0,1.0);

            col = vec3(0.05,0.1,0.15)*amb + vec3(1.0,0.8,0.5)*dif*sha;
            col *= mate;
            col += 0.5*pow(spe,16.0)*dif*sha;
            col += fre*fre*0.2*mate*(0.5+0.5*dif*sha);
        }

        // gamma
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // dither to remove banding in the background
    tot += fract(sin(fragCoord.x*vec3(13,1,11)+fragCoord.y*vec3(1,7,5))*158.391832)/255.0;

	fragColor = vec4( tot, 1.0 );
}

#define LEVEL 15U
#define WIDTH ( (1U << LEVEL) )
#define AREA ( WIDTH * WIDTH )

float HilbertIndex( uvec2 Position )
{   
    uvec2 Regions;
    uint Index = 0U;
    for( uint CurLevel = WIDTH/2U; CurLevel > 0U; CurLevel /= 2U )
    {
        uvec2 Region = uvec2(greaterThan((Position & uvec2(CurLevel)), uvec2(0U)));
        Index += CurLevel * CurLevel * ( (3U * Region.x) ^ Region.y);
        if( Region.y == 0U )
        {
            if( Region.x == 1U )
            {
                Position = uvec2(WIDTH - 1U) - Position;
            }
            Position.xy = Position.yx;
        }
    }

    return float(Index) / float(AREA);
}

vec4 mirrored(vec4 v)
{
    vec4 Mod = mod(v, 2.0);
    return mix(Mod, 2.0 - Mod, step(1.0, Mod));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 UV = fragCoord/iResolution.xy;
    UV.x *= iResolution.x / iResolution.y;
    UV.x -= fract((iResolution.x ) / iResolution.y) / 2.0;

    uvec2 FragCoord = uvec2( UV * float(WIDTH) );
    float Index = HilbertIndex( FragCoord );

    Index += iTime / 12.0;
	vec2 Border = smoothstep(vec2(0.0), vec2(0.0) + vec2(0.01), UV) -
        smoothstep(vec2(1.0) - vec2(0.01), vec2(1.0), UV);
    fragColor = mirrored(
        vec4(
            Index * 7.0,
            Index * 11.0,
            Index * 13.0,
            1.0
        )
	) * (Border.x * Border.y);
}

