const float scale = 3.0;
const float timeScale = 0.5;
const float PI = 3.1415926535;

//  Function from Iñigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

float arctan(vec2 v) {
	return atan(v.y, v.x);
}

vec3 col(vec2 complex) {
	float normAng = arctan(-complex) / PI / 2.0 + 0.5;
	float len = length(complex) + 1.0;
	len = 1.0 / len;

	return hsb2rgb(vec3(normAng, 1.0, len));
}

vec2 sq(vec2 c) {
	return vec2(c.x*c.x - c.y*c.y, 2.0*c.x*c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
	vec2 screenPos = fragCoord.xy - vec2(0.5, 0.5) * iResolution.xy;
	screenPos /= min(iResolution.x, iResolution.y);
	screenPos *= scale;
    screenPos += vec2(-0.6,0.0);

    vec2 z = vec2(0.0);
	vec2 pz = z;
	float ntime = mod(iTime * timeScale, 30.0);
	float dtime = ntime - floor(ntime);

	for (float i = 0.0; i < ntime + 1.0; i++) {
        pz = z;
        z = sq(pz) + screenPos;
    }

    z = mix(pz, z, dtime);

    fragColor = vec4(col(z), 1.0);
}

