const float phi = (1.+sqrt(5.))*.5;

float noise(vec3 p)
{
    return fract(
        sin(
            dot(p, vec3(12.4536728,432.45673828,32.473682))
        )*43762.342);
}

vec2 rotate(vec2 a, float b)
{
    float c = cos(b);
    float s = sin(b);
    return vec2(
        a.x * c - a.y * s,
        a.x * s + a.y * c
    );
}

float sdIcosahedron(vec3 p, float r)
{
    const float q = (sqrt(5.)+3.)/2.;

    const vec3 n1 = normalize(vec3(q,1,0));
    const vec3 n2 = vec3(sqrt(3.)/3.);

    p = abs(p/r);
    float a = dot(p, n1.xyz);
    float b = dot(p, n1.zxy);
    float c = dot(p, n1.yzx);
    float d = dot(p, n2.xyz)-n1.x;
    return max(max(max(a,b),c)-n1.x,d)*r; // turn into (...)/r  for weird refractive effects when you subtract this shape
}

float sdDodecahedron(vec3 p, float r)
{
    const vec3 n = normalize(vec3(phi,1,0));

    p = abs(p/r);
    float a = dot(p,n.xyz);
    float b = dot(p,n.zxy);
    float c = dot(p,n.yzx);
    return (max(max(a,b),c)-n.x)*r;
}

float scene(vec3 p)
{
    p.xy = rotate(p.xy, p.z*.05);

    float n = noise(floor((p)/4.));
    float shape = fract((floor(p.x/4.)+floor(p.y/4.)*2.)/4.);
    float spinOffset1 = floor(p.z/4.);
    float spinOffset2 = floor(p.z/4.+2.);
    float spinOffset3 = floor(p.z/4.+4.);


    p = mod(p,4.)-2.;
    p.xy = rotate(p.xy, iTime+spinOffset1);
    p.yz = rotate(p.yz, iTime+spinOffset2);
    p.zx = rotate(p.zx, iTime+spinOffset3);

    if (shape < .25) {
        return min(
            sdDodecahedron(p,1.),
            sdIcosahedron(p.zyx,1.)
        );
    } else if (shape < .5) {
        return max(
            sdDodecahedron(p,1.),
            -sdIcosahedron(p.zyx,.9)
        );
    } else if (shape < .75) {
        return max(
            -sdDodecahedron(p,.95),
            sdIcosahedron(p.zyx,1.)
        );
    } else {
        return max(
            sdDodecahedron(p,.95),
            sdIcosahedron(p.zyx,1.)
        );
    }
}

void mainImage(out vec4 out_color, vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;

    uv *= 1.+length(uv)*.5;

    vec3 cam = vec3(0,0,0);
    vec3 dir = normalize(vec3(uv,1));

    //cam.yz = rotate(cam.yz, .3);
    //dir.yz = rotate(dir.yz, .3);

    cam.z = iTime * 4.;
    dir.xy = rotate(dir.xy, iTime*.1);
    //dir.yz = rotate(dir.yz, iTime*.3);
    //dir.zx = rotate(dir.zx, iTime*.3);

    float t = 0.;
    float k = 0.;
    int i;
    for(i=0;i<100;++i)
    {
        k = scene(cam+dir*t)*.55;
        t += k;
        if (k < .001) break;
    }

    vec3 h = cam+dir*t;
    vec2 o = vec2(.001, 0);
    vec3 n = normalize(vec3(
        scene(h+o.xyy)-scene(h-o.xyy),
        scene(h+o.yxy)-scene(h-o.yxy),
        scene(h+o.yyx)-scene(h-o.yyx)
    ));

    float iterFog = pow(1.-(float(i)/100.), 2.);
    float light = pow(max(0.,n.x*.5+.5),2.);
    float vignette = smoothstep(2.,0.,length(uv));
    vec3 a = mix(vec3(.01,.01,.1),vec3(0,1,1),iterFog);
    vec3 b = mix(vec3(0,0,0),vec3(1,sin(iTime*.4)*.5+.5,cos(iTime*.4)*.5+.5),light*iterFog*4.);
    out_color.rgb = a + b;
    out_color *= vignette;
}

////////////////////////////////////////////////////////////////////////////////
//
// Projective geometry: the eleven point conic, dual.
//
// Take 4 lines (red) and the 6 points (yellow) on intersections of pairs of lines,
// forming a complete quadrilateral.
//
// There are 3 diagonal lines joining each point where two lines meet
// to the point where the other two lines meet (cyan).
//
// Given a point l (red), the 6 quadrilateral points form 6 lines with l (purple)
// and each line has a harmonic conjugate with the two lines of the quadrangle
// going through the same point (green).
//
// Additionally, the 4 lines of the quadrilateral and the point l
// define a pair of conics (blue and grey), tangent to the 4 lines
// and passing through the point (these conics may be imaginary though).
//
// The three diagonal liness, the six harmonic lines and the two lines
// of tangency of the pair of conics at l are all tangent to a third conic,
// the eleven point conic (white).
//
// Copyright (c) Matthew Arcus, 2018
// MIT License: https://opensource.org/licenses/MIT
//
// Controls:
// Drag mouse to change line
// '1' and '2': change initial configuration
// 'f': change line & point drawing style
// 'z': zoom in
//
////////////////////////////////////////////////////////////////////////////////

int configuration = 2;

const float lwidth = 0.015;
const float pwidth = 0.045;
float ledge0 = 0.0, ledge1 = lwidth;
float pedge0 = lwidth, pedge1 = pwidth;
const float scale = 2.5;
float zoom = 1.0;

const float PI =  3.141592654;

const vec3 pcolor0 = vec3(1,0,0);
const vec3 pcolor1 = vec3(0,1,0);
const vec3 pcolor2 = vec3(1,1,0);
const vec3 pcolor3 = vec3(0,1,1);
const vec3 pcolor4 = vec3(0.5,0,0.5);
const vec3 lcolor0 = pcolor0;
const vec3 lcolor1 = pcolor1;
const vec3 lcolor2 = vec3(1,1,0.2);
const vec3 ccolor0 = vec3(1,1,1);
const vec3 ccolor1 = vec3(0,0,1);
const vec3 ccolor2 = vec3(0.5,0.5,1);

// Solve Ax^2 + 2Bx + C = 0
bool quadratic(float A, float B, float C,
               out float x1, out float x2) {
   float D = B*B - A*C;
   if (D < 0.0) return false;
   D = sqrt(D);
   if (B < 0.0) D = -D;
   x1 = (-B-D)/A;
   x2 = C/(A*x1);
   return true;
}

// Return either intersection of lines p and q
// or line through points p and q, r = kp + jq
vec3 join(vec3 p, vec3 q) {
  return cross(p,q);
}

// Find a projective mapping taking p0,p1,p2,p4 to
// triangle of reference and unit point, ie:
// p0 -> (1,0,0), p1 -> (0,1,0), p2 -> (0,0,1), p3 -> (1,1,1)
// No three points collinear.
mat3 rproject(vec3 p0, vec3 p1, vec3 p2, vec3 p3) {
  // Just an inverse for the first three points
  // (the triangle of reference). No inverse if collinear.
  mat3 m = inverse(mat3(p0,p1,p2)); // column major!
  p3 = m*p3; 
  // Then scale each row so the unit point (1,1,1) is correct
  m = transpose(m);
  // zero components here only if not collinear
  m[0] /= p3[0];
  m[1] /= p3[1];
  m[2] /= p3[2];
  return transpose(m);
}

// Represent a projective conic as a 3x3 matrix:
//
// M = (a,d,e,
//      d,b,f,
//      e,f,c)
//
// is: axx + byy + czz + 2(dxy + exz + fyz) = 0
// calculated as pMp for p = (x,y,z).
//
// We can treat this as a distance field, scaled by the
// (x,y) derivative in order to get correct line widths.

// With this representation, the dual conic is just the inverse;
// if the determinant is zero then there is no dual and the
// conic is degenerate.

// Conic through 4 points and a line - there are 2 solutions, so
// need to solve quadratic. I'm sure I didn't invent this but
// can't remember the source.
// l.p = l'p == l'(MinvM)p = (Minv'l)'Mp
bool conic4points(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 l4, out mat3 M0, out mat3 M1) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // and then to the square (1,1),(1,-1),(-1,1),(-1,-1)
  mat3 q = rproject(vec3(1,-1,1), vec3(-1,1,1),vec3(-1,-1,1),vec3(1,1,1));
  p = inverse(q)*p; // q is constant
  // Now construct a conic through the images of p0-p3 and l4
  l4 = transpose(inverse(p))*l4;
  float i = l4.x, j = l4.y, k = l4.z;
  float A = -j*j, B = 0.5*(i*i+j*j-k*k), C = -i*i;
  float t0,t1;
  if (!quadratic(A,B,C,t0,t1)) return false;
  M0 = mat3(t0, 0, 0,
            0, -1, 0,
            0,  0, 1.0-t0);
  M0 = transpose(p)*M0*p;
  if (isnan(determinant(M0))) return false;
  M1 = mat3(t1, 0, 0,
            0, -1, 0,
            0,  0, 1.0-t1);
  M1 = transpose(p)*M1*p;
  if (isnan(determinant(M1))) return false;
  return true;
}

// Find the two points of intersection of a line through p0 and
// p1 with the conic defined by M.
bool lineconic(vec3 p0, vec3 p1, mat3 M, out vec3 q0, out vec3 q1) {
  // (tp0 + p1)M(tp0 + p1) == 0
  float A = dot(p0,M*p0);
  float B = dot(p0,M*p1);
  float C = dot(p1,M*p1);
  float t0,t1;
  if (!quadratic(A,B,C,t0,t1)) return false;
  q0 = t0*p0+p1;
  q1 = t1*p0+p1;
  return true;
}

// Find the two points of intersection of the line l
// with the conic defined by c.
bool lineconic(vec3 l, mat3 M, out vec3 i0, out vec3 i1) {
  // Intersect l with two of the axes. This might of course
  // be the same point (1,0,0) here, so should use the third
  // axis.
  vec3 p0 = join(l,vec3(0,0,1));
  vec3 p1 = join(l,vec3(0,1,0));
  return lineconic(p0,p1,M,i0,i1);
}

// Construct the conic defined by 5 points.
// Method taken from "Geometry", Brannan, Esplan & Gray, CUP, 2012
mat3 conic5points(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 p4) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // Now construct a conic through the images of p0-p4,
  p4 = p*p4;
  float a = p4.x, b = p4.y, c = p4.z;
  float d = c*(a-b);
  float e = b*(c-a);
  float f = a*(b-c);
  mat3 m = mat3(0,d,e,
                d,0,f,
                e,f,0);
  // And combine the two.
  return transpose(p)*m*p;
}

// Harmonic conjugate of p,q and intersection of p,q with l.
vec3 harmonic(vec3 p, vec3 q, vec3 l) {
  // kp + jq is conjugate of kp - lq:
  // l.(kp - jq) = 0
  // k(l.p) = j(l.q)
  float k = dot(l,q);
  float j = dot(l,p);
  return k*p+j*q;
}

