float line(vec3 pos, vec3 p) {
  if (p.z == 0.0) return 0.0;
  pos /= pos.z; p /= p.z; // Normalize
  float dist = distance(pos,p);
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(pedge0,pedge1,dist);
}

float point(vec3 pos, vec3 l) {
  float dist = dot(pos,l)/(pos.z*length(l.xy));
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(ledge0,ledge1,abs(dist));
}

float conic(vec3 pos, mat3 m) {
  m = inverse(m);
  float dist = dot(pos,m*pos);   // Distance from the conic
  vec3 grad = m*pos*2.0;         // The gradient uses the same matrix.
  dist /= pos.z*length(grad.xy); // Normalize for Euclidean distance
  if (isnan(dist)) return 0.0;
  return 1.0-smoothstep(ledge0,ledge1,abs(dist));
}

// Screen coords to P2 coords
vec3 map(vec2 p) {
  return vec3(scale*zoom*(2.0*p - iResolution.xy) / iResolution.y, 1);
}

