void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float pixelwidth = 2.0*scale/iResolution.y;
  if (keypress(CHAR_Z)) zoom = 0.25;
  if (!keypress(CHAR_F)) {
      pedge0 = pwidth-pixelwidth;
      pedge1 = pwidth+pixelwidth;
      ledge0 = lwidth-pixelwidth;
      ledge1 = lwidth+pixelwidth;
  }
  ledge1 = max(ledge1,1.5*pixelwidth);
  configuration = int(keypress(CHAR_0+1)) + 2*int(keypress(CHAR_0+2));
  vec3 pos = map(fragCoord.xy);
  vec3 p0,p1,p2,p3,l4; // p4 is the movable line
  if (configuration == 3) {
    p0 = vec3(1,0,0); p1 = vec3(0,1,0);
    p2 = vec3(0,0,1); p3 = vec3(1,1,1);
    l4 = vec3(0.5,-1,1);
  } else if (configuration == 1) {
    p0 = vec3(0,0,1); p1 = vec3(1,0,1);
    p2 = vec3(0,1,1); p3 = vec3(1,1,1);
    l4 = vec3(0.5,-1,1);
  } else if (configuration == 2) {
    p0 = vec3(1,1,1); p1 = vec3(0,1,1);
    p2 = vec3(0.866,-0.5,1); p3 = vec3(-0.866,-0.5,1);
    l4 = vec3(0.5,-1,1);
  } else {
    p0 = vec3(1,-2,1);  p1 = vec3(-1,-2,1);
    p2 = vec3(2,1,1); p3 = vec3(-2,1,1);
    l4 = vec3(-1,-1,1);
  }
  p0 = transform(p0); p1 = transform(p1);
  p2 = transform(p2); p3 = transform(p3);
  if (iMouse.x != 0.0) {
    l4 = map(iMouse.xy);
  }
  vec3 l01 = join(p0,p1);
  vec3 l02 = join(p0,p2);
  vec3 l03 = join(p0,p3);
  vec3 l12 = join(p1,p2);
  vec3 l13 = join(p1,p3);
  vec3 l23 = join(p2,p3);

  // diagonal points
  vec3 d0 = join(l01,l23);
  vec3 d1 = join(l02,l13);
  vec3 d2 = join(l03,l12);

  // harmonic points
  vec3 h01 = harmonic(p0,p1,l4);
  vec3 h02 = harmonic(p0,p2,l4);
  vec3 h03 = harmonic(p0,p3,l4);
  vec3 h12 = harmonic(p1,p2,l4);
  vec3 h13 = harmonic(p1,p3,l4);
  vec3 h23 = harmonic(p2,p3,l4);

  // conics
  mat3 elevenpoint = conic5points(d0,d1,d2,h01,h23);
  mat3 M1,M2;
  bool doM1M2 = conic4points(p0,p1,p2,p3,l4,M1,M2);
  vec3 i0,i1;
  bool doN = lineconic(l4,elevenpoint,i0,i1);

  vec3 color = vec3(0);

  // The points of the quadrangle
  color = cmix(color,pcolor0,point(pos,p0));
  color = cmix(color,pcolor0,point(pos,p1));
  color = cmix(color,pcolor0,point(pos,p2));
  color = cmix(color,pcolor0,point(pos,p3));

#if 1
  // The midpoints of the sides of the quadrangle
  color = cmix(color,pcolor1,point(pos,h01));
  color = cmix(color,pcolor1,point(pos,h02));
  color = cmix(color,pcolor1,point(pos,h03));
  color = cmix(color,pcolor1,point(pos,h12));
  color = cmix(color,pcolor1,point(pos,h13));
  color = cmix(color,pcolor1,point(pos,h23));

  // The magic triangle
  color = cmix(color,pcolor3,point(pos,d0));
  color = cmix(color,pcolor3,point(pos,d1));
  color = cmix(color,pcolor3,point(pos,d2));

  color = cmix(color,pcolor4,point(pos,join(l01,l4)));
  color = cmix(color,pcolor4,point(pos,join(l02,l4)));
  color = cmix(color,pcolor4,point(pos,join(l03,l4)));
  color = cmix(color,pcolor4,point(pos,join(l12,l4)));
  color = cmix(color,pcolor4,point(pos,join(l13,l4)));
  color = cmix(color,pcolor4,point(pos,join(l23,l4)));

  if (doN) {
    color = cmix(color, pcolor2, point(pos,i0));
    color = cmix(color, pcolor2, point(pos,i1));
  }

  // The conics
  color = cmix(color,ccolor0,conic(pos,elevenpoint));
  if (doM1M2) {
    color = cmix(color,ccolor1,conic(pos,M1));
    color = cmix(color,ccolor2,conic(pos,M2));
  }

  // The diagonal lines of the quadrangle
  color = cmix(color,lcolor2,line(pos,l01));
  color = cmix(color,lcolor2,line(pos,l02));
  color = cmix(color,lcolor2,line(pos,l03));
  color = cmix(color,lcolor2,line(pos,l12));
  color = cmix(color,lcolor2,line(pos,l13));
  color = cmix(color,lcolor2,line(pos,l23));

  // The moving line
  color = cmix(color,lcolor0,line(pos,l4));
#endif  
  fragColor = vec4(pow(1.0*color,vec3(0.4545)),1);
}

