//---------------------------------------------------------

#define calcRecursion rec3  // use n rays levels (rays1 to rays6): total  RAY_COUNT = 2^n-1

#define DIST_EPSILON 0.005

#define ID_SKY 2.001
#define ID_FLOOR 1.
#define ID_LIGHT 1.001
#define ID_GLASS_WALL 2.000
#define ETA 0.85
#define PI 3.14159265359

#define DENSITY_MIN 0.1
#define DENSITY_MAX 0.1
#define MATERIAL_COLOR vec3(0.05, 0.08, 0.1)
#define AIR_COLOR vec3(0.2, 0.3, 0.5)

//#define GLASS_1_COLOR vec3(0.8,0.2,0.6)
#define GLASS_1_COLOR vec3(1.2, 1., 0.8)*(1.-0.2* mod( floor(5.0*p.z) + floor(5.1*p.x) + floor(5.1*p.y), 2.0))
//#define GLASS_1_COLOR vec3(0.8,1.,0.8)*(0.6+0.4*noise(p.xz*30. + p.yz*23.))

vec3 LIGHT_DIR = normalize(vec3(-0.6,0.7,-0.3));
//-------------------------------------------------------------------------------

float rand(vec2 n) { return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453); }

float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

struct HitData {
    float dist;
    vec3 normal;
    float mat;
    vec3 p;
};

struct Ray {
    vec3 rd;
    HitData hit;
    vec3 col;
    float share;
    float eta;
};

//-------------------------------------------------------------------------------
// primitives:  https://www.shadertoy.com/view/Xds3zN
// info: http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
//-------------------------------------------------------------------------------
float sdPlane( vec3 p ) {return p.y;}

float sdSphere( vec3 p, float radius) {return (length(p)-radius);}

// Rounded Cylinder: ra=radius, rb=roundingRadius, h=height
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

// Box - exact: b=dimension
float sdBox (vec3 p, vec3 b)
{
  vec3 d = abs(p) - b;
  return (min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0)));
}

// Rounded Box: b=dimension, r=roundingRadius
float sdRoundedBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) - r + min(max(d.x,max(d.y,d.z)),0.0);
}

// rounded box: b=size, r=roundingRadius
float roundedBox (vec3 p, vec3 b, float r) { return abs(length(max(abs(p)-b,0.0))-r); }

float sdTorus (vec3 p, vec2 t) {return length(vec2(length(p.xz)-t.x,p.y))-t.y;}

float sdCone (vec3 p, vec2 c) {return dot(c, vec2(length(p.xz),p.y));}   // c must be normalized

//---------
vec3 opU (vec3 d1, vec3 d2) { return (d1.x < d2.x) ? d1 : d2; }  // union

vec3 opS (vec3 d1, vec3 d2) { return -d1.x > d2.x  ? d2 : d1; }  // subtraction

//---------
mat2 rotation(float angle) 
{
    float c = cos(angle), s = sin(angle);
    return mat2(c, s, -s, c);
}

//-------------------------------------------------------------------------------
vec3 map(in vec3 pos)
{
  vec3 res = vec3(sdPlane(pos), ID_FLOOR, -1. );

  res = opU(res, vec3((sdRoundedCylinder (pos-vec3(0.5,0.5,-0.2), 0.15, 0.05, 0.4)), ID_GLASS_WALL, ETA));

  res = opU(res, vec3(sdSphere (pos-vec3(-0.2,0.31,0.4),0.2+0.1*sin(iTime)),ID_GLASS_WALL, ETA));

  res = opU(res, vec3((sdCone  (pos-vec3(-0.7,0.49,-0.2), vec2(0.4,0.3))), ID_GLASS_WALL, ETA));

  mat2 rot1 = rotation(iTime*1.2);  vec3 pr = pos.yxz - vec3(0.4,-0.1,-0.7);
  pr.yz *= rot1;
  pr.xy *= rot1;
  pr.xz *= rot1;
  res = opU(res, vec3(sdTorus      (pr, vec2(0.3,0.1)),ID_GLASS_WALL, ETA));

  res = opU(res, vec3(sdRoundedBox (pos-vec3(0.8,0.4,0.5), vec3(0.14), 0.06+0.02*sin(iTime)),ID_GLASS_WALL, ETA));

  res.x = abs(res.x);
  return res;
}
//-------------------------------------------------------------------------------
vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.0001, 0.0, 0.0 );
    float d = map(pos).x;
    return normalize( vec3(
	    map(pos+eps.xyy).x - d,
	    map(pos+eps.yxy).x - d,
	    map(pos+eps.yyx).x - d));
}

HitData findIntersection(vec3 p, vec3 rd)
{
    float tmin = 0.000;
    float tmax = 50.0;

    float precis = DIST_EPSILON;
    float t = tmin;
    float eta = -1.;
    vec3 res;
    for( int i=0; i<50; i++ )
    {
	  	res = map(p+rd*t);
        eta = res.z;
        if( res.x<precis || t>tmax ) break;
        t += res.x;
    }

    p += rd*t;
    // calculate normal in the father point to avoid artifacts
    vec3 n = calcNormal(p-rd*(precis-res.x));
    return HitData(t, n, res.y, p);
}

//-------------------------------------------------------------------------------
vec3 refractCaustic(vec3 p, vec3 rd, vec3 ld, float eta)
{
    vec3 cl = vec3(1);
    for(int j = 0; j < 2; ++j)
    {
        HitData cp = findIntersection(p, rd);
        if (length(cp.p) > 2.) {
            break;
        }
        cl *= GLASS_1_COLOR;//*(abs(dot(rd, cp.normal)));
        vec3 normal = sign(dot(rd, cp.normal))*cp.normal;
        rd = refract(rd, -normal, eta);

        p = cp.p;
        eta = 1./eta;
        p += normal*DIST_EPSILON*2.;
    }
    float d = clamp( dot( rd, ld ), 0.0, 1.0 );
    return smoothstep(0.99, 1., d)*cl;
}

vec3 caustic(vec3 p,vec3 ld, Ray ray)
{
    vec3 VX = normalize(cross(ld, vec3(0,1,0)));
    vec3 VY = normalize(cross(ld, VX));
    vec3 c = vec3(0);

    const int N = 3;
    p += ray.hit.normal*DIST_EPSILON;

    for(int i = 0; i < N;++i) {

        float n1 = rand(p.xz*10. + vec2(iTime*2. +float(i)*123.));
        float n2 = rand(p.xz*15. +vec2(iTime*3. +float(i)*111.));

        vec3 rd = ld + (VX*(n1-0.5)+VY*(n2-0.5))*0.1;
     // rd = ld;
        rd = normalize(rd);

        vec3 cl = refractCaustic(p, rd, ld, ray.eta);

      	c += cl* dot(rd,ray.hit.normal);
    }
    return c*3./float(N);
}

// lightning is based on https://www.shadertoy.com/view/Xds3zN
vec3 getFloorColor(in Ray ray)
{
    vec3 col = vec3(0);
    vec3 pos = ray.hit.p;
    vec3 ref = reflect( ray.rd, ray.hit.normal );

    float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
    col = 0.4 + 0.1*f*vec3(1.0);

    float dif = clamp( dot( ray.hit.normal, LIGHT_DIR ), 0.0, 1.0 );
    vec3 brdf = vec3(0.0);
    brdf += caustic(pos, LIGHT_DIR, ray);
    brdf += 1.20*dif*vec3(1.00,0.90,0.60);
    col = col*brdf;
    // exclude branching
    col *= (ID_GLASS_WALL-ray.hit.mat);

    return col;
}

vec3 getColor(in Ray ray)
{
    vec3 p = ray.hit.p ;// can be used by SURFACE_COLOR define
    vec3 c1 = ray.col * GLASS_1_COLOR;
    vec3 c2 = getFloorColor(ray);
    // exclude branching
    return mix(c2, c1, ray.hit.mat - ID_FLOOR);
}

//-------------------------------------------------------------------------------
vec3 getRayColor(Ray ray)
{
    float d = mix(DENSITY_MIN, DENSITY_MAX, (ray.eta - ETA)/(1./ETA-ETA));
    vec3 matColor = mix(AIR_COLOR, MATERIAL_COLOR, (ray.eta - ETA)/(1./ETA-ETA));
    vec3 col = getColor(ray);

    float q = exp(-d*ray.hit.dist);
    col = col*q+matColor*(1.-q);
    return col*ray.share;
}

void getRays(inout Ray ray, out Ray r1, out Ray r2) {
     vec3 p = ray.hit.p;
    float cs = dot(ray.hit.normal, ray.rd);
    // simple approximation
    float fresnel = 1.0-abs(cs);
//	fresnel = mix(0.1, 1., 1.0-abs(cs));
    float r = ray.hit.mat - ID_FLOOR;
     vec3 normal = sign(cs)*ray.hit.normal;
    vec3 refr = refract(ray.rd, -normal, ray.eta);
    vec3 refl = reflect(ray.rd, ray.hit.normal);
    vec3 z = normal*DIST_EPSILON*2.;
    p += z;
    r1 = Ray(refr, findIntersection(p, refr),  vec3(0),(1.-fresnel)*r, 1./ray.eta);
    p -= 2.*z;
    r2 = Ray( refl, findIntersection(p, refl), vec3(0),r*fresnel, ray.eta);
}

// set of "recursion" functions

void rec1(inout Ray ray)
{
    ray.col += getRayColor(ray);
}


void rec2(inout Ray ray)
{
    Ray r1,r2;
    getRays(ray, r1, r2);
    ray.col += getRayColor(r1);
    ray.col += getRayColor(r2);
}

void rec3(inout Ray ray)
{
    Ray r1,r2;
    getRays(ray, r1, r2);
    rec2(r1);
    ray.col += getRayColor(r1);
    // use first level of reflection rays only to improve performance
    rec1(r2);
    ray.col += getRayColor(r2);
}

void rec4(inout Ray ray)
{
    Ray r1,r2;
    getRays(ray, r1, r2);
    rec3(r1);
    ray.col += getRayColor(r1);
    // use first level of reflection rays only to improve performance
    rec1(r2);
    ray.col += getRayColor(r2);
}

void rec5(inout Ray ray)
{
    Ray r1,r2;
    getRays(ray, r1, r2);
    rec4(r1);
    ray.col += getRayColor(r1);
    // use first level of reflection rays only to improve performance
    rec1(r2);
    ray.col += getRayColor(r2);
}

void rec6(inout Ray ray)
{
    Ray r1,r2;
    getRays(ray, r1, r2);
    rec5(r1);
    ray.col += getRayColor(r1);
    // use only first level of reflection to improve performance
    rec1(r2);
    ray.col += getRayColor(r2);
}

vec3 render(vec3 p, vec3 rd)
{
    HitData hit = findIntersection(p, rd);
    Ray ray = Ray( rd, hit, vec3(0), 1., ETA);
    calcRecursion(ray);
    ray.col = getRayColor(ray);
    return ray.col;
}

// https://www.shadertoy.com/view/Xds3zN
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
  vec3 cw = normalize(ta-ro);
  vec3 cp = vec3(sin(cr), cos(cr),0.0);
  vec3 cu = normalize( cross(cw,cp) );
  vec3 cv = normalize( cross(cu,cw) );
  return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;
  vec2 mo = iMouse.xy/iResolution.xy;
  // this strange construction is used to define initial view angle
  mo = mix(vec2(0.6,0.3),mo, sign(mo.x+mo.y));
  mo.y += 0.02;
  mo.y *= 1.5;
  float rotTime = sin(iTime*0.04);
  mo.x *= 10.;
  float R = 4.3;   // view distance
  float Y = sin(mo.y);
  float X = cos(mo.y);  // ro = ray origin
  vec3 ro = vec3(cos(rotTime + mo.x)*X, Y, X*sin(rotTime + mo.x) )*R;
  vec3 ta = vec3( 0,0.4,0);

  // camera-to-world transformation
  mat3 ca = setCamera( ro, ta, 0. );
  vec3 rd = ca * normalize( vec3(uv.xy,2.5) ); // ray direction

  // get raymarching color
  vec3 c = render(ro, rd);

  fragColor = vec4(c, 1);
}

vec2 rotate(vec2 a, float b)
{
    float c = cos(b);
    float s = sin(b);
    return vec2(
        a.x * c - a.y * s,
        a.x * s + a.y * c
    );
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdBeveledBox(vec3 p, vec3 r, float R)
{
    float a = sdBox(p,r);
    p=abs(p);
    float b = (p.x + p.y - r.x - r.y + R) / sqrt(2.);
    float c = (p.y + p.z - r.y - r.z + R) / sqrt(2.);
    float d = (p.z + p.x - r.z - r.x + R) / sqrt(2.);
    float e = (p.x + p.y + p.z - r.x - r.y - r.z + R + R) / sqrt(3.);
    return max(max(max(a,b),max(c,d)),e);
}

float sdCylinder(vec3 p, float r)
{
    return length(p.zy)-r;
}

float sdCappedCylinder(vec3 p, float r, float h)
{
    return max(sdCylinder(p,r),abs(p.x)-h);
}

float scene(vec3 p)
{
    float box = sdBeveledBox(p, vec3(1.), .1);
    p.xz=abs(p.xz);
    p.xz=vec2(max(p.x,p.z),min(p.x,p.z));
    float a = sdBeveledBox(p-vec3(0,.25,0), vec3(1.1,.5,.2), .1);
    float b = sdBeveledBox(p+vec3(0,.6,0), vec3(1.1,.15,.2), .1);
    p.y=abs(p.y);
    float indent = sdBeveledBox(p-vec3(1.06,.7,.7),vec3(.1),.05);
    indent = sdCappedCylinder(p-vec3(1.06,.7,.7),.07,.1);
    return max(min(box,max(min(a,b),p.x-1.05)),-indent);
}

float scene2(vec3 p)
{
    return -(length(p)-7.);
    return -sdBox(p, vec3(7.));
}

float CAMERA_ELEVATION = 0.4;
float SPIN_RATE = .3;
#define REFRACTIVITY (sin(iTime*.5)*.5+.5)

void cameraSpin(inout vec3 p)
{
    p.yz = rotate(p.yz, CAMERA_ELEVATION);
    p.xz = rotate(p.xz, iTime*SPIN_RATE);
}

void cameraSpinBackwards(inout vec3 p)
{
    p.xz = rotate(p.xz, -iTime*SPIN_RATE);
    p.yz = rotate(p.yz, -CAMERA_ELEVATION);
}

vec3 background(vec2 uv)
{
    vec3 cam = vec3(0,0,-5);
    vec3 dir = normalize(vec3(uv,1));

    float t=0.;
    float k=0.;
    for(int i=0;i<10;++i)
    {
        k = scene2(cam+dir*t);
        t+=k;
        if (k<.001)
            break;
    }
    vec3 h = cam+dir*t;

    float T = length(sin(h*2.))/sqrt(3.);
    T=fract(T+iTime*.05);
    T=step(.5,T);
    vec3 color = vec3(.9)+T*.1-.2;
    color -= smoothstep(0.,1.,length(uv))*.5;
    return color;
}

void mainImage(out vec4 out_color, vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy - .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 cam = vec3(0,0,-10);
    vec3 dir = normalize(vec3(uv,2.5));
    vec3 fwd = vec3(0,0,1);

    cameraSpin(cam);
    cameraSpin(dir);
    cameraSpin(fwd);

    float t=0.;
    float k=0.;
    for(int i=0;i<100;++i)
    {
        k = scene(cam+dir*t);
        t+=k;
        if (k<.001)
            break;
    }
    vec3 h = cam+dir*t;
    vec2 o = vec2(.001, 0);
    vec3 worldNormal = normalize(vec3(
        scene(h+o.xyy)-scene(h-o.xyy),
        scene(h+o.yxy)-scene(h-o.yxy),
        scene(h+o.yyx)-scene(h-o.yyx)
    ));
    vec3 eyeNormal = worldNormal;
    cameraSpinBackwards(eyeNormal);

    vec3 specular = vec3(0);

    if (k < .001)
    {
        uv += reflect(vec3(0,0,1),eyeNormal).xy * REFRACTIVITY * .3;
        specular += pow(1.-max(0.,dot(-dir,worldNormal)),5.) * REFRACTIVITY * .5;
    }

    out_color.rgb = background(uv) + specular;
    out_color.rgb = pow(out_color.rgb, vec3(1.2,1.1,1.0));
}

// #define-less version of David Hoskins' two-tweet caustics effect,
// suitable for use as a custom HLSL expression within Unreal Engine's
// material editor.
//
// cleaner golf'd version by FabriceNeyret2
//
// ORIGINAL: https://www.shadertoy.com/view/MdKXDm
//
// A simple, if a little square, water caustic effect.
// David Hoskins.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// Inspired by akohdr's "Fluid Fields"
// https://www.shadertoy.com/view/XsVSDm

void mainImage(out vec4 k, vec2 p)
{
    mat3 m = mat3(-2,-1,2, 3,-2,1, 1,2,2);
    vec3 a = vec3( p / 4e2, iTime / 4. ) * m,
         b = a * m * .4,
         c = b * m * .3;
    k = vec4(pow(
          min(min(   length(.5 - fract(a)), 
                     length(.5 - fract(b))
                  ), length(.5 - fract(c)
             )), 7.) * 25.);
}


/* UE4 Custom HLSL Material Expression: 

Input: k, where k.xy is texcoord, k.z is time

float3x3 m = float3x3(-2,-1,2, 3,-2,1, 1,2,2);
float3 a = mul(k, m) * 0.5;
float3 b = mul(a, m) * 0.4;
float3 c = mul(b, m) * 0.3;
return pow(min(min(length(0.5 - frac(a)), length(0.5 - frac(b))), length(0.5 - frac(c))), 7) * 25.;

*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xx;
    uv *= 18.;

    uv.y += (1.-step(1.,mod(iTime,2.)))*iTime*step(1.,mod(uv.x,2.));
    uv.y -= (1.-step(1.,mod(iTime,2.)))*iTime*(1.-step(1.,mod(uv.x,2.)));

    uv.x += (step(1.,mod(iTime,2.)))*iTime*step(1.,mod(uv.y,2.));
    uv.x -= (step(1.,mod(iTime,2.)))*iTime*(1.-step(1.,mod(uv.y,2.)));

    vec2 f_uv = fract(uv);

    float c = smoothstep(.24,.25,length(vec2(.5)-f_uv));

    vec3 col = vec3(c);

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float pi = acos(-1.);
const float phi = (1.+sqrt(5.))*.5;

vec2 rotate(vec2 a, float b)
{
    float c = cos(b);
    float s = sin(b);
    return vec2(
        a.x * c - a.y * s,
        a.x * s + a.y * c
    );
}

float scene(vec3 p)
{
    const vec3 n = normalize(vec3(phi,1,0));

    p = abs(p);
	float a = dot(p,n.xyz);
    float b = dot(p,n.zxy);
    float c = dot(p,n.yzx);
    return max(max(a,b),c)-phi*n.y;
}

vec3 trace(vec3 cam, vec3 dir)
{
    vec3 accum = vec3(1);
    for(int bounce=0;bounce<3;++bounce)
    {
        float t;
        float k;
        for(int i=0;i<100;++i)
        {
            k = scene(cam+dir*t);
            t += k;
            if (k < .001 || k > 10.)
                break;
        }

        vec3 h = cam+dir*t;
        vec2 o = vec2(.001, 0);
        vec3 n = normalize(vec3(
            scene(h+o.xyy)-scene(h-o.xyy),
            scene(h+o.yxy)-scene(h-o.yxy),
            scene(h+o.yyx)-scene(h-o.yyx)
        ));

        if (k > 10.)
        {
            return vec3(1);
        }
        else if (iMouse.z > 0.)
        {
            return n*.5+.5;
        }
        else
        {
            float A = .1;
            float B = scene(h+n*A);
            float fakeAO = clamp(B/A,0.,1.);
            fakeAO = pow(fakeAO,.6)*.2+.8;

            float light = dot(n,normalize(vec3(1,4,2)))*.5+.5;

            vec3 color = vec3(.9);

            return pow(light * fakeAO * accum * color, vec3(.4545));
        }
    }
    return vec3(0);
}

void mainImage(out vec4 out_color, vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy-.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 cam = vec3(uv*3.,-5.);
    vec3 dir = vec3(0,0,1);

    cam.yz = rotate(cam.yz, .5);
    dir.yz = rotate(dir.yz, .5);

    cam.xz = rotate(cam.xz, iTime*.2);
    dir.xz = rotate(dir.xz, iTime*.2);

    out_color.rgb = trace(cam,dir);
}

#define PI 3.1415926
#define MAXIT  256.

struct Ray {
   vec3  o;
   vec3  d;
};

mat2 rot2D(float a){
    float c = cos(a);
    float s = sin(a);

    return mat2(c,-s,s,c);
}


vec3 getCol(float v){
    float r = cos((v-0.75)*PI*1.5);
    float g = cos((v-0.55)*PI*1.5);
    float b = cos((v-0.25)*PI*1.5);

    return vec3(r,g,b);
}    


void drawStars(inout vec3 col,vec2 uv,vec2 c){
   uv-=c;

   uv*=2.8;

   float l =length(uv);
   float arc = atan(uv.y,uv.x); 

   float d = abs(dot(vec2(0,1),uv));
   float d1 = abs(dot(vec2(1,0),uv));
   float ctv = c.x*10.0;
   // clip the star
   d+=pow(l,1.1+0.6*cos(iTime*2.0+ctv));
   d1+=pow(l,1.1+0.6*cos(iTime*2.0+ctv));

   float v =0.0005/l;  
   // blend the star
   v+=0.01/(d);
   v+=0.01/(d1);
   v=pow(v,1.6);

   col+=vec3(cos(iTime+ctv)*0.1+0.5,sin(iTime+ctv)*0.4+0.5,sin(iTime)*0.3+0.7)*v;
}

void drawStarGroup(inout vec3 col,vec2 uv,float starNum){
    for (float i=0.0;i<starNum;i++){
        drawStars(col,uv,vec2(i*0.1*sin(i),cos(i)*0.3));
    }
}

void drawMeteor(inout vec3 col, in vec2 uv,vec2 startP,vec2 endP,float linWidth){

   uv*=3.0;
   vec2 lineDir=endP-startP;
   vec2 fragDir=uv-startP;

   // keep the line coefficient bewteen [0,1] so that the projective dir on the 
   // lineDir will not exceed or we couldn't get a line segment but a line.
   float lineCoe=clamp(dot(lineDir,fragDir)/dot(lineDir,lineDir),0.,1.0);

   vec2 projDir=lineCoe*lineDir;

   vec2 fragToLineDir= fragDir- projDir;

   float dis=length(fragToLineDir);
   float disToTail = length(projDir);
   dis=linWidth/dis;

   col += vec3(dis*0.2,dis*0.6,0.8*dis)*pow(disToTail,3.0);

}

void drawMeteors(inout vec3 col,vec2 uv){
    vec2 starPosBegin = vec2(0.5,0.4);
    vec2 starPosEnd = vec2(-0.5,-0.1);
    vec2 starDir = normalize(starPosEnd-starPosBegin);
    float trackC = 16.*cos(mod(-iTime*0.5,PI));
    vec2 starCoe = starDir*trackC*2.0;

    drawMeteor(col,uv,starPosBegin+starCoe,starPosEnd+starCoe,0.03);

}

vec3 grad2D(vec3 p){
   float arch = atan(p.z,p.x); 
   float lnc = length(texture(iChannel0,vec2(arch,p.y)));
   float offx = length(texture(iChannel0,vec2(atan(p.z,p.x+0.0001),p.y)));
   float offy = length(texture(iChannel0,vec2(arch,p.y+0.0001)));

   return normalize(vec3(offx-lnc,offy-lnc,0.1)); 
}
void setCamera(inout Ray r,vec3 eye,vec3 tar,vec2 uv){
   vec3 look = normalize(tar-eye);
   vec3 right = normalize(cross(vec3(0,1,0),look));
   vec3 up = normalize(cross(look,right)); 

   r.o = eye;
   r.d = normalize(uv.x*right+uv.y*up+look);
}

void sphereVolume(inout vec3 col,inout Ray r,vec3 lp,vec3 lp1,vec2 uv){


   vec3 p   = r.o+r.d;
   vec3 p1  = p;

   vec3 c   = vec3(0);
   vec3 n   = vec3(0);
   vec3 ld  = vec3(0);
   vec3 ld1  = vec3(0);

   float ra   = 2.0; 

   float t =(length(p-c)-ra);
   float plen = 0.;

   float atmosv  = 0.;
   float planetv = 0.;

   float steps = .01;
   float nl = 0.;
   float nl1 = 0.;
   float li = 0.; 
   vec3 start = r.o+r.d*t;

   for(float i=0.;i<MAXIT;i++){
       p= start+r.d*i*0.01;
       p1=start+r.d*i*0.01;
       plen = length(p);
       // draw the planet
       if(plen<=ra){

           if(abs(n.x)<=0.){
              ld  =  normalize(lp-p);
              ld1  =  normalize(lp1-p);

              n = normalize(p); 
              nl = smoothstep(0.,1.,dot(ld,n));
              nl1 =  smoothstep(0.,1.,dot(ld1,n))/(length(lp1)*0.2);
           }
           p.xz*=rot2D(iTime*0.2);  

           if(plen>ra-0.35){
              atmosv+=steps*(nl+nl1)*0.6;
              li += smoothstep(-1.,1.,pow(dot(ld,r.d),2.)+1.)*steps;   
           }else{ 
              planetv=1.;
              break; 
           }

       }else if(dot(p,r.o)<0.){
           break;
       }  

   }

   if(planetv<=0.){
        drawMeteors(col,uv);
        drawStarGroup(col,uv,16.); 
   }

   vec3 ocolor = getCol(1.1-(atmosv+li)*0.3)*atmosv;
   vec3 pcolor = planetv*texture(iChannel0,vec2(atan(p.z,p.x),p.y)*0.8).xyz;
   nl+=nl1; 
   col+=smoothstep(0.,1.,(ocolor*(li+nl)+pcolor*nl));

}

