float dist2(vec3 p) {
    float single = length(p) - sin(iTime)*sin(iTime);

    //p = mod(p,1.0)-vec3(0.5); // instance on xy-plane
    vec3 folded = fract(p+0.5)-vec3(0.5); // instance on xy-plane
    float infinite = length(folded)-abs(sin(length(p))/4.0);

    return min(infinite, single);
}


int iterations = 15;
float scale = 2.0;
float dist3(vec3 z) {
	vec3 a1 = vec3(1,1,1);
	vec3 a2 = vec3(-1,-1,1);
	vec3 a3 = vec3(1,-1,-1);
	vec3 a4 = vec3(-1,1,-1);
	vec3 c;
	int n = 0;
	float dist, d;
	while (n < iterations) {
		 c = a1; dist = length(z-a1);
	        d = length(z-a2); if (d < dist) { c = a2; dist=d; }
		 d = length(z-a3); if (d < dist) { c = a3; dist=d; }
		 d = length(z-a4); if (d < dist) { c = a4; dist=d; }
		z = scale*z-c*(scale-1.0);
		n++;
	}

	return length(z) * pow(scale, float(-n))-0.001;
}

float dist4(vec3 z) {
    float r;
    int n = 0;
    while (n < iterations) {
       if(z.x+z.y<0.) z.xy = -z.yx; // fold 1
       if(z.x+z.z<0.) z.xz = -z.zx; // fold 2
       if(z.y+z.z<0.) z.zy = -z.yz; // fold 3	
       z = z*scale - vec3(1.,1.,1.)*(scale-1.0);
       n++;
    }
    return (length(z) ) * pow(scale, -float(n));
}

float dist(vec3 p) {
    return dist4(p);
}

vec3 dx = vec3(0.0001, 0.0, 0.0);
vec3 dy = vec3(0.0, 0.0001, 0.0);
vec3 dz = vec3(0.0, 0.0, 0.0001);

vec3 norm(vec3 p) {
    return normalize(
        vec3(dist(p+dx)-dist(p-dx),
		     dist(p+dy)-dist(p-dy),
		     dist(p+dz)-dist(p-dz))
    );   
}

