//rd MUST BE NORMALIZED, or distance estimation will march the ray in incorrect step sizes.
bool march(out int iterations, out vec3 point, out float smallest_dist, in vec3 r0, in vec3 rd) {
    float total_dist = 0.0;
    float cur_dist;
    smallest_dist = 10000000.0;

    point = r0 + rd * total_dist;
    for (iterations=0; iterations<120; iterations++) {
        cur_dist = dist(point);
        if(cur_dist < smallest_dist) {
            smallest_dist = cur_dist;
        }
        if(cur_dist < 0.001) {
            return true;
        }
        total_dist += cur_dist;
    	point = r0 + rd * total_dist;
    }
    return false;
}

vec3 ortho_light_dir = vec3(0.57735, -0.57735, 0.57735);
vec3 ortho_light_color = vec3(0.8, 0.5, 1.0);

vec4 lighting(vec3 p, vec3 n) {
    //AMBIENT (currently 0) Should be multiplied by diffuse color.
    vec3 retval = vec3(0., 0., 0.); 
    float dprod = dot(n, ortho_light_dir);
    if(dprod >= 0.0) {
        retval += ortho_light_color * (
            //DIFFUSE
            vec3(0.7, 0.8, 1.0) * dprod +
            //SPECULAR
            //pow(dprod, 40.0)
            0.0
        );
    }
    return vec4(retval, 1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    //Screen x goes from -1.0 to 1.0
	vec2 screen_coord = -1.0 + 2.0 * (fragCoord.xy / iResolution.xy);
    float aspect_ratio = iResolution.x/iResolution.y;
    //Screen y goes from (-1.0/aspect_ratio to 1.0/aspect_ratio)
    screen_coord.y /= aspect_ratio;

    //Given a viewpoit (location of the eye) and a point to look at,
    //compute the vector out the top of the viewer's head (up) and out
    //his right ear (right);
	vec3 viewpoint = vec3(2.5*sin(iTime/8.), 2.5*cos(iTime/8.), sin(iTime/16.));
    vec3 lookat = vec3(0., 0.0, 0.0);
    vec3 lookdir = lookat - viewpoint;
    vec3 right = normalize(cross(lookdir, vec3(0., 0., 1.)));
    vec3 up = normalize(cross(right, lookdir));

    //Scale right and up to fix the 'focal length' of the camera. By
    //doing this, I can make the distance between viewpoint and lookat
    //arbitrarily large or small and the camera doesn't jump from zoom
    //to fisheye. Change the 1.0s to something larger for fisheye, smaller
    //for zoom.
    float looklen = length(lookdir);
    right *= looklen * 0.5;
    up *= looklen * 0.5;

    //From the viewpoint, the ray we'll travel along to pass through the
    //appropriate pixel:
    vec3 ray_d = 
        normalize(
        	lookat
            + screen_coord.x * right
            + screen_coord.y * up
            - viewpoint
        );

    int iterations;
    vec3 intersection;
    float smallest_dist;
    bool hit = march(iterations, intersection, smallest_dist, viewpoint, ray_d);
    if(hit) {
        //
        fragColor = lighting(intersection, norm(intersection));
    } else {
        float sky_brightness = max(dot(ray_d, ortho_light_dir), 0.0);
        vec3 sky_color = sky_brightness * ortho_light_color;
    	fragColor = vec4(sky_color, 1.0);
    }
}

vec2 rotate(vec2 p, float theta) {
	float s = sin(theta), c = cos(theta);
    return vec2(
    	p.x * c + p.y * s,
    	p.y * c - p.x * s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float bigTime = mod(iTime / 10., 1.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy - .5) * pow(2., bigTime);
    uv = rotate(uv, 3.1415927 / 4.) * 5.;
    vec2 bigUv = fract(uv * .5) * 2.;
	uv = fract(uv);

    vec4 back = vec4(0., 0., .4, 1.);
    vec4 fore = vec4(0., 0., .8, 1.);

    if (bigTime > 0.5) {
        if (bigUv.x > 1. != bigUv.y > 1.) {
        	// the top square that slides right
            if (uv.y < 0.5)
    	    	fragColor = (1. - uv.x > bigTime) ? fore : back;
            else
            // the bottom square that slides left
    	    	fragColor = (uv.x > bigTime) ? fore : back;
        } else {
            // the constant squares
            if (uv.x > 0.5 == uv.y > 0.5)
                fragColor = fore;
            // the square sliding in from the left
            else if (uv.y < 0.5)
                fragColor = (1.5 - uv.x < bigTime) ? fore : back;
            // the square sliding in from the right
            else
                fragColor = (0.5 + uv.x < bigTime) ? fore : back;
        }    
    }
    else {
    	if (uv.x > 0.5 != uv.y > 0.5)
    	    fragColor = back;
	    else
    	    fragColor = fore;
    }
}

