void animateScene(float time)
{
    const float pi = 3.1415926535;
    const float rs = 2.0;
    const float spr = 5.0;
    float as = 2.0 * pi * time / spr;

    spherePos = cameraTarget + rs * vec3(-sin(as),0.0,cos(as)) + vec3(0,1,0);

    lightPos += vec3(0,10.5 + 9.5 * cos(time) - 10.,0);

    float targetDist = length(cameraTarget - cameraPos);
    cameraPos -= vec3(0,0,targetDist);
    cameraPos += targetDist * vec3(sin(time),max(sin(time*0.5),0.),cos(time));
}

vec3 raycastAtPixelCoord(vec2 pixCoord)
{
    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(pixCoord, rayPos, rayDir);

    int objectId;
    vec3 intersecI, normalI;
    float distI = computeNearestIntersection(rayPos, rayDir, objectId, intersecI, normalI);

    if(distI <= 0.0)
        return skyCol;

    vec3 L = lightPos - intersecI;
    float Ldist = length(L);
    L = L / Ldist;

    vec3 R = 2.0 * dot(normalI,L) * normalI - L;

    vec3 V = -rayDir;

    Material objectMat;
    vec3 objectCol = getObjectColorAtPoint(objectId, intersecI, objectMat);

    float shadowFactor = getShadowFactorAtPoint(intersecI, normalI, objectMat, L, Ldist);

    vec3 resCol = computePhongShading(objectCol, objectMat, shadowFactor, normalI, L, R, V);

    return resCol;
}

