    }

    resCol /= float(PIXEL_SAMPLING_GRID_SIZE * PIXEL_SAMPLING_GRID_SIZE);*/

    //Stochastic antialiasing

    for (int i=0; i<PIXEL_SAMPLING_SIZE; i++) 
    {
        vec2 q =  vec2(float(i), float(i));
        vec2 n = noise2( fragCoord , q );
        vec2 offset = vec2(n.x/800., n.y/600.);
		resCol += raycastAtPixelCoord(fragCoord + offset);
    }
    resCol /= float(PIXEL_SAMPLING_SIZE);

    fragColor = vec4(resCol,1);
}

vec3 white = vec3(1,1,1);
vec3 black = vec3(0,0,0);
vec3 red = vec3(1, 0, 0);

float sinp(float x) {
    return abs(sin(x));
}

vec3 sp(vec2 c, float r, vec2 p) {
    float d = length(p - c);
    if (d < r)
    {
        if (sin(d) > 0.0) {
            return sin((iTime*vec3(0, 0.5, 1)));    
        }
        else {
      float d_norm = r / d;
            return vec3(sinp(iTime)*d_norm);
        }
    }
    else
        return white;
}

vec3 sq(vec2 c, float bedro, vec2 p, float a) {
  float r = bedro / 2.0;
    mat2 rot_mat =mat2(
        sin(a), cos(a),
        sin(a), -sin(a)
        );
    vec2 p2 = p * rot_mat;
    if (abs(p2.x - c.x) < r && abs(p2.y - c.y) < r)
        return black;
    else
        return vec3(-1);

}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

