#define FAR 40.0
#define EPS 0.001
#define STEPS 1000

#define voronoiscale 0.4
#define voronoiscalefloor 1.0
#define voronoiborder 0.1
#define gyroidborder 0.03
#define roundness 0.05
#define edge 0.3

#define ambient 0.3

#define dot2(a) dot(a,a)
#define rot(A) mat2(cos(A),-sin(A),sin(A),cos(A))

#define PI acos(-1.0)

#define HASHSCALE4 vec4(.1031, .1030, .0973, .01099)

float torus(vec3 p, float r) {
    vec2 q = vec2(length(p.xz)-r,p.y);

    return length(q);
}

float map(vec3 p) {

    vec3 s = sin(p);
    vec3 c = cos(p.yzx);
    float len = abs(dot(s,c)*0.7)-gyroidborder;

    //float t1 = torus(p+vec3(1,0,0),2.0);
    //float t2 = torus(p.yzx-vec3(1,0,0).yzx,2.0);
    //float len = abs(t1-t2)*max(t1,t2)*0.3-0.1;

    vec4 q = vec4(abs(p)-2.0*PI+roundness,len);
    return length(max(q,0.0))*0.8+min(0.0,max(max(max(q.x,q.y),q.z),q.w))-roundness;
}


//if this is under 0, ignore voronoi
float map2(vec3 p) {

    vec3 q = abs(p);

    float a = max(max(q.x,q.y),q.z);


    return 2.0*PI-a-edge;
}

float planetrace(vec3 p, vec3 d,vec3 plane) {
    float len = -min(dot(p,plane)+voronoiborder,0.0)/max(dot(d,plane),EPS);
    return len;
}

vec3 hash34(vec4 p4)
{
	p4 = fract(p4  * HASHSCALE4);
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.zzyw)*p4.zywx).xyz;
}

vec3 noise(vec3 p3)
{
    //animated whitenoise, moving linearly to different random points
    //float floortime = floor(iTime);
    //return mix(hash34(vec4(p3,floortime)),hash34(vec4(p3,floortime+1.0)),iTime-floortime);

    //moving around in circles
    return sin(vec3(iTime+p3.xzz*p3.yxy+p3))*0.5+0.5;

    //static procedual whitenoise
    //return hash34(vec4(p3,0.75));

    //volume texture noise (fastest, for me at least)
    //return (texelFetch(iChannel0,ivec3(mod(p3,32.0)),0).rgb-0.5)*0.5;
}

vec4 holes(vec3 ro, vec3 rd, float scale) {
    /*
    vec3 p = abs(mod(ro,0.2)-0.1);

    return -p.z+0.05;
    */


    ro /= scale;

    float len = 10000.0;
    vec3 cell;
    vec3 center = floor(ro);
    vec3 center2;

    //finding closest voronoi cell
    for(float x = -1.0; x <= 1.0; x++) {
    	for(float y = -1.0; y <= 1.0; y++) {
    		for(float z = -1.0; z <= 1.0; z++) {
                vec3 orig = vec3(x,y,z)+center;
                orig += noise(orig);
                if (len > dot2(ro-orig)) {
                    cell = orig;
                    center2 = vec3(x,y,z)+center;
                    len = dot2(ro-orig);
                }
            }
    	}
    }


    //raytracing
    vec3 normal;

    len = 10000.0;
    center = center2;

    for(float x = -1.0; x <= 1.0; x++) {
        for(float y = -1.0; y <= 1.0; y++) {
            for(float z = -1.0; z <= 1.0; z++) {
                if (!(x==0.0&&y==0.0&&z==0.0)) {
                    vec3 orig = vec3(x,y,z)+center;
                    orig += noise(orig);
                    vec3 mid = mix(orig,cell,0.5);
                    vec3 plane = vec3((orig-cell));
                    float num = planetrace(ro-vec3(mid),rd,plane);

                    if (num < len) {
                        len = num;
                        normal = -plane;
                    }
                }
            }
        }
    }

    return vec4(len*scale,normal);
}

vec3 normal(vec3 p) {
    vec2 e = vec2(EPS,0);

    return normalize(vec3(
        map(p+e.xyy),
        map(p+e.yxy),
        map(p+e.yyx))
       -map(p));
}

vec3 normal2(vec3 p) {
    vec2 e = vec2(EPS,0);

    return normalize(vec3(
        map2(p+e.xyy),
        map2(p+e.yxy),
        map2(p+e.yyx))
       -map2(p));
}

vec4 ray(vec3 ro, vec3 rd, float maxdist) {
    float d = 0.0;

    vec3 n = vec3(-1);
    for(int i = 0; i < STEPS; i++) {
        vec3 p = ro+rd*d;
        float l = map(p);
        float l2 = map2(p);

        if (l < EPS && l2 > EPS) {
            vec4 h = holes(p, rd, voronoiscale);
            if (h.x > l) {
                if (l2 < h.x) {
                    n = vec3(-2);
                    l = l2;
                } else {
                    n = h.yzw;
                    l = h.x;
                }
            } else {
                n = vec3(-1);
            }
        }

        d += l;
        if (d > maxdist||l < EPS) {
            break;
        }
    }
    if (n == vec3(-1)) n = normal(ro+rd*d);
    if (n == vec3(-2)) n = normal2(ro+rd*d);
    return vec4(d,n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0,0,-12.0);
    vec3 rd = normalize(vec3(uv,1));

    if (length(iMouse.xy/iResolution.y) > 0.1) {
        rd.yz *= rot(-iMouse.y/iResolution.y*3.14+3.14*0.5);
        ro.yz *= rot(-iMouse.y/iResolution.y*3.14+3.14*0.5);
        rd.xz *= rot(-iMouse.x/iResolution.x*6.28);
        ro.xz *= rot(-iMouse.x/iResolution.x*6.28);
    } else {
        rd.xz *= rot(iTime*0.2);
        ro.xz *= rot(iTime*0.2);
    }
    ro.y = max(ro.y-1.5,-2.0*PI+0.1);

    float plane;
    if (rd.y >= 0.0) {
        plane = FAR;
    } else {
        plane = min(-(ro.y+6.28)/rd.y,FAR);
    }


    vec4 r = ray(ro,rd, plane);

    vec3 background = vec3(0.2,0.4,0.3)*(rd.y*0.5+0.5)
                     +vec3(0.4,0.2,0.5)*(dot(rd,vec3(-1.0/sqrt(3.0)))*0.5+0.5);

    background *= 0.2;

    if (r.x < plane) {
        vec3 p = ro+rd*r.x;
        vec3 n = r.yzw;

        fragColor = vec4(1);

        vec3 lightn = normalize(vec3(1));
        float light = max(ambient, dot(n,lightn));

        fragColor *= light;

        fragColor = mix(fragColor,vec4(background,1), r.x/FAR);

    } else if (plane < FAR) {
        r.x = plane;
        vec3 p = ro+rd*plane;
        vec3 n = vec3(0,1,0);

        //rd = refract(rd,n,0.9);

        vec4 vor = holes(p, rd, voronoiscalefloor);

        vec3 col;

        if (vor.x > 0.0) {
            col = vec3(0.2,0.1,0.8);
        } else {
            col = vec3(0.5,0.5,0.0);
        }

        vec3 lightn = normalize(vec3(1));
        float light = max(ambient, dot(n,lightn));

        col *= light;

        vec3 rd2 = reflect(rd,n);

        if (vor.x > 0.0) {
            p += rd*vor.x;
            r.x += vor.x;
            n = vor.yzw;
            fragColor = vec4(0.6,0.2,0.3,1);

            float light = max(ambient, dot(n,lightn));

            fragColor *= light;

            fragColor.xyz = mix(fragColor.xyz,col,0.3);

        } else {
            fragColor.xyz = col;
        }

        fragColor += clamp(dot(rd2,lightn)*200.0-199.0,0.0,1.0);
    }
    fragColor = mix(fragColor,vec4(background,1), min(r.x/FAR,1.0));

    fragColor = sqrt(fragColor);
}

