// tweakable values ///////////////////////////////////////////////////////////

float water_level = 2.0;
float snow_amount = 4.0;
float grass_amount = 5.0;
float sand_amount = 1.0;
float rock_amount = 1.0;

float sun_brightness = 12.0;
float sky_brightness = 1.0;
vec3 sun_direction = normalize(vec3(0.9,0.5,.2));

vec3 color_sun = vec3(0.9,0.8,0.6);
vec3 color_sky = vec3(0.2,0.4,0.9);
vec3 color_snow = vec3(0.96, 0.98, 0.99);
vec3 color_grass = vec3(0.3,0.5,0.2);
vec3 color_dirt = vec3(0.24,0.16,0.12);
vec3 color_rock = vec3(0.52,0.54,0.5);
vec3 color_sand = vec3(0.9,0.8,0.4);

//#define SHOW_HEIGHT_LEVELS
//#define SHOW_NORMALS        
//#define SHOW_SHADOWS


// implementation begins //////////////////////////////////////////////////////

// procedural 2d noise texture, for given (x,y) return value between 0 and 1
float noise_2d(vec2 p);

float distance_from_water(vec3 pos){
    return pos.y+14.0-water_level;
}

// returns albedo for given (x,y,z) position and (nx, ny, nz) surface normal
vec3 terrain_material(vec3 pos, vec3 normal){

    // altitude taken from y component
    float altitude = pos.y+5.0;

    vec4 map1 = texture(iChannel0, pos.xz*0.1);
    vec4 map2 = texture(iChannel1, pos.xz*0.05);
    vec4 map3 = texture(iChannel2, pos.xz*0.025);

    vec3 color = vec3(0); // black

    // add dirt layer
    color=mix(color_dirt,
              color_dirt*0.125, // darker shade of dirt
              map1.x); // use first texture to add variation

    // add rock layer
    color=mix(color, 
              color_rock*map3.xyz*1.5-.1, // use third texture to add variation to rock
              clamp( // clamp makes sure layer mask is valid (values between 0 and 1)
                  8.0*(length(normal.xz)*rock_amount // slopes should be rocky
                       + altitude*0.5-3.0+map1.z*0.5)*0.1, // add more rock at higher altitudes
                    0.0, 1.0));

    float sand = clamp(normal.y*4.0*sand_amount // don't put sand on slope
                       - distance_from_water(pos)*16.0 // the further we're from water, the less sand
                       , 0.0, 1.0);

    // add sand layer
    color=mix(color, color_sand, sand);

    // add grass layer
    color=mix(color, 
              color_grass*map2.xyz*1.4, 
              clamp(map2.y*8.0 // add variation based on texture
                    + normal.y*2.5 // add more grass on flat areas (not slope)
                    + normal.x // more grass on east side of mountains
                    - altitude*0.1 // less grass on higher altitudes
                    - 10.0 + grass_amount // constant value to adjust grass amount
                    - color.y*2.0 // less grass where previous layers have bright color
                    - sand, // less grass where we have sand
                    0.0,1.0));

    // add snow layer
    color=mix(color, 
              color_snow, 
              clamp(map1.y*2.0 // add variation using texture 1
                    + normal.y*4.0 // more snow on flat (not slope) terrain
                    + altitude // more slow on higher altitude
                    - 6.5 + snow_amount // manual adjustment using constant value
                    , 0.0, 1.0));

    // make sure components of final color is not above 1.0f for correct shading
    return clamp(color, 0.0, 1.0);
}

float heightmap(vec2 coord2d)
{
    float altitude =.0;

    // create a mask for mountain biome
    float mountains = noise_2d(coord2d*0.05); 

    altitude += noise_2d(coord2d*0.06125)*16.0*mountains; // rough shape of mountain 
    altitude += noise_2d(coord2d*0.25)*4.0*mountains;
    altitude += noise_2d(coord2d*0.5+altitude*0.5)*2.0*mountains;
    altitude += noise_2d(coord2d*2.0)*0.5*mountains;
    altitude += noise_2d(coord2d*8.0)*0.125*mountains; // smaller details of mountain

    return altitude;
}

vec3 background_fn(vec3 dir){

    vec3 background = sky_brightness*color_sky*pow(2.0-abs(dir.y), 4.0);
    return background + pow(max(.0,dot(dir, sun_direction)), 200.0)*sun_brightness;
}

// rendering and shading ////////////////////////////////////////////////

float df(vec3 pos){ // distance from geometry
    float terrain = pos.y+4.0;
    terrain += 10.0-heightmap(pos.xz);
    return min(terrain, distance_from_water(pos));
}

vec2 rot(vec2 v, float a){ // rotate
    float c = cos(a), s = sin(a);
    return vec2(
        v.x*c - v.y*s,
        v.x*s + v.y*c);
}

vec3 nf(vec3 pos, float smoothing){ // normal function
    vec2 e = vec2(0, smoothing);
    float c = df(pos+smoothing);
    return normalize(vec3(
        c-df(pos-e.yxx),
        c-df(pos-e.xyx),
        c-df(pos-e.xxy)
    ));
}

float hash(float x); vec3 hash(vec3 v); float hash(vec2 v);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = (fragCoord.xy-iResolution.xy*.5)/iResolution.yy;
    vec2 mouse = (iMouse.xy-iResolution.xy*.5)/iResolution.yy;

    vec3 opos = vec3(0,0,-4. +iTime*2.0);
    vec3 pos = opos;
    vec3 dir = normalize(vec3(cuv,1));

    //pos.xz = rot(pos.xz, mouse.x);
    dir.xz = rot(dir.xz, mouse.x);
    dir.yz = rot(dir.yz, mouse.y);


    pos += hash(cuv)*dir;

    vec3 ldir = sun_direction;

    int i, mi=200;
    float dist, spd=1.0;
    for(i=0; i<mi; i=i+1){
        dist = df(pos);
        pos += dist*dir*spd;
        if (dist<-0.0){
            spd*=.5;
        }
        else if ((dist>0.005&&dist<0.005)||dist>100.0) { break; }
    }
    float totald = length(opos-pos);

    vec3 background = background_fn(dir);
    vec3 color = background;

    if (dist < 0.1){
        color=vec3(.0);
        vec3 normal = nf(pos,0.2);
        vec3 albedo;

        float refl=0.5;
        if (distance_from_water(pos)<=dist){
            refl=0.5;
            albedo=vec3(0.15,0.19,0.13);
            normal=vec3(0,1,0);
        }else {
        	albedo = terrain_material(pos, normal);
            refl=.0;
        }

        #ifdef SHOW_HEIGHT_LEVELS
        albedo = 1.0/(1.0+vec3(pow(abs(sin(pos.y*4.0)),0.5)));
        refl=.0;
        #endif

        #ifdef SHOW_NORMALS
        albedo = (normal*.5+.5);
        refl=.0;
        #endif

        #ifdef SHOW_SHADOWS
        albedo = vec3(1.0);
        refl=.0;
        #endif

        vec3 npos = pos;
        float ao = .0;
        int ao_i, max_ao_i=4;
        for (ao_i=0; ao_i<max_ao_i; ao_i++){
            float d=pow(1.1, float(ao_i-4));
       		vec3 ndir = (hash(npos)-0.5+vec3(0,0.5,0))*d;
            npos = pos + ndir;
            ao += (df(npos)+(d*.5))/d;
        }
        ao *= 1./float(max_ao_i);
        ao=df(pos+normal)*.5+.5;
        ao=df(pos+normal*0.5)+.5;
        ao=df(pos+normal*4.0)*.25+.5;
        color+=albedo*ao*color_sky*sky_brightness;

        float shadow=1.0;
        int sh_i, sh_max=40;
        vec3 spos = pos+ldir;
        float strav =.1;
        for(sh_i=0; sh_i<sh_max; sh_i++){
            float dist = df(spos+ldir*strav);
            shadow=min(dist/strav,shadow);
            strav+=dist;
            if(shadow<.001||dist>100.0){ break; }
        }
        shadow=max(.0,shadow);
        float diffuse = max(0.,dot(normal, ldir));
        color+=albedo*diffuse*color_sun*sun_brightness*shadow;

        if (refl>.0){
            vec3 refldir=reflect(dir, normal);
            color += background_fn(refldir)*refl;
        }
    }
    color=max(vec3(.0), color);
	color = mix(background, color, 1.0/(1.0+totald*0.0025));

    color = color*1.2 / (1.+color);
    // Output to screen
    fragColor = vec4(color,1.0);
}

// implementation details /////////////////


float hash(float x){ return fract(pow(sin(x*41.7179)*41.1536, 2.0)*13.145); }
vec3 hash(vec3 v){ return vec3(hash(v.x+v.y), hash(v.y+v.z), hash(v.z+v.x)); }
float hash(vec2 v){ return hash(v.x-v.y*59.7177); }

// procedural 2d noise texture, for given (x,y) return value between 0 and 1
float noise_2d(vec2 p){
    vec2 f = fract(p);
    vec2 id = p - f;
    float a = hash(id.xy+vec2(0,0));
    float b = hash(id.xy+vec2(1,0));
    float c = hash(id.xy+vec2(0,1));
    float d = hash(id.xy+vec2(1,1));
    return mix(
        mix(a,b, f.x),
        mix(c,d, f.x),
        f.y
	);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01
#define CAMERA_X 0.
#define CAMERA_Y 3
#define CAMERA_Z 0.
#define LIGHT_Z 5.
#define OBJ_Z 7.
#define LIGHT_MOVE_SPEED 3.
#define OBJ_MOVE_SPEED 3.

float dBox(vec3 curMarchPoint, vec3 s)
{
    return length(max(abs(curMarchPoint) - s, 0.));
}

float sdTorus(vec3 curMarchPoint, vec2 r)
{
   float x = length(curMarchPoint.xz) - r.x;
   return length(vec2(x, curMarchPoint.y)) - r.y;
}

float sdCapsule(vec3 curMarchPoint, vec3 sphereA, vec3 sphereB, float r)
{
    vec3 vAtoB = sphereB - sphereA;
    vec3 vAtoP = curMarchPoint - sphereA;

    float dotAPonAB = dot(vAtoB, vAtoP) / dot(vAtoB, vAtoB);
    dotAPonAB = clamp(dotAPonAB, 0., 1.);

    vec3 closestPointOnCapsule = sphereA + vAtoB * dotAPonAB;

    return length(curMarchPoint - closestPointOnCapsule) - r;
}

float sdSphereDist(vec3 curMarchPoint, vec3 pos, float r)
{
    return length(curMarchPoint - pos) - r;
}

float GetDist(vec3 curMarchPoint)
{
    vec2 iTimeInterp1 = vec2(sin(iTime * OBJ_MOVE_SPEED), cos(iTime * OBJ_MOVE_SPEED));

    //Sphere
    vec4 sphereDef = vec4( 0, 2.5, OBJ_Z, 0.5f);
    sphereDef.y += iTimeInterp1.y;
    sphereDef.x += iTimeInterp1.x * .5;
    float sphereDist = sdSphereDist(curMarchPoint, sphereDef.xyz, sphereDef.w);

    //Capsule
    vec3 sphere1 = vec3( -2., 1., OBJ_Z);
    vec3 sphere2 = vec3( -2., 1., OBJ_Z);
    sphere1.xz += iTimeInterp1;
    sphere2.xz -= iTimeInterp1;
    float capsuleRad =  0.2;
    float capsuleDist = sdCapsule(curMarchPoint, sphere1, sphere2, capsuleRad);

    //Torus
    vec3 torusPos = vec3(0.f, 1., OBJ_Z);
    torusPos.xz += iTimeInterp1;
    vec2 torusSize = vec2(1., .2);
    float torusDist = sdTorus(curMarchPoint - torusPos, torusSize);

    vec3 boxPos = vec3(0., 1., OBJ_Z);
    boxPos.xz += iTimeInterp1;
    vec3 boxSize = vec3(.5);
    float boxDist = dBox(curMarchPoint - boxPos, boxSize);

    //Plane
    float planeDist = curMarchPoint.y;

    float retDist = min(planeDist, sphereDist);
    retDist = min(retDist, capsuleDist);
    retDist = min(retDist, torusDist);
    retDist = min(retDist, boxDist);

    return retDist;
}

float RayMarch(vec3 rayOrigin, vec3 rayDir)
{
    float distFromOrigin = 0.f;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 currMarchPoint = rayOrigin + rayDir * distFromOrigin;

        //gets the min radius from currMarchPoint to the scene
        float distToScene = GetDist(currMarchPoint); 
        distFromOrigin += distToScene;

   		if (distFromOrigin > MAX_DIST || distToScene < SURF_DIST) break;
    }

    return distFromOrigin;
}

vec3 GetNormal(vec3 p)
{
    float distP = GetDist(p);
    vec2 e = vec2(0.01, 0); //epsilon vec

    vec3 normal = distP - vec3 (GetDist(p-e.xyy), GetDist(p-e.yxy), GetDist(p-e.yyx));

    return normalize(normal);
}

float GetLight(vec3 p)
{
    //Simple diffuse light model
    vec3 lightPos = vec3 (0, 10, LIGHT_Z);
    lightPos.xz += vec2(sin(iTime * LIGHT_MOVE_SPEED), cos(iTime * LIGHT_MOVE_SPEED));

    vec3 dirToLight = normalize(lightPos - p);
    vec3 normalPointP = GetNormal(p);

    float diffuseLight = clamp(dot(normalPointP, dirToLight), 0., 1.);

    //shadow
    float shadowDist = RayMarch(p + normalPointP * SURF_DIST * 2.f, dirToLight);
    if (shadowDist < length(lightPos - p) && true)
    {
        diffuseLight *= 0.1f;
    }

    return diffuseLight;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    //Ray fire from center and direction of each pixel
    vec3 rayOrigin = vec3(CAMERA_X, CAMERA_Y, CAMERA_Z);
    vec3 rayDir = normalize(vec3(uv.x, uv.y-0.4, 1));

    float intersectionDist = RayMarch(rayOrigin, rayDir);

    vec3 shadePoint = rayOrigin + rayDir * intersectionDist;
    float diffuseColor = GetLight(shadePoint);

    vec3 col = vec3(diffuseColor);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float[] seed = float[](.25, .5,
                     .75, 1.) ;


float rnd(float seed){
	return fract((1. - cos((seed + 21.2313662551) * 37.6447205633))/2.);
}

vec2 color(vec2 uv, float col, int i, float r){
    int x = int(uv.x * 2.)%2;
    int y = int(uv.y * 2.)%2;

    float c;

    float r1 = rnd(float(i));
    float r2 = rnd(seed[y * 2 + x]);

    float r3 = rnd((r1 + r2) * r);


    c = (col + r3 + col * (r3 - 0.5) * 0.6) * 1.2 ;

	return vec2(c, r3);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    uv -= vec2(1.1, 0.6);

    // Starting at 200000x zoom and move it to 1x over period of 30s
    float zoom = 1. / min(1., mix(0.000005, 1., pow((1. + iTime) / 30., 5.)));
    uv = uv / zoom;

    uv += vec2(1.1, 0.6);
    uv.y = 1. - uv.y;
    uv.x += -0.4;

    float inn = iTime * 0.3;
    float val = 0.5; 
    vec2 n_uv = uv;
    float r = 1.;

    // Reduce number of iterations as zoom value became smaller (not necessary, just a demonstration of persistence)
    int iters = int(max(9.,mix(24., 9., pow((1. + iTime) / 30., 5.) )));

    for (int i=0;i<iters;i++){
        vec2 res = color(n_uv, inn, i+1, r);
        inn = res[0];
        r = res[1];
        n_uv = n_uv * 2.;

        val += cos(inn) / pow(float(i + 1), 0.4) /8.;
    }



    // Output to screen

    vec3 col = vec3(val, val, val);
    //if (col.r <0.2) col = vec3(0.4, 0., 0.);
    if (uv.x<0. || uv.x>1.) col = vec3(0., 0., 0.);

    fragColor = vec4(col,1.0);
}

// "Second Image of a Black Hole" by Martijn Steinrucken aka BigWings - 2019
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Email:countfrolic@gmail.com Twitter:@The_ArtOfCode
//
// In honor of the amazing achievement of the photographing of a real black hole,
// behold my 100% fake one. I know next to nothing about black holes other than
// that it distorts spacetime so much that it visibly affects light.
//
// Just marching the light rays and bending them towards the hole bulges the accretion disc
// over the top when you look at it from the side, similar to the way it looked in
// interstellar. I didn't specifically code this, it just came out that way
// so I figure my 'physics' is not completely wrong ;)
//
// The jets coming out the top and bottom I just added because lots of black hole 
// illustrations have them and they look cool :)
//
// Code is a bit of a mess. It annoys me that step size has to be super small in order
// for it to look halfway decent. 

#define SURFDIST .001
#define MAXSTEPS 200
#define MAXDIST 20.
#define TAU 6.2832

#define USEDISC
#define USESTREAM

mat2 Rot(float a) {
	float s = sin(a), c = cos(a);

    return mat2(c, -s, s, c);
}

float N21(vec2 p) {
    p = fract(p*vec2(123.34,345.35));
    p += dot(p, p+34.53);
    return fract(p.x*p.y);
}

float Noise(vec2 p) {
	vec2 gv = fract(p);
    vec2 id = floor(p);

    gv = smoothstep(0.,1.,gv);

    float b = mix(N21(id+vec2(0,0)), N21(id+vec2(1, 0)), gv.x);
    float t = mix(N21(id+vec2(0,1)), N21(id+vec2(1, 1)), gv.x);

    return mix(b, t, gv.y);
}

float Noise3(vec2 p) {
    return 
        (Noise(p) + 
        .50*Noise(p*2.12*Rot(1.)) +
        .25*Noise(p*4.54*Rot(2.)))/1.75;
}

vec3 GetRd(vec2 uv, vec3 ro, vec3 lookat, vec3 up, float zoom, inout vec3 bBend) {
    vec3 f = normalize(lookat-ro),
        r = normalize(cross(up, f)),
        u = cross(f, r),
        c = ro + zoom * f,
        i = c + uv.x*r + uv.y*u,
        rd = normalize(i-ro);

    vec3 offs = normalize(uv.x*r + uv.y*u);
    bBend = rd-.1*offs/(1.+dot(uv,uv));
    return rd;   
}

vec3 GetBg(vec3 rd) {
	float x = atan(rd.x, rd.z);
    float y = dot(rd, vec3(0,1,0));

    float size = 10.;
    vec2 uv = vec2(x, y)*size;
    float m = abs(y);

    float side = Noise3(uv);
    float stars = pow(Noise(uv*20.)*Noise(uv*23.), 10.);

    vec2 puv = rd.xz*size;
    float poles = Noise3(rd.xz*size);
    float stars2 = pow(Noise(puv*21.)*Noise(puv*13.), 10.);

    stars = mix(stars, stars2, m*m);
    float n = mix(side, poles, m*m);
    n = pow(n, 5.);

    vec3 nebulae = n * vec3(1., .7, .5);

    return nebulae + stars*4.;
}

float GetDist(vec3 p) {
    float d = length(p)-.15;

    //d = min(d, max(length(p.xz)-2., abs(p.y)));
    return d;
}

float GetDisc(vec3 p, vec3 pp) {

    float t = iTime;

    // calculate plane intersection point
    vec3 rd = p-pp;			// local ray direction
    vec3 c = pp + rd*pp.y;	// intersection point
    rd = normalize(rd)*.5;
    p = c-rd;
    rd *= 2.;

    // myeah this seemed like a good idea at some point... doesn't add as much as it should
    float m = 0.;
    const float numSamples = 3.;
    for(float i=0.; i<1.; i+=1./numSamples) {
    	c = p + i*rd;

        float d = length(c.xz);
    	float l = smoothstep(3.5, .6, d);
    	l *= smoothstep(.1, .6, d);

        float x = atan(c.x, c.z);
    	l *= sin(x*floor(5.)+d*20.-t)*.3+.7;
        m += l;
    }

    return 1.5*m/numSamples;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 0, -4.+sin(iTime*.2));
    ro.yz *= Rot(m.y*TAU+iTime*.05);
    ro.xz *= Rot(-m.x*TAU+iTime*.1);

    vec3 lookat = vec3(0);
    float zoom = .8;
    vec3 up = normalize(vec3(.5, 1,0));
    vec3 bBend;
    vec3 rd = GetRd(uv, ro, lookat, up, zoom, bBend);
    vec3 eye = rd;

    float dS, dO;
    float disc = 0.;
    vec3 p=ro;
    p += N21(uv)*rd*.05;
    vec3 pp;

    float stream = 0.;

    for(int i=0; i<MAXSTEPS; i++) {
        rd -= .01*p/dot(p,p);		// bend ray towards black hole

        pp = p;
        p += dS*rd;

        if(p.y*pp.y<0.)
            disc += GetDisc(p, pp);

        float y = abs(p.y)*.2;
        stream += smoothstep(.1+y, 0., length(p.xz))*
            smoothstep(0., .2, y)*
            smoothstep(1., .5, y)*.05;

        dS = GetDist(p);
        dS = min(.05, dS);
        dO += dS;
        if(dS<SURFDIST || dO>MAXDIST) break;
    }

    col = GetBg(bBend);

    if(dS<SURFDIST) {
        col = vec3(0);      // its black!
    }

    #ifdef USEDISC
    col += disc*vec3(1,.8,.5)*1.5;
    #endif
    #ifdef USESTREAM
    col += min(.5, stream)*vec3(.7, .7, 1.);
    #endif

    fragColor = vec4(col,1.0);
}

