float noise(vec2 p)
{
    // integer part
    vec2 i = floor(p);
    // fractional part
    vec2 f = fract(p);

    float ul = random(i); // + vec2(0.0, 0.0)
    float ur = random(i + vec2(1.0, 0.0));
    float ll = random(i + vec2(0.0, 1.0));
    float lr = random(i + vec2(1.0, 1.0));

    // mix(x, y, a): lerp between x and y using a: x*(1−a) + y*a
    float u = mix(ul, ur, f.x);
    float l = mix(ll, lr, f.x);

    float n = mix(u, l, f.y);

    return n;
}


float brownian(vec2 p)
{
    int numOctaves = 3;
    float frequency = 1.0;
    float amplitude = 1.0;

    float lacunarity = 2.0;
    float gain = 0.5;

    float result = 0.0;
    for(int i; i < numOctaves; i++)
    {
        result += noise(frequency * p) * amplitude;
        frequency *= lacunarity;
        amplitude *= gain;
    }
    return result;
}

float getHeight(vec2 p)
{

    float b1 = brownian(p)+iTime/1.3;
    float b2 = brownian(1.2*p);

    float fbm1 = brownian(vec2(iTime+p.x, iTime+p.y));
    float fbm1slow = brownian(vec2(iTime*0.3+p.x, iTime*0.3+p.y));
	float fbm2 = brownian(vec2(b1, b2));


    return fbm1+fbm1slow+fbm2;
}

vec3 getNormal(vec2 fragCoord, float zoom)
{
    vec3 normal;
    vec2 thisp = (fragCoord/iResolution.xy) * zoom;
    vec2 p2    = ((fragCoord + vec2(1.0,0.0))/iResolution.xy) * zoom;
    vec2 p3    = ((fragCoord + vec2(0.0,1.0))/iResolution.xy) * zoom;

    /*vec3 thisv 	= vec3(fragCoord.x, 		getHeight(thisp), 	fragCoord.y			);
    vec3 v2 	= vec3(fragCoord.x + 1.0, 	getHeight(p2), 		fragCoord.y			);
    vec3 v3 	= vec3(fragCoord.x, 		getHeight(p3), 		fragCoord.y + 1.0	);*/
    vec3 thisv 	= vec3(thisp.x, getHeight(thisp), thisp.y			);
    vec3 v2 	= vec3(p2.x, getHeight(p2), p2.y);
    vec3 v3 	= vec3(p3.x, getHeight(p3), p3.y);

    vec3 Vec1 = thisv - v3;
    vec3 Vec2 = v3 - v2;

    normal = normalize(cross(Vec1, Vec2));

    return normal;
}



