float valueNoise(vec2 uv){
    vec2 i = fract(uv);
    vec2 f = floor(uv);
	float a = rand(f);
    float b = rand(f + vec2(1.0, 0.0));
    float c = rand(f + vec2(0.0, 1.0));
    float d = rand(f + vec2(1.0, 1.0));    
    return mix(mix(a, b, i.x), mix(c, d, i.x), i.y);
