float fbm(vec2 uv) {
    float v = 0.0;
    float freq = 9.5;
    float amp = .75;
    float z = (20. * sin(iTime * .2)) + 30.;

    for (int i = 0; i < 10; ++i) {
        v += valueNoise(uv + (z * uv * .05) + (iTime * .1)) * amp;
    	uv *= 3.25;        
        amp *= .5;
    }

    return v;    
}

