    fragColor = vec4(color); // vec4(id*.2, 0.0, 1.0)*eye;
}

float Circle (vec2 uv, float r, float lw, float blur) 
{
	return smoothstep(r + blur, r - .005, length(uv)) -
		smoothstep(r - lw, r - lw - .005 - blur, length(uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
	uv.x *= iResolution.x / iResolution.y;
	vec3 col = vec3(0.);
	vec3 blue = vec3(.2, .2, .6);
	vec3 pink = vec3(.6, .2, .6);

	col += .6 - mix(blue, pink, .3) * length(uv);
	col += Circle(uv, .3, .01, .02) * blue * vec3(sin(iTime * .5) + 1.);
	col += Circle(uv, .3, .01, .002);
	col += Circle(uv, .38, .02, .03) * pink * vec3(sin(iTime * .5) + 1.);
	col += Circle(uv, .38, .02, .002);
	col *= vec3(sin(iTime * .5) * .3 + .8) * (1. - length(uv));

	fragColor = vec4(col, 1.);
}

