// From: https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p) {
  vec3 p3 = fract(vec3(p.xyx) * .1031);
  p3 += dot(p3, p3.yzx + 33.33);
  return fract((p3.x + p3.y) * p3.z);
}

vec2 posInCell(vec2 cell) {
  if (hash12(cell * 109.0) < 0.8) {
    // Cell is empty, so return something really far off screen
    return vec2(-100.0);
  }
  return cell + vec2(hash12(cell), hash12(cell * 33.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float s = 10.0 * pow(2.0, cos(iTime));
  vec2 grid = fract(fragCoord.xy / s) * s;
  vec2 cell = floor(fragCoord.xy / s);
  float gridLines = clamp(2.0 - min(grid.x, grid.y), 0.0, 0.1);
  float color = gridLines;

  for (float x = -1.0; x <= 1.0; x += 1.0) {
    for (float y = -1.0; y <= 1.0; y += 1.0) {
      vec2 neighbor = cell + vec2(x, y);
      vec2 pos = posInCell(neighbor);
      float len = length(fragCoord.xy / s - pos);
      float neighborColor = clamp(s * 0.25 - len * s, 0.0, 1.0);
      color = max(color, neighborColor);
    }
  }

  fragColor = vec4(vec3(color), 1);
}

// Big thanks to @BigWings for his youtube videos! 
// Impressed by https://www.youtube.com/watch?v=r1UOB8NVE8I

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;

    vec2 st = vec2(atan(uv.x, uv.y), length(uv));

    float c1 = 0.0;
    float c2 = 0.0;
    float c3 = 0.0;
    float z = 1.0;
    for(float i = 0.0; i <= 3.14; i += 0.314) {
        z *= -1.0;
        uv = vec2(st.x / 6.2831 + i + (sign(z) * iTime * sqrt(i * 5.0) * 0.01), st.y + cos(iTime+i) * 0.02);

        float x = uv.x * 14.0;
        float m = min(fract(x), fract(1.0 - x));
        c1 += smoothstep(0.0, 0.01, m * 0.5 + 0.2 - uv.y*i*.75) * 0.20;
        c2 += sign(z) * smoothstep(0.0, 0.01, m * 0.5  - uv.y*i) * 0.75;
        c3 += sign(z * -1.0) * smoothstep(0.0, 0.01, m * 0.5 + 0.2 - uv.y) * 0.75;
    }

    vec3 col = vec3(fract(c3*.5), fract(c1 +c3), floor(c2 + c1)) ;

    fragColor = vec4(col, 1.0);
}

