        sin(p.x*43.834 + p.y*78.934 + 
            p.z*48.934 + p.w*48.729));
    return vec16(a, b, c, d);
}

vec4 dott (vec16 f, vec4 v) //"No matching overload function found"
{
    return vec4(
    dot(f.x, v),
    dot(f.y, v),
    dot(f.z, v),
    dot(f.w, v));
}

vec8 perlin4x4 (vec4 p)
{
    // Address and interpolation values
    vec4 f = fract(p),
    m = f*f*f*(f*f*6. - f*15. + 10.);
    p -= f;

    // Interpolating the gradients for noise
    vec4 noise = mix(mix(mix(
        mix(dott(hash(p + vec4(0,0,0,0)), f - vec4(0,0,0,0)), 
            dott(hash(p + vec4(1,0,0,0)), f - vec4(1,0,0,0)), m.x), 
        mix(dott(hash(p + vec4(0,1,0,0)), f - vec4(0,1,0,0)), 
            dott(hash(p + vec4(1,1,0,0)), f - vec4(1,1,0,0)), m.x), m.y), mix(
        mix(dott(hash(p + vec4(0,0,1,0)), f - vec4(0,0,1,0)), 
            dott(hash(p + vec4(1,0,1,0)), f - vec4(1,0,1,0)), m.x), 
        mix(dott(hash(p + vec4(0,1,1,0)), f - vec4(0,1,1,0)), 
            dott(hash(p + vec4(1,1,1,0)), f - vec4(1,1,1,0)), m.x), m.y), m.z), mix(mix(
        mix(dott(hash(p + vec4(0,0,0,1)), f - vec4(0,0,0,1)), 
            dott(hash(p + vec4(1,0,0,1)), f - vec4(1,0,0,1)), m.x), 
        mix(dott(hash(p + vec4(0,1,0,1)), f - vec4(0,1,0,1)), 
            dott(hash(p + vec4(1,1,0,1)), f - vec4(1,1,0,1)), m.x), m.y), mix(
        mix(dott(hash(p + vec4(0,0,1,1)), f - vec4(0,0,1,1)), 
            dott(hash(p + vec4(1,0,1,1)), f - vec4(1,0,1,1)), m.x), 
        mix(dott(hash(p + vec4(0,1,1,1)), f - vec4(0,1,1,1)), 
            dott(hash(p + vec4(1,1,1,1)), f - vec4(1,1,1,1)), m.x), m.y), m.z), m.w);
    // Interpolating the values of the gradients for the first derivative normal
    // (of the alpha channel)
    // It's faster to recalculate the hashes by the way
    vec4 derivative = mix(mix(mix(
        mix(hash(p + vec4(0,0,0,0)).w, 
            hash(p + vec4(1,0,0,0)).w, m.x), 
        mix(hash(p + vec4(0,1,0,0)).w, 
            hash(p + vec4(1,1,0,0)).w, m.x), m.y), mix(
        mix(hash(p + vec4(0,0,1,0)).w, 
            hash(p + vec4(1,0,1,0)).w, m.x), 
        mix(hash(p + vec4(0,1,1,0)).w, 
            hash(p + vec4(1,1,1,0)).w, m.x), m.y), m.z), mix(mix(
        mix(hash(p + vec4(0,0,0,1)).w, 
            hash(p + vec4(1,0,0,1)).w, m.x), 
        mix(hash(p + vec4(0,1,0,1)).w, 
            hash(p + vec4(1,1,0,1)).w, m.x), m.y), mix(
        mix(hash(p + vec4(0,0,1,1)).w, 
            hash(p + vec4(1,0,1,1)).w, m.x), 
        mix(hash(p + vec4(0,1,1,1)).w, 
            hash(p + vec4(1,1,1,1)).w, m.x), m.y), m.z), m.w);
    return vec8(noise, derivative);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float a = iTime/2.;

    vec3 ro = -vec3(sin(a), 0, cos(a))*7.5;
    vec3 rs = normalize(vec3(uv, 1));
    rs.xz *= mat2(cos(-a),-sin(-a),sin(-a),cos(-a));

    vec3 p;

    fragColor = vec4(0.);
    if (uv.x < cos(iTime/2.))
    {
        for (float d = 9.; d >= 5.; d -= .25)
        {
            vec3 p = ro + rs*d;
            vec8 n = perlin4x4(vec4(p*1.25, iTime/2.));
            n.x = n.x*.5 + .5;
            fragColor = mix(fragColor, n.x*.75+.25,
                        pow(n.x.a, 6.)*smoothstep(3., 2.7, length(p)));
        }
        fragColor *= 2.;
    }else{
	    float d = 5.;
        vec8 noise;
        float m;
        vec3 norm;
        for (d; d <= 8.;)
        {
            noise = perlin4x4(vec4((ro+rs*d)*1.25, iTime/2.));
            m = max(length(ro+rs*d)-2.8, -noise.x.w*.5+.05);
            if (m <= .01)
            {
                p = ro+rs*d; 
                fragColor = noise.x*.5+.5;
                norm = normalize((length(p) < 2.8)? noise.y.xyz : p);
                fragColor += .7*max(pow(dot(reflect(rs, norm), vec3(.577)), 9.), 0.);
                fragColor *= dot(norm, vec3(.577))*.25+.75;
                break;
            }
            d += m;
        }
    }
}

// "ShaderToy Tutorial - Ray Marching Primitives" 
// by Martijn Steinrucken aka BigWings/CountFrolic - 2018
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// This shader is part of a tutorial on YouTube
// https://youtu.be/Ff0jJyyiVyw

#define MAX_STEPS 15
#define MAX_DIST 15.
#define SURF_DIST .001


float time;

//-----------------------------------------------------------------------------
float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

//-----------------------------------------------------------------------------
float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0;

    float res = mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                    mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);

    return res;
}

//-----------------------------------------------------------------------------
float SmokeParticle(vec2 loc, vec2 pos, float size, float rnd)
{
	loc = loc-pos;
	float d = dot(loc, loc)/size;
	// Outside the circle? No influence...
	if (d > 1.0) return 0.0;

	// Rotate the particles...
	float r= time*rnd*1.85;
	float si = sin(r);
	float co = cos(r);
	// Grab the rotated noise decreasing resolution due to Y position.
	// Also used 'rnd' as an additional noise changer.
	d = noise(hash(rnd*828.0)*83.1+mat2(co, si, -si, co)*loc.xy*2./(pos.y*.16)) * pow((1.-d), 3.)*.7;
	return d;
}

//-----------------------------------------------------------------------------
float RockParticle(vec2 loc, vec2 pos, float size, float rnd)
{
	loc = loc-pos;
	float d = dot(loc, loc)/size;
	// Outside the circle? No influence...
	if (d > 1.0) return 0.0;
	float r= time*1.5 * (rnd);
	float si = sin(r);
	float co = cos(r);
	d = noise((rnd*38.0)*83.1+mat2(co, si, -si, co)*loc*143.0) * pow(1.0-d, 15.25);
	return pow(d, 2.)*5.;

}


//Cone
float sdCone(in vec3 p, in vec3 c) {
    vec2 q = vec2( length(p.xz), p.y );
    float d1 = -q.y-c.z;
    float d2 = max( dot(q,c.xy), q.y);

    return length(max(vec2(d1,d2),0.5)) + min(max(d1,d2), 0.);
}



float GetDist(vec3 p) {	 	
    float planeDist = p.y;

    float sc = sdCone(p-vec3(-0.75,2.0, 5), vec3(3.0));

    float d = min(sc, planeDist);

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(0.3, 0.);
    vec3 n = d - vec3(GetDist(p-e.xyy), GetDist(p-e.yxy), GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);

    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    time = (iTime+1.);
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	 //vec2 uv = ((fragCoord.xy/iResolution.xy)) * 2.0- 1.0;
    vec3 col = vec3(0);


    vec3 ro = vec3(0, 2, 0);
    vec3 rd = normalize(vec3(uv.x-.15, uv.y-.2, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    vec3 texcolor = texture(iChannel0, p.xy + p.xz).rgb;
    col = vec3(dif) + texcolor;



    for (float i = 0.0; i < 40.0; i+=1.0)
	{
		float t = time*1.3+i*(2.+hash(i*-1239.)*2.0);
		float sm = mod(t, 9.3)*.8;
		float rnd = floor(t / 9.3);
		vec2 pos = vec2(0.0, sm) *.5;
		pos.x += (hash(i*33.0+rnd)-.5)*.2 * sm*2.13;
		// Mechanics... a butchered d = vt + (1/2)at^2    ;)
		pos.y += (.1 - (.075+hash(i*30.0+rnd*36.7)*.15)*(sm*sm)*.8);
		float d = RockParticle(pos, uv, .01*hash(i*1332.23)+.001, (hash(-i*42.13*rnd)-.5)*15.0);
		if (d <= 0.0) continue;
		float c = max(.3+abs(hash(i*11340.0))*.8+(1.0-sm*.5), 0.0);
		col = mix(col, vec3(c,c*.2, 0.0), min(d, 1.));
	}

	// Loop through smoke particles...
	for (float i = 0.0; i < 120.0; i+=1.0)
	{
		// Lots of magic numbers? Yerp....
		float t=  time+i*(2.+hash(i*-1239.)*2.0);
		float sm = mod(t, 8.6) *.5;
		float rnd = floor(t / 8.6);

		vec2 pos = vec2(0.0, sm) *.5;
		pos.x += (hash(i)-.5)*.2 * uv.y*5.13;
		float d = SmokeParticle(pos, uv, .03*hash(i*1332.23+rnd)+.001+sm*0.03, hash(i*rnd*2242.13)-0.5);
		if (d <= 0.0) continue;
		d = d* max((3.0-(hash(i*1127.0)*1.5) - sm*.63), 0.0);
		float c = abs(hash(i*4.4));
		// Black/rusty smoke...
		col= mix(col, vec3(c*.3+.05, c*.3, c*.25), min(d, 1.0));
		// Lava gush...
		col = mix(col, vec3(.52, .25, 0.0), max((d-1.05)*8.0, 0.0));
	}
	uv = ((fragCoord.xy/iResolution.xy));
    //col *= pow( 45.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), .08 );

    fragColor = vec4(col,1.0);
}

// Based on a shader by Shane
// https://www.shadertoy.com/view/ll2SRy
// almost all the code comes from his shader. It's a very good resource!

#define PI 3.14159265359
#define grad_step 0.01
#define time iTime+2.31

// Spectrum colour palette
// IQ https://www.shadertoy.com/view/ll2GD3
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 spectrum(float n) {
    //return pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
    //return pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.0,0.5),vec3(1.0,1.0,1.0),vec3(0.2,0.33,0.67) );
    return pal( n, vec3(0.4,0.9,0.7),vec3(0.9,0.2,0.4),vec3(1.0,1.0,1.0),vec3(0.3,0.4,0.8) );

}

// iq's distance functions
float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float opOnion( in float sdf, in float thickness )
{
    return abs(sdf)-thickness;
}

float sdUnion_s( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 carToPol(vec3 p) {
    float r = length(p);
    float the = acos(p.z/r);
    float phi = atan(p.y,p.x);
    return vec3(r,the,phi);
}

// 2D rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}


float map( vec3 pos ){



    vec3 p2 = vec3(1.0*cos(-0.3*time),1.4*sin(time)*cos(-0.4*time),1.7*sin(time)*sin(-0.3*time));
    float d2 = sdSphere( pos-p2, 0.4);
    vec3 p3 = vec3(1.4*sin(0.3*time),0.2,-1.6*sin(0.3*time+0.3));
    float d3 = sdSphere( pos-p3, 0.25);
    vec3 p4 = vec3(0);
    float d4 = sdSphere( pos-p4, 0.25);
    float d00 = sdUnion_s(d2,d3,0.2);
    float d0 = sdUnion_s(d00,d4,0.2);

    vec3 pol = carToPol(pos);

    float d1 = sdSphere( pos, 1.0 );
    float wave = 0.08*sin(15.*(pol.y+PI));
    d1 = opOnion(d1+wave, 0.001);

    return sdUnion_s(d1,d0,0.3);

}

// get gradient in the world
vec3 gradient( vec3 pos ) {
	const vec3 dx = vec3( grad_step, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, grad_step, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, grad_step );
	return normalize (
		vec3(
			map( pos + dx ) - map( pos - dx ),
			map( pos + dy ) - map( pos - dy ),
			map( pos + dz ) - map( pos - dz )			
		)
	);
}

vec3 selfColor(vec3 pos) {
    vec3 pol = carToPol(pos);
    return spectrum(1.0*pol.z/PI/2.0+0.5*pol.y/PI);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

