float rCoeff(in vec3 p, in vec3 rd){
    vec3 g = gradient(p);
    g = rotationXY(vec2(2.0,1.0))*g;
    float refl = 1.0-dot(g,rd);
    return refl;
}
*/

void mainImage( out vec4 fragColor, vec2 fragCoord ) {

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
//    vec3 ro = vec3( -5.0*cos(0.2*iTime+0.0), 0.0, 5.0*sin(0.2*iTime+0.0));
    vec3 ro = vec3( 0.0, 5.0*cos(0.3*time), 5.0*sin(0.3*time));
    vec3 ta = vec3( 0. , 0. , 0. );

    float aa = 1.0/min(iResolution.y,iResolution.x);

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 1.5);  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy, 3.0) ); // 3.0 is the lens length

    // rotate camera with mouse
	mat3 rot = rotationXY( ( iMouse.xy - iResolution.xy * 0.5 ).yx * vec2( 0.01, -0.01 ) );
	rd = rot * rd;
	ro = rot * ro;
    vec3 col = vec3(1), sp;

	// Ray distance, bail out layer number, surface distance and normalized accumulated distance.
	float t=0., layers=0., d, aD;

    // Surface distance threshold. Smaller numbers give a sharper object. Antialiased with aa
    float thD = .4*sqrt(aa); 

    // Only a few iterations seemed to be enough. Obviously, more looks better, but is slower.
	for(int i=0; i<100; i++)	{

        // Break conditions. Anything that can help you bail early usually increases frame rate.
        if(layers>10. || col.x<0. || t>8.) break;

        // Current ray postion
        sp = ro + rd*t;

        d = map(sp); // Distance to nearest point in the cube field.

        // If we get within a certain distance of the surface, accumulate some surface values.
        // Values further away have less influence on the total.
        //
        // aD - Accumulated distance. I interpolated aD on a whim (see below), because it seemed 
        // to look nicer.
        //
        // 1/.(1. + t*t*.25) - Basic distance attenuation. Feel free to substitute your own.

         // Normalized distance from the surface threshold value to our current isosurface value.
        aD = (thD-abs(d))/thD;

        // If we're within the surface threshold, accumulate some color.
        // Two "if" statements in a shader loop makes me nervous. I don't suspect there'll be any
        // problems, but if there are, let us know.
        if(aD>0.) { 
            // Smoothly interpolate the accumulated surface distance value, then apply some
            // basic falloff (fog, if you prefer) using the camera to surface distance, "t."
            // selfColor is the color of the object at the point sp
            vec3 sc = selfColor(sp);

            vec3 g = gradient(sp);

            // reflection value based omn the gradient
		    vec3 rg = rotationXY(vec2(1.5,2.5))*g;
		    float refl = 1.0-dot(rg,rd);
			refl = pow(exp(-3.0*refl),3.0);

            //float refl = 0.1*pow(exp(-4.0*rCoeff(sp,rd)),2.0);
            col -= sc*(aD*aD*(3. - 2.*aD)/(1. + t*t*1.75)*5.0);
            col += 0.15*refl;
            layers++;

            // fake refraction
            // bend the ray by adding some of the gradient
            // this makes the glass look thicker
            rd += 0.5*aD*aD*aD*g*aa;
        }


        // Kind of weird the way this works. I think not allowing the ray to hone in properly is
        // the very thing that gives an even spread of values. The figures are based on a bit of 
        // knowledge versus trial and error. If you have a faster computer, feel free to tweak
        // them a bit.
        t += max(abs(d)*.5, thD*0.8); 
	}

    // I'm virtually positive "col" doesn't drop below zero, but just to be safe...
    col = max(col, 0.);

	fragColor = vec4(clamp(col, 0., 1.), 1);
 }

// divisions of grid
const float repeats = 30.;

// number of layers
const float layers = 21.;

// star colours
const vec3 blue = vec3(51.,64.,195.)/255.;
const vec3 cyan = vec3(117.,250.,254.)/255.;
const vec3 white = vec3(255.,255.,255.)/255.;
const vec3 yellow = vec3(251.,245.,44.)/255.;
const vec3 red = vec3(247,2.,20.)/255.;

// spectrum function
vec3 spectrum(vec2 pos){
    pos.x *= 4.;
    vec3 outCol = vec3(0);
    if( pos.x > 0.){
        outCol = mix(blue, cyan, fract(pos.x));
    }
    if( pos.x > 1.){
        outCol = mix(cyan, white, fract(pos.x));
    }
    if( pos.x > 2.){
        outCol = mix(white, yellow, fract(pos.x));
    }
    if( pos.x > 3.){
        outCol = mix(yellow, red, fract(pos.x));
    }

    return 1.-(pos.y * (1.-outCol));
}

float N21(vec2 p){
    p = fract(p*vec2(233.34, 851.73));
    p+= dot(p, p+23.45);
    return fract(p.x*p.y);
}

vec2 N22 (vec2 p){
	float n = N21(p);
    return vec2 (n, N21(p+n));
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = N21(i);
    float b = N21(i + vec2(1.0, 0.0));
    float c = N21(i + vec2(0.0, 1.0));
    float d = N21(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

float perlin2(vec2 uv, int octaves, float pscale){
    float col = 1.;
    float initScale = 4.;  
    for ( int l; l < octaves; l++){
        float val = noise(uv*initScale);
        if (col <= 0.01){
            col = 0.;
            break;
        }
        val -= 0.01;
        val *= 0.5;
        col *= val;
        initScale *= pscale;
    }
 	return col;
}

vec3 stars(vec2 uv, float offset){

    float timeScale = -(iTime + offset) / layers;

    float trans = fract(timeScale);

    float newRnd = floor(timeScale);

    vec3 col = vec3(0.);


    // translate uv then scale for center
    uv -= vec2(0.5);
    uv = scale( vec2(trans) ) * uv;
    uv += vec2(0.5);

    // create square aspect ratio
    uv.x *= iResolution.x / iResolution.y;

    // add nebula colours
    float colR = N21(vec2(offset+newRnd));
    float colB = N21(vec2(offset+newRnd*123.));

    // generate perlin noise nebula on every third layer
    if (mod(offset,3.) == 0.){
    	float perl = perlin2(uv+offset+newRnd,3,2.);
    	col += vec3(perl*colR,perl*0.1,perl*colB);
    }

    // create boxes
    uv *= repeats;

    // get position
    vec2 ipos = floor(uv);

    // return uv as 0 to 1
    uv = fract(uv);

    // calculate random xy and size
    vec2 rndXY = N22(newRnd + ipos*(offset+1.))*0.9+0.05;
    float rndSize = N21(ipos)*100.+200.;


    vec2 j = (rndXY - uv)*rndSize;
    float sparkle = 1./dot(j,j);

    col += spectrum(fract(rndXY*newRnd*ipos)) * vec3(sparkle);


	// visualize layers
    /*if ((uv.x > 9. || uv.y > 0.99) && ipos.y == 8.){
        col += vec3(1.,0.,0.)*smoothstep(1.,0.5,trans);
    }
    if (mod(offset,3.) == 0.){
    	if (uv.x > 0.99 || uv.y > 0.99){
        	col += vec3(1.,0.,0.)*smoothstep(0.2,0.1,trans);
    	}
    }*/

   	col *= smoothstep(1.,0.8,trans);	
    col *= smoothstep(0.,0.1,trans);
    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);

    for (float i = 0.; i < layers; i++ ){
    	col += stars(uv, i);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 rot(vec2 uv,float a){
	return vec2(uv.x*cos(a)-uv.y*sin(a),uv.y*cos(a)+uv.x*sin(a));
}

mat2 rot(float a)
{

 float si = sin(a);
    float cs = cos(a);

    mat2 mat= mat2(cs, -si, si, cs);
    return mat;
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Value Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float oldnoise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
	vec2 uv = p.xy + f.xy*f.xy*(3.0-2.0*f.xy);
	return texture( iChannel0, (uv+118.4)/200., -100.0 ).y-0.4;
}


mat2 mat = mat2(.944, -.640, 1.124, 1.076);

float oldfbm(vec2 st)
{
    float f = noise(st)*0.5; st*=2.02*mat;
    f += noise(st)*0.25; st*=2.04*mat;
    f += noise(st)*0.125; st*=2.03*mat;
    f += noise(st)*0.06125; st*=2.03*mat;

    return f;
}


