float trace(vec3 ro, vec3 rd)
{
    float eps = 0.001;
    float dist;
    float t = 0.0;
    for(int i = 0;i<96;i++)
    {
        dist = map(ro+rd*t);
    if(dist<eps || t > 120.)
        break;

    t +=dist*0.95;
    }

    return t;

}


