// zoomable + values display variant of https://shadertoy.com/view/3lf3DM

float pInt(vec2,float);                    // display integer
// #define S(v) min( 1., abs(v)/p  )       // lazy AA

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U, P,V,B, C = vec2(1,0);
    float l,r,  _F=0.,F=1.,                // Fibonacci sequence
          p = exp2(iTime)/R.y;             // pixel size (i.e. 1/scaling)

    U = p* ( u+u - R );

    O = vec4(1);

    for (float i=0.; i<3.+ iTime/log(2.); i++) {
        V = cos( 1.5708 * vec2(i+1.,i) );  // next box direction
        r = F/2.;                          // box radius & center
        B = C - r* vec2(V.y+V.x,V.y-V.x);  // vec2(1)*mat2(V.y,V.x,-V.x,V.y);
        P = abs( B - U );
        l = max(P.x,P.y) - r;              // SDF to box border
        if ( l < p/4.) {                   // in box: draw arc and box
            O = mix(vec4(1,0,0,1), .98+.02*cos( i + vec4(0,23,21,0) ), min(1.,abs(length(C-U)-2.*r)/p/5.) ); // https://www.shadertoy.com/view/ll2cDc
            O = min(O, abs(l)/p/3. );
            O.rg -= pInt((U-B)*6./F,F)     // display size = Fibonacci
                    *( i<36.? 1.: .2 );    // faint when approx
         // O.rg -= pInt((U-B)*8./p/R.y,F);
        }
        C += _F * V;                       // next arc center
        l=F; F += _F; _F=l;                // Fibonacci sequence
    }
}

// --- utils : display integer
#define C(U,c) U.x<.0||U.x>1.||U.y<0.||U.y>1. ?0.: textureGrad(iChannel3, U/16. + fract( vec2(c, 15-(c)/16) / 16.), dFdx(U/16.),dFdy(U/16.) ).x
float pInt(vec2 p, float n) {
    float v = 0.; 
    p+=.5; p.x -= .25*floor(log(n)/log(10.));// centering
    for (int i = int(n); i>0; i/=10, p.x += .5 )
        v += C(p, 48+ i%10 );
    return v;
}

#define ITERATIONS 64
#define MAX_DISTANCE 200.
#define MIN_DISTANCE .001

float sdSphere(vec3 pos, vec3 center, float r) {
 	return length(pos-center) - r;
}

float sdFloor(vec3 pos) {
 	return pos.y;
}

float sdScene(vec3 pos) {
 	return min(sdFloor(pos),sdSphere(pos, vec3(0.,0.5,-3.), .5));
}

vec3 grad(vec3 pos) {
 	vec2 e = vec2(0.,0.1);
    return normalize(vec3(
        sdScene(pos + e.yxx) - sdScene(pos - e.yxx), 
        sdScene(pos + e.xyx) - sdScene(pos - e.xyx), 
        sdScene(pos + e.xxy) - sdScene(pos - e.xxy)
    ));
}

float castray(vec3 ro, vec3 rd) {
    float d = 1.;
  	float inc;
    for(int i=0; i< ITERATIONS;i++) {
        vec3 p = ro + d * rd;
     	inc = sdScene(p);
        d += inc;
        if(d > MAX_DISTANCE || inc <= MIN_DISTANCE) break;
    }
    return d;
}

float getDiffuse(vec3 p, float dist) {
   vec3 lightPos = vec3(0.+3.*sin(iTime), 4.,-3.+3.*cos(iTime));
   vec3 lightV = normalize(lightPos - p);
   vec3 n = grad(p);
   float d = dot(lightV,n); 
   // shadows
    float rayToLight = castray(p, lightV);
    if(rayToLight < length(lightPos-p)) d *=0.1;
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord /iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 camPos = vec3(0.,.5,0.);
	vec3 ro = camPos + vec3(0.,0.,1.);
    vec3 rd = normalize(camPos + vec3(uv,0.) - ro);
    float dist = castray(ro, rd);
    vec3 p = ro + rd * dist;
    float diff = getDiffuse(p, dist);
    vec3 shade = diff*vec3(1.0,0.,0.);
    if(diff>0.) shade += pow(diff,5.)*vec3(0.,1.,0.);
    vec3 col = shade;

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 hash(vec3 p)
{ 
    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768)*n); 
}

float map(vec3 p)
{
    vec3 o = hash(floor(p)) * 0.1; 

    p = fract(p + o) - .5; 
    float r = dot(p, p) - 0.21; 
    p = abs(p); 

    float d = max(p.x, p.y);
    return d;    

}

