// Basic domain coloring plot (https://en.wikipedia.org/wiki/Domain_coloring)
// of the Lambert W function w = W(z) (https://en.wikipedia.org/wiki/Lambert_W_function)

#define PI 3.14159265359
#define EE 2.71828182846
#define SCALE 5.0 // plot scale
#define SPACING 0.0625 // grid line spacing

// from Fabrice Neyret, 
#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) )
#define cinv(Z) ( vec2( (Z).x, -(Z).y ) / dot(Z, Z) ) 
#define cdiv(A,B) cmul( A, cinv(B) )

// Schlick bias function, from http://dept-info.labri.u-bordeaux.fr/~schlick/DOC/gem2.ps.gz
float bias( float a, float x )
{
    return x/((1.0/a - 2.0) * (1.0 - x) + 1.0);
}

// biased sawtooth
float my_saw( float x, float p )
{
    float xs = mod(x, 1.0);
    float xh = clamp(xs, 0.0, p);
    return 0.5 + 0.5 * bias(0.95, xh) * (1.0 - smoothstep(p, 1.0, xs));
}

// modified version of Inigo Quilez's method at https://www.shadertoy.com/view/MsS3Wc
// using "rational smoothstep" from https://tpfto.wordpress.com/2019/03/28/on-a-rational-variant-of-smoothstep/
vec3 smooth_hue( float h )
{
    vec3 rgb = clamp( abs(mod(6.0 * h + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// modified DLMF coloring, adapted from https://www.shadertoy.com/view/WtXGWN
vec3 smooth_dlmf( float h )
{
    vec3 rgb = clamp( vec3(1., -1., -1.) * abs((vec3(8., 4., 8.) * mod(h, 1.0) - vec3(4.5, 1.5, 5.5))) + vec3(-1.5, 1.5, 2.5), 0.0, 1.0 );
	return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// complex square root, from Numerical Recipes

vec2 sqrtz( in vec2 z )
{
	float ar = abs(z.x);
    float ai = abs(z.y);

    float w = (dot(z, z) == 0.0) ? 0.0 : ( (ar >= ai) ? (sqrt(ar) * sqrt(0.5 * (1.0 + sqrt(1.0 + (ai * ai)/(ar * ar))))) : (sqrt(ai) * sqrt(0.5 * (ar/ai + sqrt(1.0 + (ar * ar)/(ai * ai))))));

    return ((w == 0.0) ? vec2(0.0) : ((z.x >= 0.0 ? vec2(w, 0.5 * z.y/w) : vec2(0.5*ai/w, ((z.y >= 0.0) ? w : -w)))));
}

// complex logarithm

vec2 logz( in vec2 z )
{
	return vec2(log(length(z)), atan(z.y, z.x));
}

// Lambert W function, Winitzki's approximation (https://doi.org/10.1007/3-540-44839-X_82)

vec2 LambertW( in vec2 z )
{
    vec2 v = sqrtz(2.0 * EE * z + 2.0);
    vec2 w = 2.0 * logz(vec2(1.0, 0.0) + 0.8842 * v);

    return cdiv(w - logz(vec2(1.0, 0.0) + 0.9294 * logz(vec2(1.0, 0.0) + 0.5106 * v)) - vec2(1.213, 0.0), vec2(1.0, 0.0) + cinv(w + vec2(4.69483568, 0.0)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 z = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
    z *= SCALE;

    vec2 w = LambertW(z);
    float ph = atan(w.y, w.x);
    float lm = log(0.0001 + length(w));

    vec3 c = vec3(1.0);
    c = smooth_dlmf(0.5 * (ph / PI));
    // uncomment for HSV version 
    // c = smooth_hue(0.5 * (ph / PI));

    c *= mix(1.0, my_saw((0.5 * (lm/PI))/SPACING, 0.95) * my_saw((0.5 * (ph / PI))/SPACING, 0.95), 0.5 + 0.5 * cos(iTime));
    fragColor = vec4(c, 1.0);
}

float hash(float x)
{
    return fract(sin(x) * 43758.5453) * 2.0 - 1.0;
}

vec2 startPos(float x, vec2 pos)
{
	return vec2(pos.x + (cos(hash(x)) * 0.05), pos.y + (sin(hash(x * 1.4)) * 0.05));
}

vec2 endPos(float x, vec2 pos)
{
	return vec2(pos.x + (cos(hash(x)) * 0.4), pos.y + (sin(hash(x * 1.7)) * 0.4));
}

float sphere(vec2 center, float rayon, vec2 coord)
{
    return length(coord.xy - center) - rayon;
}

vec3 glow(vec3 color, float ld)
{
    return color * exp(-(ld/25.0)*25.5);
}

vec2 getBasisFragCoord(vec2 coord)
{   
    vec2 frag = coord;
    frag.x /= iResolution.x / iResolution.y;
    frag = frag / 2.0 + 1.0;
    return frag.xy * iResolution.xy;
}

vec4 particles(vec2 uv, float time, float mx, float my, float speed)
{
    float px = mod(uv.x, 0.86) - 0.43;
    float py = mod(uv.y, 0.86) - 0.43;
    vec3 ret = vec3(0.0);
    vec2 center = vec2(uv.x + px * mx, uv.y + py * my);
    vec4 ret_color = vec4(vec3(0.0), 1.0);

    float i_time = iTime + time;

    const float seq_duration = 2.0;
	float current_seq = floor(i_time / seq_duration);
	float current_seq_time = mod(i_time, seq_duration);

    vec2 start_pos = startPos(current_seq + ((uv.x - px) + (uv.y - py)), center);
	vec2 end_pos   = endPos(current_seq + ((uv.x - px) + (uv.y - py)), center);
	vec2 current_pos;

	float potential_dist = speed * current_seq_time;
	float end_dist = length(end_pos - start_pos);

	current_pos = mix(start_pos, end_pos, potential_dist / end_dist) * step(potential_dist, end_dist);
	current_pos += end_pos * step(end_dist, potential_dist);

    float dist_length     = sqrt((end_pos.x - start_pos.x)*(end_pos.x - start_pos.x) + (end_pos.y - start_pos.y)*(end_pos.y - start_pos.y));
    float dist_bright_off = dist_length * 0.2;
    float dist_current    = sqrt((end_pos.x - current_pos.x)*(end_pos.x - current_pos.x) + (end_pos.y - current_pos.y)*(end_pos.y - current_pos.y));

    float ld = sphere(getBasisFragCoord(current_pos), 0.01, getBasisFragCoord(uv));

    float alpha = smoothstep(0.0, dist_bright_off, dist_length - dist_current) * smoothstep(0.0, dist_bright_off, dist_current);
    alpha *= 1.0 - step(0.1, length(uv - current_pos));
    ret_color = vec4(glow(vec3(1.0, 1.0, 0.0), ld), 1.0) * alpha;

    return ret_color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv * 2.0 - 1.0;
