    fragColor = particles(uv + vec2(25.0), 0.0, 1.0, 1.0, 0.35);
    fragColor += particles(uv - vec2(20.0), 0.25, -1.0, -1.0, 0.1);
    fragColor += particles(uv + vec2(20.0), 0.5, -1.0, 1.0, 0.25);
    fragColor += particles(uv - vec2(40.0), 0.75, 1.0, -1.0, 0.15);

    fragColor += particles(uv + vec2(10.0), 1.75, -1.0, 1.0, 0.3);
    fragColor += particles(uv - vec2(40.0), 1.0, -1.0, -1.0, 0.2);
    fragColor += particles(uv + vec2(30.0), 1.25, -1.0, 1.0, 0.15);
    fragColor += particles(uv - vec2(30.0), 1.5, 1.0, -1.0, 0.25);

    fragColor += particles(uv - vec2(10.0), 0.35, 1.0, -1.0, 0.3);
    fragColor += particles(uv - vec2(15.0), 0.65, 1.0, 1.0, 0.25);
