//---------------------------------- flat_band
vec3 flat_band(float t, vec3 colA, float posA, float posB){
	vec3 retColor = vec3(0.);     
    float maskStep = step(posA, t)*  (1.- step(posB, t));  
    retColor +=   colA * maskStep;
    return retColor;
}

//---------------------------------- color_lerp
vec3 color_lerp(float t, vec3 colA,float posA, float posB){
    //t = mod(t*_Scale +iTime*_Speed, 1.0); // transform UVs
    t = t* (1.0-1.0/float(numColors)) + (1.0/float(numColors)*0.5); // frame for seamless looping
	vec3 retColor = vec3(0.);   // return color
    float m = posA + ((posB - posA)/2.); // midpoint between A and B
    float d = (posB - posA); // distance between A and B
    float maskSmooth =  smoothstep(m-d, m, t) *( 1. - smoothstep(m, m+d, t));      
    retColor = colA * maskSmooth; 
    return retColor;
}

