        a = i % (numColors-1 + ((1-loop)*1000));      
        col +=  color_lerp(uv.x,  colorArray[a],   ptInterval * float(i) ,  ptInterval * float(i)+ptInterval  ); 
        //col += flat_band(uv.x,  colorArray[a],   ptInterval * float(i),  ptInterval * float(i)+ptInterval ); 
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

