// Renderingcode from a shader by Shane
// https://www.shadertoy.com/view/ll2SRy

#define PI 3.14159265359
#define grad_step 0.01

#define wormTimeXF pow(clamp(sin(2.0*PI*time-0.3*2.0*PI),0.0,1.0),0.8)
#define wormTimeXB pow(clamp(sin(2.0*PI*time-0.3*2.0*PI-1.0*PI),0.0,1.0),0.8)

#define time 0.5*iTime

#define ZERO (min(iFrame,0))

vec2 opUC( vec2 d1, vec4 mate1, vec2 d2, vec4 mate2, out vec4 mate){
    if (d1.x<d2.x) {
        mate = mate1;
        return d1;
    }
    mate = mate2;
    return d2;
}


vec2 opUC_s( vec2 a, vec4 matea,vec2 b, vec4 mateb, float k , out vec4 mate){
    float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 );
    float d = mix( b.x, a.x, h ) - k*h*(1.0-h);

    mate = mix( mateb, matea, h ) - k*h*(1.0-h);

    if (a.x<b.x) {
        return vec2(d,a.y);
    }
    return vec2(d,b.y);
}


// iq's distance functions

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

vec3 carToCyl(vec3 p) {
    mat2 rot = mat2(-1.0,0.0,0.,-1.0);
    p.xy = rot*p.xy;
    float r = length(p.xy);
    float the = atan(p.y,p.x);
    return vec3(r,the,p.z);
}

// 2D rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}

float pModPolar(inout vec2 p, float repetitions) { 
	float angle = 2.*PI/repetitions; 
	float a = atan(p.y, p.x) + angle/2.; 
	float r = length(p); 
	float c = floor(a/angle); 
	a = mod(a,angle) - angle/2.; 
	p = vec2(cos(a), sin(a))*r; 
    // For an odd number of repetitions, fix cell index of the cell in -x direction // (cell index would be e.g. -5 and 5 in the two halves of the cell): 
	if (abs(c) >= (repetitions/2.)) c = abs(c);
    return c;
}


float sdVerticalCapsule( vec3 p, float h, float r )
{
    p.z -= clamp( p.z, 0.0, h );
    return length( p ) - r;
}

float blob(vec2 p,float r){
    float f = smoothstep(0.2*r,r,length(p));
    return f;
}

float line( vec3 p, vec3 a, vec3 b, float radius, inout vec4 mate)
{
	vec3 ba = b - a;
	vec3 pa = p - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - radius;
}

float sdCylinder( vec3 p, vec3 c )
{
  return length(p.xy-c.xy)-c.z;
}

// https://www.shadertoy.com/view/4djSRW
// thanks to Dave_Hoskins
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}


vec2 map(in vec3 p, out vec4 mate){

    float tubeTime = floor(time)+smoothstep(0.3,0.7,fract(time));
    p.x += 0.2*sin(2.0*p.z+4.0*time);
    p.z += tubeTime;


    // tube
    vec3 pt = p;
    pt.z -= tubeTime;
	const float xOffset = 0.4;

    // tube material
    vec3 ptm = pt;
    vec3 pcyl = carToCyl(ptm-vec3(xOffset,0,0));
    vec4 mate0 = vec4(1,1,1,0.2);
    vec4 mate1 = vec4(texture(iChannel0,1.0*vec2(pcyl.y/PI,pcyl.z)).rrr,0.6);
    const float radius = 0.3;
    float modRadius = 0.06*mate1.r+radius+0.1*pow(abs(sin(-3.*time+0.25*pt.z)),25.0);
    mate0 = mix(mate0,mate1,0.7);

    vec2 d0 = vec2(sdCylinder(pt, vec3(xOffset,0.0,modRadius)),1.0);

    // leaves
    vec3 pl = pt;
	pl.xy -= vec2(xOffset,0.0);
    pl.z = fract(0.5*pl.z)/0.5;
    float leafIndex = pModPolar(pl.xy,3.);
    pl = rotationXY(vec2(0.0,-2.7))*pl;
    vec2 d1 = vec2(sdVerticalCapsule(pl-vec3(-1.2,0.0,0.4),-1.8,0.03-0.03*pl.z),3.0);

    // leaf material
    mate1 = vec4(0.6,0.8,1,0.2);
    d0 = opUC_s(d0, mate0,d1,mate1,0.3,mate0);

    // worm body
    mate1 = vec4(1,1,1,0.2);
    vec3 pb = p;

    float wormTimeZ = floor(time)+smoothstep(0.3,0.7,fract(time))-floor(time-0.5)-smoothstep(0.3,0.7,fract(time-0.5));
    vec3 back = vec3(-0.4*wormTimeXB+0.7*wormTimeXF,0.,1.5+wormTimeZ);
    float l = length(back);
    pb.x += 0.7*wormTimeXF+radius-0.19+0.4*(0.5+0.5*sin(1.6*PI*p.z/l-0.8));
    pb = rotationXY(vec2(0.0,-atan(back.x/back.z)))*pb;
    pb.z *= 1.0/length(back);

    // worm's freckles
    pcyl = carToCyl(pb-vec3(0.,0.,0.));
    vec2 ps = vec2(10.0*0.5*pcyl.y/PI,20.0*pcyl.z);
   	vec2 fps = fract(ps);
    vec2 ips = floor(ps);
    vec2 ran = hash22(ips);
//    vec2 ran = texelFetch( iChannel0, (ivec2(ips)+10), 0 ).rg;
    float r = 0.2+0.3*ran.x;
    float blob = blob(fps-0.2-0.4*ran.xy, r);
    float freckles = (1.0-blob)*smoothstep(0.0,0.1,pb.z)*smoothstep(1.0,0.9,pb.z);
    mate1 = mix(mate1, vec4(1,0.2,0,0.8), freckles);

    d1 = vec2(sdVerticalCapsule(pb, 1.0, 0.15-0.07*wormTimeZ+0.01*freckles),1.0);

    pb.z *= length(back);
    // head
    vec4 mate2 = vec4(1,0.2,0,0.4);
    vec4 mate3;
    vec2 d2 = vec2(sdSphere(pb-vec3(0,0,-0.2),0.15),2.0);
    d1 = opUC_s(d1, mate1 ,d2, mate2 ,0.1, mate3);

    // back end
    d2 = vec2(sdSphere(pb-vec3(0,0,length(back)+0.15),0.15),3.0);
    d1 = opUC_s(d1, mate3 ,d2, mate2 ,0.1, mate3);

    vec2 dFinal = opUC_s(d0, mate0, d1, mate3, 0.05, mate);
    return dFinal;
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos, in float ep )
{
    vec4 kk;
#if 1
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep, kk).x + 
					  e.yyx*map( pos + e.yyx*ep, kk).x + 
					  e.yxy*map( pos + e.yxy*ep, kk).x + 
					  e.xxx*map( pos + e.xxx*ep, kk).x );
#else
    // prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+e*ep, kk).x;
    }
    return normalize(n);
#endif    

}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

vec3 phongColor(in vec3 p, in vec3 rd){

    vec3 Ks = vec3(1.0); // specular reflected intensity
    float shininess = 80.0;

    vec3 n = calcNormal(p, 0.01);
    vec3 ref = reflect( rd, n );
    vec3 final = vec3(0);


    // light 0
	{
		vec3 light_pos   = p+vec3( -1.0, 1.0, 0.0 );
		vec3 light_color = vec3( 1.0, 1.0, 1.0 );

		vec3 vl = normalize( light_pos - p );
		vec3 specular = vec3( max( 0.0, dot( vl, ref ) ) );
        specular = pow( specular, vec3( shininess ) );
		final += light_color * specular; 
	}
    return final;
}

// The MINIMIZED version of https://www.shadertoy.com/view/Xl2XWt
const float MAX_TRACE_DISTANCE = 11.0;           // max trace distance
const float INTERSECTION_PRECISION = 0.009;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 50;
const float EPS_NORMAL = 0.01;
const float SCALE_DIST = 0.9;


vec2 calcIntersection( in vec3 ro, in vec3 rd ){
    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;
    vec4 kk;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){
        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = map( ro+rd*t , kk);
        h = m.x;
        t += h*SCALE_DIST;
        id = m.y;
    }
    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;
    return vec2( res , id );
}

vec3 render( vec2 res , vec3 ro , vec3 rd ){
  vec3 c = vec3(0);
  if( res.y > -.5 ){
      vec3 pos = ro + rd * res.x;
      c = phongColor(pos, rd);
  }
  return c;
}



void mainImage( out vec4 fragColor, vec2 fragCoord ) {

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
	vec3 ro = vec3(-2,-3,-3.0-time);
    vec3 ta = vec3( 0.3*sin(time), 0.3*cos(2.0*time) , -time);

    float aa = 1.0/min(iResolution.y,iResolution.x);

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, -1.1);  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy, 4.5+1.5*sin(time)) );

    // rotate camera with mouse
	mat3 rot = rotationXY(iMouse.xy * vec2( 0.01, -0.01 ) );
    vec3 shift = ro-vec3(-2,-3,-3.0);
    ro -= shift;
	rd = rot * rd;
	ro = rot * ro;
    ro += shift;

    vec3 col = vec3(0.05,0.05,0.15);
    vec3 sp;
    vec4 mate;

    // volume rymarching
	// Ray distance, bail out layer number, surface distance and normalized accumulated distance.
	float t=0., layers=0., d, aD;

    // Surface distance threshold. Smaller numbers give a sharper object. Antialiased with aa
    float thD = 0.25*sqrt(aa); 

    // Only a few iterations seemed to be enough. Obviously, more looks better, but is slower.
    for(int i=0; i<90; i++)	{
        if(layers>100. || col.g>1.0 || t>11.) break;
        sp = ro + rd*t;
        d = map(sp, mate).x; // Distance to nearest point in the cube field.
        // Normalized distance from the surface threshold value to our current isosurface value.
        aD = (thD-abs(d))/thD;
        // If we're within the surface threshold, accumulate some color.
        if(aD>0.) { 
            vec3 tc = mate.rgb*pow(mate.a,2.0);
            col += 5.0*tc*(aD*aD*(3. - 2.*aD)/(1. + t*t*.2));
            layers += 30.0*pow(mate.a+0.2,2.0);
        }
        t += max(abs(d)*0.7, thD*0.7); 
	}

    // standard raymarching code to color the surface
    vec2 res = calcIntersection( ro , rd  );
    vec3 surfaceColor = render(res, ro, rd);
    col += surfaceColor;


    col = clamp(col, 0.0,1.0);
    // post processing
    col = pow(col,vec3(0.7,0.85,1.0));
    // iq's vignetting
 	vec2 q = fragCoord/iResolution.xy;
    col *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.3 );

	fragColor = vec4(clamp(col, 0., 1.), 1);
 }

float line(vec2 uv, float height, float width) 
{
    return smoothstep(height-width/2., height, uv.y) // Add what's above the line's bottom
         - smoothstep(height, height+width/2., uv.y); // Subtract what's above the line's top
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/min(iResolution.x, iResolution.y);

    vec3 col = vec3(line(uv, .5+sin(iTime)*.5, .02), 0, 0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

