// Bounding box of a capsule


// Other capsule functions:
//
// Capsule intersection: https://www.shadertoy.com/view/Xt3SzX
// Capsule bounding box: https://www.shadertoy.com/view/3s2SRV
// Capsule distance:     https://www.shadertoy.com/view/Xds3zN
// Capsule occlusion:    https://www.shadertoy.com/view/llGyzG


// Other bounding box functions:
//
// Disk             - 3D BBox : https://www.shadertoy.com/view/ll3Xzf
// Cylinder         - 3D BBox : https://www.shadertoy.com/view/MtcXRf
// Ellipse          - 3D BBox : https://www.shadertoy.com/view/Xtjczw
// Cone             - 3D BBox : https://www.shadertoy.com/view/WdjSRK
// Capsule          - 3D Bbox : https://www.shadertoy.com/view/3s2SRV
// Cubic     Bezier - 2D BBox : https://www.shadertoy.com/view/XdVBWd 
// Quadratic Bezier - 3D BBox : https://www.shadertoy.com/view/ldj3Wh
// Quadratic Bezier - 2D BBox : https://www.shadertoy.com/view/lsyfWc



#define AA 3

struct bound3
{
    vec3 mMin;
    vec3 mMax;
};

//---------------------------------------------------------------------------------------
// bounding box for a capsule
//---------------------------------------------------------------------------------------
bound3 CapsuleAABB( in vec3 pa, in vec3 pb, in float ra )
{
    vec3 a = pb - pa;

    return bound3( min( pa - ra, pb - ra ),
                   max( pa + ra, pb + ra ) );
}

// http://www.iquilezles.org/www/articles/intersectors/intersectors.htm
float iCapsule( in vec3 ro, in vec3 rd, in vec3 pa, in vec3 pb, in float r )
{
    vec3  ba = pb - pa;
    vec3  oa = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoa = dot(ba,oa);
    float rdoa = dot(rd,oa);
    float oaoa = dot(oa,oa);

    float a = baba      - bard*bard;
    float b = baba*rdoa - baoa*bard;
    float c = baba*oaoa - baoa*baoa - r*r*baba;
    float h = b*b - a*c;
    if( h>=0.0 )
    {
        float t = (-b-sqrt(h))/a;

        float y = baoa + t*bard;

        // body
        if( y>0.0 && y<baba ) return t;

        // caps
        vec3 oc = (y<=0.0) ? oa : ro - pb;
        b = dot(rd,oc);
        c = dot(oc,oc) - r*r;
        h = b*b - c;
        if( h>0.0 )
        {
            return -b - sqrt(h);
        }
    }
    return -1.0;
}

// compute normal
vec3 capNormal( in vec3 pos, in vec3 a, in vec3 b, in float r )
{
    vec3  ba = b - a;
    vec3  pa = pos - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    return (pa - h*ba)/r;
}


// ray-box intersection
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 cen, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*(ro-cen);
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

	if( tN > tF || tF < 0.0) return vec2(-1.0);

	return vec2( tN, tF );
}


float hash1( in vec2 p )
{
    return fract(sin(dot(p, vec2(12.9898, 78.233)))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);

#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

        // camera position
        vec3 ro = vec3( -0.5, 0.4, 1.5 );
        vec3 ta = vec3( 0.0, 0.0, 0.0 );
        // camera matrix
        vec3 ww = normalize( ta - ro );
        vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
        vec3 vv = normalize( cross(uu,ww));
        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // cylidner animation
        vec3  c_a =  0.2 + 0.3*sin(iTime*vec3(1.11,1.27,1.47)+vec3(2.0,5.0,6.0));
        vec3  c_b = -0.2 + 0.3*sin(iTime*vec3(1.23,1.41,1.07)+vec3(0.0,1.0,3.0));
        float c_ra =  0.3 + 0.2*sin(iTime*1.3+0.5);

        // render
        vec3 col = vec3(0.4)*(1.0-0.3*length(p));

        // raytrace
        float t = iCapsule( ro, rd, c_a, c_b, c_ra );
        float tmin = 1e10;
        if( t>0.0 )
        {
            tmin = t;
            // shading/lighting	
            vec3 pos = ro + t*rd;
            vec3 nor = capNormal( pos, c_a, c_b, c_ra );

            float dif = clamp( dot(nor,vec3(0.5,0.7,0.2)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            col = sqrt( vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif );
            col *= vec3(1.0,0.75,0.3);
        }


        // compute bounding box of cylinder
        bound3 bbox = CapsuleAABB( c_a, c_b, c_ra );

        // raytrace bounding box
        vec3 bcen = 0.5*(bbox.mMin+bbox.mMax);
        vec3 brad = 0.5*(bbox.mMax-bbox.mMin);
        vec2 tbox = iBox( ro, rd, bcen, brad );
        if( tbox.x>0.0 )
        {
            // back face
            if( tbox.y < tmin )
            {
                vec3 pos = ro + rd*tbox.y;
                vec3 e = smoothstep( brad-0.03, brad-0.02, abs(pos-bcen) );
                float al = 1.0 - (1.0-e.x*e.y)*(1.0-e.y*e.z)*(1.0-e.z*e.x);
                col = mix( col, vec3(0.0), 0.25 + 0.75*al );
            }
            // front face
            if( tbox.x < tmin )
            {
                vec3 pos = ro + rd*tbox.x;
                vec3 e = smoothstep( brad-0.03, brad-0.02, abs(pos-bcen) );
                float al = 1.0 - (1.0-e.x*e.y)*(1.0-e.y*e.z)*(1.0-e.z*e.x);
                col = mix( col, vec3(0.0), 0.15 + 0.85*al );
            }
        }

        // no gamma required here, it's done in line 118

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    // dithering
    tot += ((hash1(fragCoord.xy)+hash1(fragCoord.yx+13.1))/2.0 - 0.5)/256.0;


	fragColor = vec4( tot, 1.0 );
}

#define PI 3.141592

float random1(vec3 p){
    return fract(sin(dot(p.xyz,vec3(12.9898,46.2346,78.233)))*43758.5453123)*2.0-1.0;
}

float random2(vec3 p){
    return fract(sin(dot(p.xyz,vec3(73.6134,21.6712,51.5781)))*51941.3781931)*2.0-1.0;
}

float random3(vec3 p){
    return fract(sin(dot(p.xyz,vec3(39.1831,85.3813,16.2981)))*39183.4971731)*2.0-1.0;
}

float perlinNoise(vec3 p){
    vec3 i1=floor(p);    
    vec3 i2=i1+vec3(1.0,0.0,0.0);
    vec3 i3=i1+vec3(0.0,1.0,0.0);
    vec3 i4=i1+vec3(1.0,1.0,0.0);
    vec3 i5=i1+vec3(0.0,0.0,1.0);
    vec3 i6=i1+vec3(1.0,0.0,1.0);
    vec3 i7=i1+vec3(0.0,1.0,1.0);
    vec3 i8=i1+vec3(1.0,1.0,1.0);
    vec3 f1=vec3(random1(i1),random2(i1),random3(i1));
    vec3 f2=vec3(random1(i2),random2(i2),random3(i2));
    vec3 f3=vec3(random1(i3),random2(i3),random3(i3));
    vec3 f4=vec3(random1(i4),random2(i4),random3(i4));
    vec3 f5=vec3(random1(i5),random2(i5),random3(i5));
    vec3 f6=vec3(random1(i6),random2(i6),random3(i6));
    vec3 f7=vec3(random1(i7),random2(i7),random3(i7));
    vec3 f8=vec3(random1(i8),random2(i8),random3(i8));
    vec3 k1=p-i1;
    vec3 k2=p-i2;
    vec3 k3=p-i3;
    vec3 k4=p-i4;
    vec3 k5=p-i5;
    vec3 k6=p-i6;
    vec3 k7=p-i7;
    vec3 k8=p-i8;
    vec3 j=fract(p);
    j=j*j*(3.0-2.0*j);
	return mix(mix(mix(dot(f1,k1),dot(f2,k2),j.x),mix(dot(f3,k3),dot(f4,k4),j.x),j.y),mix(mix(dot(f5,k5),dot(f6,k6),j.x),mix(dot(f7,k7),dot(f8,k8),j.x),j.y),j.z)*0.95+0.05;
}

float octavePerlinNoise(vec3 p){
    float value=0.0;
    float maxValue=0.0;
    for(float i=0.0;i<5.0;i++){
        value+=pow(0.5,i)*perlinNoise(vec3(p.x*pow(2.0,i),p.y*pow(2.0,i),p.z*pow(2.0,i)));
        maxValue+=pow(0.5,i);
    }
    return value/maxValue;
}

vec3 rayMarching(vec2 p) {
    vec3 cPos = vec3(0.0, 0.0, iTime/5.0);
    vec3 cDir = vec3(0.0, 0.0, 1.0);
    vec3 cUp = vec3(sin(iTime/30.0), cos(iTime/30.0), 0.0);
    float depth = 1.0;    
    vec3 cSide = cross(cDir, cUp);
    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * depth);
    vec3 rPos = cPos;    
    vec3 color = vec3(50.0,0.0,0.0);
    for (float i = 0.0; i < 5.0; i++) {
        color+=vec3(1.0/octavePerlinNoise(rPos));
        color=mix(color,mod(color,0.5),0.5);        
        rPos+=ray*2.0;
    }
  return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 p =
      (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 color = rayMarching(p);
    fragColor =vec4(color,1.0);
}

vec4 iMouseZwFix(vec4 m,bool NewCoke
 ){if(m.z>0.){ //while mouse down
