float box(vec2 uv, vec2 topLeft, vec2 bottomRight)
{
    return uv.x > topLeft.x && uv.x < bottomRight.x && uv.y < topLeft.y && uv.y > bottomRight.y ? 1. : 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Center our coordinate system    
    vec2 uv = (fragCoord - .5*iResolution.xy) / min(iResolution.x, iResolution.y);

    // Move space by adding uniformally to all coordinates

    // Show space coordinates as colors on the background
    vec3 col = vec3(uv.x,uv.y,0.0);

    float radius = .2;
    uv += radius*vec2(sin(iTime), cos(iTime));

    col += box(uv, vec2(-.1,.1), vec2(.1,-.1));

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0,uv);
    vec4 color1 = vec4(0.251,0.921,0.930,1.000);
    vec4 color2 = vec4(0.214,0.708,0.900,1.000);
	vec4 colorp = mix(color2,color1,uv.y *sin(iTime));

	vec4 BG_color = texture(iChannel1,uv);

	//position portal vec2(0.5) is middel of the screen
	vec2 posPortal = vec2(0.5) * iResolution.xy;
	vec2 position = (fragCoord.xy-posPortal.xy)/ iResolution.y;
	//altering y position to make the circle oval form
	position.y *= 0.5;
	//making the inner circle and outer circle
	float win = smoothstep(0.0, 0.03, 0.27-distance(position , vec2(0.0))); 
	float wex = smoothstep(0.0, 0.05, 0.25-distance(position , vec2(0.0))); 
	//combining the background color with the blue color 
	vec4 portalcolor = mix(BG_color, colorp, win);
	//projecting the import image in the inside of the portal
	fragColor= mix(portalcolor,col, wex);
}

#define SIZE 10.

float rand(vec2 co) { 
    return fract(sin(dot(co.xy , vec2(12.9898, 78.233))) * 43758.5453);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{     
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;

    uv = uv * SIZE;
    uv.x += floor(mod(uv.y, 2.0)) * iTime * 2.0 - iTime;
    vec2 id = floor(uv);
    vec2 gv = fract(uv) - 0.5;

    float sF = 1.5 * (SIZE / iResolution.y);         
    float size = 0.4;
    float colMask = smoothstep(size, size - sF, length(gv));

    vec3 circleColor = vec3(
        rand(id),
        rand(id + 1.0),
        rand(id + 2.0)
    );

    vec3 col = mix(vec3(1.0), circleColor, colMask);

    fragColor = vec4(col, 1.0);
}

