  vec3 camDir = normalize(pos - camPos);
  float plDist = max(planeIntersect(camPos, camDir), 0.0);

  vec3 planeColor = getPlaneColor(camPos, camDir, plDist);
  vec2 sphDist = max(sphIntersect(camPos, camDir, sph ), 0.0);
  vec3 spherePos = camPos + sphDist.x*camDir;
  vec3 sphereNormal = normalize( spherePos - sph.xyz ); 
  vec3 backgroundColor = vec3( 0.7, 0.7, 0.7);
  vec4 color = vec4( backgroundColor, 1.0);
  float ligth = dot( sphereNormal, normalize( sph.xzy ));
  vec3 sphColor = vec3( 0.0 );

  if(plDist < currentDepth)
  {
    color.xyz = planeColor.xyz;
    currentDepth = plDist;
  }
  if( sphDist.x > 0.0)
  {
    sphColor = backgroundColor;
    float colorLength = 0.0;
    for(int i = 0; i < sizeOffsets; i++ )
    {
      float traceDepth = 1e20;
      vec3 newDir = refract(camDir, sphereNormal, rgbOffsets[i]);

      vec2 refrCol = abs(sphIntersect(spherePos, newDir, sph ));

      vec3 refrSpherePos = spherePos + refrCol.y*(newDir);

      vec3 refrSphereNormal = normalize(refrSpherePos - sph.xyz);

      vec3 dir = refract(newDir, refrSphereNormal, rgbOffsets[i]); 

      float refrPlDist = max(planeIntersect(refrSpherePos, dir), 0.0);

      vec3 planeColor = getPlaneColor(refrSpherePos, dir, refrPlDist);
      vec3 rgbColor = vec3( colormap(float(i) /float(sizeOffsets) ) );

      sphColor.xyz += planeColor*rgbColor;
      colorLength += length(1.0);
      if( refrPlDist < traceDepth)
      {
        traceDepth = refrPlDist;
      }

    }
    if(sphDist.x < currentDepth)
    {
      color.xyz = sphColor.xyz/colorLength ;
      currentDepth = sphDist.x;
    }

  }

  fragColor = vec4( color.xyz, 1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv += iTime / 10.0;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 15.0;

    vec2 grid = abs(fract(uv - 0.5) - 0.5) / fwidth(uv);
    float line = min(grid.x,grid.y);
    float edge = 1.0 - min(line,1.0);
    fragColor = vec4(vec3(edge),1.0);
}

