float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

float animate( float t )
{
    return fract(t+iTime*0.05);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float sc = 1.2;

    float px = sc*2.0/iResolution.y;
	vec2  p  = sc*(2.0*fragCoord-iResolution.xy)/iResolution.y;

    // compute distance to shape, brute force way
    vec2 o = circle(animate(0.0));
    vec2 d = vec2(length( p - o ));
    for( int i=0; i<NUMPOINTS; i++ )
    {
        float t = animate(float(i+1)/float(NUMPOINTS));
        vec2  c = circle(t);
        float w = sdSegment( p, o, c );
        d = min( d, vec2(w, length(p-c)-0.02) );
        o = c;
	}

	// colorize
    vec3 col = vec3(0.25)*(1.0-0.1*length(p));
    col = mix( col, vec3(1.0,1.0,1.0), 1.0 - smoothstep(0.0, 2.0*px, d.x ) );
    col = mix( col, vec3(1.0,0.7,0.0), 1.0 - smoothstep(0.0, 2.0*px, d.y ) );

    // dither
    col += (1.0/128.0)*cos(31.1*dot(fragCoord,vec2(57.,13.)));

    fragColor = vec4( col, 1.0 );
}

