#define TAU 6.283185307179586
#define PI 3.141592653589793

float maskIt(in float mask, in float v0, in float v1)
{
	return mask * v0 + (1.0 - mask) * v1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Phase
    float phase = iTime * 0.5;

    // Colors
	vec3 c0 = vec3(1.0, 0.5, 0.0);
	vec3 c1 = vec3(0.8, 0.1, 0.3);

    // Waves
	vec2 st = fragCoord.xy / iResolution.xy;
	float m = sin(phase * TAU) * 0.5 + 1.0;
	m = m * 1.0 + 8.0;
	float x = fract(cos((2.0 * (st.x) + phase) * TAU) * 3.0 + m * st.y);
	float y = fract(sin((1.0 * st.y + 0.0 * phase) * TAU) * 1.0);
	x = step(0.5, x);

    // Checkerboard
	float nTiles = 2.5;
	float x1 = step(0.5, fract(st.x * nTiles));
	float y1 = step(0.5, fract(st.y * nTiles));
	float n = mod(x1 + y1, 2.0);

    // Create composite
	float v = maskIt(n, mod(x + y, 2.0), n);
	vec3 composite = vec3(mix(c1, c0, v));

    // Output
	vec3 color = vec3(composite);
	fragColor = vec4(color, 1.0);
}

#define MAX_DIST 100.
#define SURF_DIST 0.01
#define MAX_STEPS 100



float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float dist(vec3 p){
    float an = sin(iTime*2.);
    float an2 = sin(iTime*3.);
    vec4 sph = vec4(0, 2.+ an, 6, 1);
    float sphDist = length(p - sph.xyz) - sph.w;
    float planeDist = p.y;
    float boxDist = sdTorus(p-vec3(0,1.,6.), vec2(.5+0.5*an2, 0.5));

    return min(min(sphDist, planeDist), boxDist);
}

float rayMarch(vec3 ro, vec3 rd){
	float dO = 0.;

    for(int i=0; i<MAX_STEPS; i++){
    	vec3 p = ro + rd*dO;
        float d = dist(p);
        dO += d;
        if(d <= SURF_DIST || dO > MAX_DIST) break;                   
    }
    return dO;
}

vec3 calcNormal( in vec3 p ) // for function f(p)
{
    const float eps = 0.0001; // or some other value
    const vec2 h = vec2(eps,0);
    return normalize( vec3(dist(p+h.xyy) - dist(p-h.xyy),
                           dist(p+h.yxy) - dist(p-h.yxy),
                           dist(p+h.yyx) - dist(p-h.yyx) ) );
}

float getLight( in vec3 p){
    vec3 lightPos = vec3(1.+5.*cos(iTime), 7, 5.+5.*sin(iTime));    
    vec3 lightDir = normalize(lightPos - p);
    vec3 normal = calcNormal(p);
    float dif = dot(normal, lightDir);

    float d = rayMarch(p+ normal*SURF_DIST*2., lightDir);
    if(d<length(lightPos-p)) dif *= .1;

    return dif*0.9+0.05;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv= (fragCoord - 0.5*iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.);

    vec3 ro = vec3(0., 2., 0.);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.));

    vec3 d = vec3(rayMarch(ro, rd)); 
    vec3 p = ro + rd* d;

    float light = getLight(p);    
    col = vec3(light);
    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float d = length(uv);

    float fft1 = texture(iChannel0, vec2(.1, 0.)).x * .8;
    float fft2 = texture(iChannel0, vec2(.4, 0.)).x;

    vec3 col = vec3(smoothstep(.01, .9, d)) + vec3(.1, .3, .8);
    col += d * 1.2;

    col += smoothstep(.5 + cos(iTime + fract(fft2)) * .02 + .04,
                      .25,
                      d - (fft1 * abs(uv.x))) * 1.2 - vec3(.1, d, fft1) - 
        smoothstep(.3  * sin(iTime * .5) * .002 + .003, 
                   .25,
                   d - (fft1 * abs(uv.x * 1.5))) * .8 - vec3(fft2, d, .1);

    col.rb += fft1 * .2 /  d;
    col.gr += fft2 * cos(iTime * .2) * uv;
    col -= abs(uv.y) * 1.5;
    col -= abs(uv.x) * 1.5;

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;  

    float factor = 50.0;

    // Rotate
    float a = iTime * 2.0;
    float as = sin(a);
    float ac = cos(a);
    uv *= uv * mat2(ac, - as, as, ac);

    float v = fract(sin(uv.x * uv.y * factor) * 10.0);       

    float mask = smoothstep( .3, 0., abs(v-.5) );

    vec3 col = vec3(uv.y * 1000.0, 0.7, uv.x * 1000.0) * mask;

    fragColor = vec4(col, 1.0);
}

#define ARM_COUNT 5.
#define WHIRL 14.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv = vec2((atan(uv.y,uv.x)) - iTime*.1, sqrt(uv.x*uv.x + uv.y*uv.y));
    float g = pow(1.-uv.y, 10.)*10.;
    vec3 col = vec3(sin((uv.x + pow(uv.y, 0.2)*WHIRL) * ARM_COUNT)) + g - uv.y*2.2;
    fragColor = vec4(col/5.,1.0);
}

