// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

float dot2( in vec2 v ) { return dot(v,v); }

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float sdCappedCone( in vec3 p, in float h, in float r1, in float r2 )
{
    vec2 q = vec2( length(p.xz), p.y );

    vec2 k1 = vec2(r2,h);
    vec2 k2 = vec2(r2-r1,2.0*h);
    vec2 ca = vec2(q.x-min(q.x,(q.y < 0.0)?r1:r2), abs(q.y)-h);
    vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float dBox2d(vec2 p, vec2 b) {
	return max(abs(p.x) - b.x, abs(p.y) - b.y);
}

float sdTriangle( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

vec4 combine(vec4 val1, vec4 val2 ){
    if ( val1.w < val2.w ) {
        return val1;
    }
    return val2;
}

vec3 faceTex(vec2 uv) {
    float eyeL = length(uv+vec2(0.07,-0.35))-0.005;
    float eyeL2 = length(uv+vec2(0.08,-0.36))-0.005;
    float eyeR = length(uv+vec2(-0.07,-0.35))-0.005;
    float eyeR2 = length(uv+vec2(-0.08,-0.36))-0.005;
    vec3 col = vec3(0.0,0.7,0.0);
    float f = dBox2d(uv+vec2(0.0,-0.3),vec2(0.15,0.15));
    float mouth = dBox2d(uv+vec2(0.0,-0.2),vec2(0.03,0.03+sin(iTime*10.0)*0.01));

    float nose = sdTriangle(vec2(0.0,0.02),vec2(0.02,-0.02),vec2(-0.02,-0.02),uv+vec2(0.0,-0.3));

    col = mix( col, vec3(0.9,0.8,0.1), 1.0-smoothstep(0.01,0.02,f) );
    col = mix( col, vec3(0.6,0.0,0.0), 1.0-smoothstep(0.01,0.02,eyeL) );
    col = mix( col, vec3(0.6,0.0,0.0), 1.0-smoothstep(0.01,0.02,eyeR) );
    col = mix( col, vec3(0.6,0.0,0.0), 1.0-smoothstep(0.01,0.02,eyeL2) );
    col = mix( col, vec3(0.6,0.0,0.0), 1.0-smoothstep(0.01,0.02,eyeR2) );
    col = mix( col, vec3(0.7,0.0,0.0), 1.0-smoothstep(0.01,0.02,mouth) );
    col = mix( col, vec3(0.8,0.6,0.0), 1.0-smoothstep(0.01,0.02,nose) );

    col = (uv.x<-0.35)? vec3(0.95):col;
    col = (uv.x>0.35)? vec3(0.95):col;

    col = (uv.x< -0.25 && uv.x>-0.3)? vec3(0.0,0.9,0.0):col;
    col = (uv.x> 0.25 && uv.x<0.3)? vec3(0.0,0.9,0.0):col;

    return col;
}

vec3 ranbowTex(vec2 uv) {
    vec3 col = vec3(1.0);
    uv.y = cos(uv.x)*0.5+sin(uv.y)*0.5;
    col = (uv.y<-0.35)? vec3(1.0,0.0,0.0):col;
    col = (uv.y>=-0.35 && uv.y< -0.15)? vec3(1.0,0.6,0.0):col;
    col = (uv.y>=-0.15 && uv.y< 0.05)? vec3(1.0,1.0,0.0):col;
    col = (uv.y>=0.05 && uv.y< 0.25)? vec3(0.0,1.0,0.0):col;
    col = (uv.y>=0.25 && uv.y< 0.55)? vec3(0.3,0.3,0.8):col;
    col = (uv.y>=0.55 && uv.y< 0.85)? vec3(0.7,0.7,1.0):col;
    col = (uv.y>=0.85)? vec3(0.8,0.0,1.0):col;

    return col;   
}

