vec4 Chara(vec3 p) {
    vec4 head0 = vec4(1.0,0.0,0.0,length(p+vec3(0.0,-0.72,0.0))-0.05);
    vec4 head1 = vec4(1.0,1.0,0.0,sdCappedCone((p+vec3(0.0,-0.5,0.0)),0.2,0.1,0.005));
    vec4 head2 = vec4(faceTex(p.xy),sdRoundedCylinder((p+vec3(0.0,-0.3,0.0))*matRotateZ(radians(90.0)),0.11,0.32,0.22));
    vec4 body = vec4(bodyTex(p.xy),sdCappedCone((p+vec3(0.0,0.17,0.0)),0.25,0.3,0.22));
    vec4 armL = vec4(0.0,0.7,0.0,sdCapsule((p+vec3(0.33,0.2,0.0)),vec3(-0.2,-0.2,sin(iTime*10.0)*0.1),vec3(0.1,0.25,0.0),0.02));
    vec4 armR = vec4(0.0,0.7,0.0,sdCapsule((p+vec3(-0.33,0.2,0.0)),vec3(0.2,-0.2,sin(iTime*10.0)*-0.1),vec3(-0.1,0.25,0.0),0.02));
    vec4 legL0 = vec4(0.5,0.1,0.3,sdCapsule((p+vec3(0.15,0.7,0.0)),vec3(0.0,0.0,sin(iTime*10.0)*-0.05),vec3(0.0,0.25,0.0),0.04));
    vec4 legL1 = vec4(0.5,0.1,0.3,sdCapsule((p+vec3(0.15,0.95,0.0)),vec3(0.0,0.0,sin(iTime*10.0)*-0.15),vec3(0.0,0.25,sin(iTime*10.0)*-0.05),0.04));
    vec4 legL = combine(legL0,legL1);
    vec4 legR0 = vec4(0.5,0.1,0.3,sdCapsule((p+vec3(-0.15,0.7,0.0)),vec3(0.0,0.0,sin(iTime*10.0)*0.05),vec3(0.0,0.25,0.0),0.04));
    vec4 legR1 = vec4(0.5,0.1,0.3,sdCapsule((p+vec3(-0.15,0.95,0.0)),vec3(0.0,0.0,sin(iTime*10.0)*0.15),vec3(0.0,0.25,sin(iTime*10.0)*0.05),0.04));
    vec4 legR = combine(legR0,legR1);
    return combine(combine(combine(combine(combine(combine(head0,combine(head1,head2)),body),armL),armR),legL),legR);
}

vec4 map(vec3 p){    
    vec3 pref = p;
    vec2 uv = p.xy;
    vec4 f = vec4(floorTex(uv),p.y+1.0);
    vec4 c = Chara(p);
    p.z += iTime*3.0;
    p.z = mod(p.z,6.0)-3.0;
    p.x = abs(p.x);
    vec4 sphere = vec4(ranbowTex(uv),length((p+vec3(-5.0,-0.1,2.0)))-1.3);

    return combine(combine(f,c),sphere);
}

vec3 normalMap(vec3 p){
	float d = 0.0001;
	return normalize(vec3(
		map(p + vec3(  d, 0.0, 0.0)).w - map(p + vec3( -d, 0.0, 0.0)).w,
		map(p + vec3(0.0,   d, 0.0)).w - map(p + vec3(0.0,  -d, 0.0)).w,
		map(p + vec3(0.0, 0.0,   d)).w - map(p + vec3(0.0, 0.0,  -d)).w
	));
}

float shadowMap(vec3 ro, vec3 rd){
	float h = 0.0;
	float c = 0.001;
	float r = 1.0;
	float shadow = 0.5;
	for(float t = 0.0; t < 30.0; t++){
		h = map(ro + rd * c).w;
		if(h < 0.001){
			return shadow;
		}
		r = min(r, h * 16.0 / c);
		c += h;
	}
	return 1.0 - shadow + r * shadow;
}

// from simon green and others
float ambientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - map(p + n*d).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

	vec3 camPos = vec3(0.0, 0.0, 1.3);
	vec3 Fwd = vec3(0.0, 0.0, -1.0);
	vec3 Up  = vec3(0.0,  1., 0.0);

	vec3 left = cross(Fwd, Up);
	float depth = 0.7;
	vec3 ray = normalize(left * p.x + Up * p.y + Fwd * depth);

	mat3 camRot = matRotateY(radians(sin(iTime*2.0)*10.0));

	float t, dist;
	t = 0.0;
	vec3 distPos = camPos;
	vec4 distCl = vec4(0.0);
	for(int i = 0; i < 64; i++){
		distCl = map(distPos);
		dist = distCl.w;
		t += dist;
		distPos = (camPos + t * ray)*camRot;
        if(dist < 0.01 || dist > 30.0) break;
	}

	vec3 color;
	float shadow = 1.0;
	if(dist < 0.1){
		// lighting
		vec3 lightDir = vec3(0.0, 1.0, 0.0);
		vec3 light = normalize(lightDir + vec3(0.5, 0.0, 0.9));
		vec3 normal = normalMap(distPos);

		// difuse color
		float diffuse = clamp(dot(light, normal), 0.1, 1.0);
		float lambert = max(.0, dot( normal, light));

        // ambient occlusion
        float ao = ambientOcclusion(distPos,normal);

		// shadow
		shadow = shadowMap(distPos + normal * 0.001, light);

		// result
		color += vec3(lambert);
		color = ao*diffuse*(distCl.xyz+(.1-length(p.xy)/3.))*vec3(1.0, 1.0, 1.0);

	}else{
		color =.84*max(mix(vec3(0.0,0.81,0.85)+(.1-length(p.xy)/3.),vec3(1),.1),0.);
	}

	// rendering result
	float brightness = 1.5;
	vec3 dst = (color * max(0.5, shadow))*brightness;
	fragColor = vec4(dst, 1.0);
}

