const bool REFLECTIONS = true;

mat2 r2d (float deg) {
	float rad = radians (deg);
	float c = cos (rad);
	float s = sin (rad);
	return mat2 (c, s, -s, c);
}

float smin (float d1, float d2, float k)
{
	float h = clamp (.5 + .5*(d2 - d1)/k, .0, 1.);
	return mix (d2, d1, h) - k*h*(1. - h);
}

float sdSphere (vec3 p, float r) {
	return length (p) - r;
}

float ballMerge (float d, vec3 p, float r, float k) {
	float ball = sdSphere (p, r);
	return smin (d, ball, k);
}

float opRepeat (inout float p, float size) {
	float c = floor ((p + .5*size)/size);
	p = mod (p + .5*size, size) - .5*size;
	return c;
}

float map (vec3 p) {
	float ground = p.y + 1.5;
	float wall = p.z + 1.5;
	float size = 4.;
	float cell = opRepeat (p.x, 4.);
	p.xz *= r2d (45.*iTime + 80.*cell);
	p.xy *= r2d (57.*iTime + 50.*cell);
	float t = iTime;
	float r1 = .5 + .1*(.5+.5*cos (2.*t));
	float r2 = .3 + .1*(.5+.5*cos (3.*t));
	float r3 = .4 + .1*(.5+.5*cos (4.*t));
	float r4 = .2 + .1*(.5+.5*cos (2.*t));
	float r5 = .6 + .1*(.5+.5*cos (3.*t));
	float r6 = .3 + .1*(.5+.5*cos (4.*t));
	float k1 = .35 + .1*(.5+.5*cos (2.*t));
	float k2 = .5 + .1*(.5+.5*cos (3.*t));
	float k3 = .4 + .1*(.5+.5*cos (4.*t));
	float k4 = .6 + .1*(.5+.5*cos (2.*t));
	float k5 = .3 + .1*(.5+.5*cos (3.*t));
	vec3 c1 = vec3 (cos(2.*t), .1, sin(2.*t));
	vec3 c2 = vec3 (cos(3.*t), .2, sin(3.*t));
	vec3 c3 = vec3 (cos(4.*t), sin(2.*t), .3);
	vec3 c4 = vec3 (cos(2.*t), sin(4.*t), .5);
	vec3 c5 = vec3 (.2, cos (3.*t), sin(3.*t));
	vec3 c6 = vec3 (.5, cos (4.*t), sin(2.*t));
	float ball1 = sdSphere (p + c1, r1);
	ball1 = ballMerge (ball1, p + c2, r2, k1);
	ball1 = ballMerge (ball1, p + c3, r3, k2);
	ball1 = ballMerge (ball1, p + c4, r4, k3);
	ball1 = ballMerge (ball1, p + c5, r5, k4);
	ball1 = ballMerge (ball1, p + c6, r6, k5);
	float d = smin (ball1, smin (ground, wall, .7), .9);
	return d;
}

float march (vec3 ro, vec3 rd, inout int iter) {
	float t = .0;
	float d = .0;
	for (int i = 0; i < 48; ++i) {
		vec3 p = ro + d*rd;
		t = map (p);
		if (abs (t) < .001*(1. + .125*t)) break;
		d += t*.95;
		iter = i;
	}
	return d;
}

vec3 norm (vec3 p) {
	float d = map (p);
	vec2 e = vec2 (.001, .0);
	return normalize (vec3 (map (p+e.xyy),
                            map (p+e.yxy),
                            map (p+e.yyx)) - d);
}

float sha (vec3 p, vec3 lp, vec3 n, float ldist, vec3 ldir)
{
	int foo;
	float d2w = march (p+.01*n, ldir, foo);
	return ldist < d2w ? 1. : .1;
}

vec3 shade (vec3 ro, vec3 rd, float d, vec3 n, vec3 lp, vec3 lc, float li) {
	vec3 p = ro + d*rd;
	vec3 amb = vec3 (.05);
	vec3 ldir = normalize (lp - p);
	float ldist = distance (p, lp);
	float att = 7. / (ldist*ldist);
	float diff = max (.0, dot (n, ldir));
	vec3 mat = vec3 (.1, .2, 0);
	float m = smoothstep (.2, .3, .5+.5*cos(4.*(10.+p.z*p.y*p.y)));
	mat = mix (vec3 (1.), vec3 (.0), m);
	float s = sha (p, lp, n, ldist, ldir);
	vec3 h = normalize (-rd + ldir);
	float sp = pow (max (.0, dot (n, h)), 80.);
	return s*att*(amb + diff*lc*li*mat + sp*vec3 (1.));
}

vec3 cam (vec2 uv, vec3 ro, vec3 aim, float zoom) {
	vec3 f = normalize (aim - ro);
	vec3 wu = vec3 (.0, 1., .0);
	vec3 r = normalize (cross (wu, f));
	vec3 u = normalize (cross (f, r));
	vec3 c = ro + f*zoom;
	return normalize (c + r*uv.x + u*uv.y - ro);
}

const	vec3 lps[3] = vec3[3](vec3 (.0, .0, 2.),
                          vec3 (.0, 3., .0),
                          vec3 (-3., 1., .5));
const	vec3 lcs[3] = vec3[3](vec3 (.9, .8, .7),
                          vec3 (.8, .7, .9),
                          vec3 (.8, .9, .7));


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uvRaw = fragCoord/iResolution.xy;
	vec2 uv = uvRaw*2. - 1.;
	uv.x *= iResolution.x/iResolution.y;
	uv *= 1. + .75*length (uv);

    vec3 ro = vec3 (2., 2., 1.);
	vec3 aim = vec3 (.0);
	ro.x -= 7.*iTime;
	aim.x -= 7.*iTime;
	vec3 rd = cam (uv, ro, aim, 1.75);
	int iter = 0;
	float d = march (ro, rd, iter);
	float fog = 1. / (1. + d*d*.1);
	float glow = float (iter) / 48.;
	vec3 p = ro + d*rd;
	vec3 n = norm (p);
	vec3 col = shade (ro, rd, d, n, p+lps[0], lcs[0], 2. );
	col += shade (ro, rd, d, n, p+lps[1], lcs[1], 3.);
	col += shade (ro, rd, d, n, p+lps[2], lcs[2], 4.);
	col += pow (glow, 1.125)*vec3 (1., .3, .1);

    if (REFLECTIONS) {
        ro = p+.01*n;
		rd = normalize (reflect (rd, n));
		d = march (ro,rd,  iter);
		vec3 rcol = shade (ro, rd, d, n, p+lps[0], lcs[0], 2.);
		rcol += shade (ro, rd, d, n, p+lps[1], lcs[1], 3.);
		rcol += shade (ro, rd, d, n, p+lps[2], lcs[2], 4.);
		col += .15*rcol;
    }

	col *= fog;
	col = col / (1.25 + col*.5);	
	col *= 1. - .65*length (uvRaw*2. - 1.);
	col *= mix (1., .75, cos (300.*(uvRaw.y+1.)*(uvRaw.x+uvRaw.y)));
	col = pow (col, vec3 (1./2.2));

	fragColor = vec4(col, 1.);
}

#define PI 3.14159
mat2 rot(float a) {
    float s = sin(a), c = cos(a);

    return mat2(c, s, -s, c);
}

float box(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return max(max(q.x, q.y), q.z);
}

float box(vec2 p, vec2 b) {
    vec2 q = abs(p) - b;
    return max(q.x, q.y);
}

vec2 opU(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

float ac[16]; // store rotations.

vec2 de(vec3 p) {
    vec3 op = p;
    float at = mod(iTime*3.0, PI*9.0);

    float j = 0.0;
    for(int i = 0; i < 16; i++) {
        float s = i < 8 ? 1.0 : -1.0;
        ac[i] = s*PI*smoothstep(j*PI, (j+0.5)*PI, at);
        j += 0.5;
    }

    // I hate this, but I can't find a better way to do it.
    // do the rotations in reverse so the steps() don't interfer with eachother.
    p.xz *= rot(ac[15]*step(0.6, p.y)); // last rotation to happen chronologically
    p.xy *= rot(ac[14]*step(0.6, p.z));
    p.yz *= rot(ac[13]*step(0.6, p.x));
    p.xz *= rot(ac[12]*step(0.6, -p.y));
    p.xy *= rot(ac[11]*step(0.6, -p.z));
    p.yz *= rot(ac[10]*step(0.6, -p.x));
    p.xz *= rot(ac[9]*step(0.6, p.y));
    p.xy *= rot(ac[8]*step(0.6, p.z));

    p.xy *= rot(ac[7]*step(0.6, p.z));
    p.xz *= rot(ac[6]*step(0.6, p.y));
    p.yz *= rot(ac[5]*step(0.6, -p.x));
    p.xy *= rot(ac[4]*step(0.6, -p.z));
    p.xz *= rot(ac[3]*step(0.6, -p.y));
    p.yz *= rot(ac[2]*step(0.6, p.x));
    p.xy *= rot(ac[1]*step(0.6, p.z));
    p.xz *= rot(ac[0]*step(0.6, p.y)); // first rotation to happen chronologically

    // hacky way to get the different colors.
    float m = 0.0;
    if(p.x > 1.73) m = 1.0;
    else if(p.y > 1.73) m = 2.0;
    else if(p.z > 1.73) m = 3.0;
    else if(p.x < -1.73) m = 4.0;
    else if(p.z < -1.73) m = 5.0;
    else if(p.y < -1.73) m = 6.0;

    // these two lines turn one cube into 27.
    p = abs(p) - vec3(0.6);
    p = abs(p) - vec3(0.6);

    float b = box(p, vec3(0.55)); // the actual rubik cube.

    // this distance is for the black border around the cube.
	float c = box(p, vec3(0.56));
	c = max(c, -box(p.xz, vec2(0.47)));
    c = max(c, -box(p.xy, vec2(0.47)));
    c = max(c, -box(p.yz, vec2(0.47)));

    vec2 t = vec2(b, m);
    vec2 s = vec2(length(op) - 1.7, 7.0);
    vec2 u = vec2(c, 7.0);

    return opU(t, opU(s, u));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);
    vec3 bcol = col = vec3(0.3, 0.4, 1.0);

    float a = 6.7;
    float at = iTime*0.6;

    vec3 ro = vec3(a*cos(at), 3, -a*sin(at));
    vec3 ww = normalize(vec3(0, 0, 0)-ro);
    vec3 uu = cross(vec3(0, 1, 0), ww);
    vec3 vv = cross(ww, uu);
    vec3 rd = normalize(mat3(uu, vv, ww)*vec3(uv, 1.0));

    float t = 0.0, m = -1.0, mx = 50.0;
    for(int i = 0; i < 300; i++) {
        vec2 d = de(ro + rd*t);
        if(abs(d.x) < 0.0001 || t >= mx) break;
        t += d.x*0.5;
        m = d.y;
    }

    vec3 ld = normalize(vec3(0.6, 0.5, -0.5));

    if(t < mx) {
        vec3 p = ro + rd*t;
        vec2 h = vec2(0.001, 0.0);
        vec3 n = normalize(vec3(
        	de(p + h.xyy).x - de(p - h.xyy).x,
            de(p + h.yxy).x - de(p - h.yxy).x,
            de(p + h.yyx).x - de(p - h.yyx).x
        ));

        vec3 ld = normalize(p);

        float glo = 16.0;
        vec3 alb = vec3(0.9);

        if(m == 1.0) alb = vec3(1.0, 0.3, 0.3);
        else if(m == 2.0) alb = vec3(0.3, 1.0, 0.3);
        else if(m == 3.0) alb = vec3(0.3, 0.3, 1.0);
        else if(m == 4.0) alb = vec3(3.0);
        else if(m == 5.0) alb = vec3(1.0, 1.0, 0.3);
        else if(m == 6.0) alb = vec3(1.0, 0.3, 1.0);
        else if(m == 7.0) alb = vec3(0);

        float occ = exp2(-pow(max(0.0, 1.0 - de(p + n*0.05).x/0.05), 2.0));
        float dif = max(0.0, dot(ld, n));
        float spe = pow(max(0.0, dot(reflect(-ld, n), -rd)), glo);
        float fre = pow(1.0 + dot(rd, n), 4.0);

        col = 0.5*mix(occ*(alb*(0.25 + dif) + spe), bcol, fre);
    }

    fragColor = vec4(pow(col, vec3(0.4545)), 1);
}

#define PI 3.14159265

float atan2(vec2 p){
    return p.x == 0.0 ? sign(p.y)*PI/2. : atan(p.y, p.x);
}
vec2 polar(vec2 p){
  float r = length(p);
  float t = atan2(p);
  t+=PI;
  return vec2(r,t);
}
vec3 grad(vec2 uv){
  vec3 f = vec3(uv.x*2.0,1.0-uv.y*4.0,1.0-uv.x*0.9)*0.9;
  return f;
}

vec2 rot2(vec2 p,float a){
  vec2 q = p;
  q.x = p.x*cos(a)-p.y*sin(a);
  q.y = p.x*sin(a)+p.y*cos(a);
  return q;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    vec3 col = grad((uv+0.5));
    vec2 puv = polar(uv);

     col.y = 0.03 * pow(
        (cos(puv.y-PI)+1.0),4.0
     );

    float r = 0.3 + 0.1 * sin(iTime);
    if(length(uv)>r + 0.05)col = vec3(1);
    if(length(uv)<r - 0.05)col = vec3(1);

    col = mix(col,vec3(1),0.2);

    fragColor = vec4(col,1.0);
}

// Crazy Springs by eiffie (re-uploaded)

#define time iTime
#define size iResolution

float f1,f2,f3;
vec3 crazy(vec3 g){return sin(g.yzx*f1+g.zxy*f2+g*f3)*0.25;}
vec3 mcol=vec3(0.0);
float DE(vec3 p){//trying to get by with just the bounding 4 points
 vec3 g=floor(p+0.5);
 vec3 g1=g+crazy(g);
 vec4 dlt=vec4(sign(p-g1),0.0);
 vec3 g2=g+dlt.xww;
 vec3 g3=g+dlt.wyw;
 vec3 g4=g+dlt.wwz;
 g1-=p;
 g2+=crazy(g2)-p;
 g3+=crazy(g3)-p;
 g4+=crazy(g4)-p;
 vec3 gD=g2-g1,gDs=gD;
 float t1=clamp(dot(-g1,gD)/dot(gD,gD),0.0,1.0);
 vec3 p1=mix(g1,g2,t1);
 float m1=dot(p1,p1);
 gD=g3-g1;
 float t2=clamp(dot(-g1,gD)/dot(gD,gD),0.0,1.0);
 vec3 p2=mix(g1,g3,t2);
 float m2=dot(p2,p2);
 if(m2<m1){m1=m2;t1=t2;p1=p2;gDs=gD;}
 gD=g4-g1;
 t2=clamp(dot(-g1,gD)/dot(gD,gD),0.0,1.0);
 vec3 p3=mix(g1,g4,t2);
 m2=dot(p3,p3);
 if(m2<m1){m1=m2;t1=t2;p1=p3;gDs=gD;}
 float d1=sqrt(min(dot(g1,g1),min(dot(g2,g2),min(dot(g3,g3),dot(g4,g4)))))-0.15;
 float len=length(gDs);
 float d2=sqrt(m1)-0.07+len*sqrt(d1)*0.03;//0.02
 float d=0.25;
 if(d2<d && d2<d1){
  if(d2<0.015){
   gDs/=len;
   gD=normalize(cross(gDs,vec3(1.0,0.0,0.0)));
   float b=dot(p1,gD),c=dot(p1,cross(gDs,gD));
   vec2 v=vec2(d2,(fract( (t1*16.0+atan(b,c)*0.795775))-0.5)*0.05*len);
   d2=length(v)-0.01;
  }
  d=d2;
 }
 if(mcol.x>0.0){
   if(d1<0.005)mcol+=vec3(0.6,0.2,0.0)+sin(g*2.0)*0.2;
   else mcol+=vec3(0.5,0.4,0.2)+sin(p*100.0)*0.1;
 }
 return min(d,d1);
}
mat3 lookat(vec3 fw,vec3 up){
 fw=normalize(fw);vec3 rt=normalize(cross(fw,up));return mat3(rt,cross(rt,fw),fw);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
 f1=fract(time*0.01)*6.2832;f2=6.2832*(1.0-fract(time*0.013));f3=6.2832*(fract(time*0.017));
