// Basic domain coloring plot (https://en.wikipedia.org/wiki/Domain_coloring)
// of the inverse Gudermannian function w = gd^(-1)(z) (https://en.wikipedia.org/wiki/Gudermannian_function)

#define PI 3.14159265359
#define TWOPI 6.28318530718
#define SCALE 7.5 // plot scale
#define XY_SPACING 1.0 // Cartesian grid line spacing
#define R_SPACING 1.5 // polar grid line spacing

// modified version of Inigo Quilez's method at https://www.shadertoy.com/view/MsS3Wc
// using "rational smoothstep" from https://tpfto.wordpress.com/2019/03/28/on-a-rational-variant-of-smoothstep/
vec3 smooth_hue( float h )
{
    vec3 rgb = clamp( abs(mod(6.0 * h + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// modified DLMF coloring, adapted from https://www.shadertoy.com/view/WtXGWN
vec3 smooth_dlmf( float h )
{
    vec3 rgb = clamp( vec3(1., -1., -1.) * abs((vec3(8., 4., 8.) * mod(h, 1.0) - vec3(4.5, 1.5, 5.5))) + vec3(-1.5, 1.5, 2.5), 0.0, 1.0 );
	return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// Inverse Gudermannian function

vec2 invgd( in vec2 z )
{
	float sx = sin(z.x);
    float cy = cosh(z.y);
    return vec2(0.5 * log((cy + sx)/(cy - sx)), atan(sinh(z.y), cos(z.x)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 z = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
    z *= SCALE;
    z.x += iTime;

    vec2 w = invgd(z);
    float ph = atan(w.y, w.x);
    float lm = log(0.0001 + length(w));

    vec3 cd = smooth_dlmf(0.5 * (ph / PI));
    vec3 ch = smooth_hue(0.5 * (ph / PI));
    // transition between normal hue and DLMF coloring
    vec3 c = mix(cd, ch, 0.5 + 0.5 * cos(2.0 * iTime));

    float sat = abs(sin(TWOPI * lm/R_SPACING));
    float bri = pow(abs(sin(TWOPI * w.x/XY_SPACING) * sin(TWOPI * w.y/XY_SPACING)), 0.25);
    bri = max(1.0 - sat, bri);
    sat = sqrt(sat);

    fragColor = vec4(bri * mix( vec3(1.0), c, sat), 1.0);
}

#define PI 3.141592
#define TAU (PI*2.0)

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float fbm(vec2 p) {
    float r = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for(int i = 0; i < 3; i++) {
        r += amp * noise(freq*p);
        amp *= 0.5;
        freq *= 1.0/0.5;
    }
    return r;
}

mat2 rot( float th ){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }

float remap(float val, float im, float ix, float om, float ox)
{
    return clamp(om + (val - im) * (ox - om) / (ix - im), om, ox);
}

float cio(float t) {
	return t < 0.5
	? 0.5 * (1.0 - sqrt(1.0 - 4.0 * t * t))
	: 0.5 * (sqrt((3.0 - 2.0 * t) * (2.0 * t - 1.0)) + 1.0);
}

float animHeight(vec2 p)
{
    float s = 0., hs = 1.;
    float t = mod(iTime, 7.);
    float tt = remap(t, 1., 2., 0., 1.);
    s = mix(0., .3, cio(tt));

    tt = remap(t, 3., 4., 0., 1.);
    p *= rot(cio(tt) * 3.);

    tt = remap(t, 4., 5., 0., 1.);
    s = mix(s, 1.0, cio(tt));

    tt = remap(t, 5., 6., 0., 1.);
    p *= rot(-cio(tt) * 3.);

    tt = remap(t, 6.5, 7., 0., 1.);
    p *= rot(cio(tt) * 1.);
    hs = mix(1., 0., cio(tt));

    float pls = (sin(t * TAU - PI*.5) * .5 + .5) * step(mod(t, 2.), 1.) * .2;
    return (fbm(p * s + t * .5) + pls) * hs;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0));
}

vec2 rep( in vec2 p, in vec2 c)
{
    return mod(p,c)-0.5*c;
}

float map(vec3 p)
{
    float bd = length(p.xz) - 5.0;
    if (bd > 0.1) {
    	return bd;
    }
    vec2 id = floor(p.xz / 0.2);
    float height = animHeight(id * 0.2) * 0.5;
    p.xz = rep(p.xz, vec2(0.2));
    p.y -= height;
    float box = sdBox(p, vec3(0.03, height, 0.03));
    return max(box, bd) * .5;
}

vec2 trace(vec3 p, vec3 ray, float mx)
{
    float t = 0.0;
    vec3 pos;
    float dist;
    for (int i = 0; i < 128; i++) {
        pos = p + ray * t;
        dist = map(pos);
        if (dist < 0.002 || t > mx) {
        	break;
        }
        t += dist;
    }
    return vec2(t, dist);
}

vec3 getColor(vec3 p, vec3 ray)
{
    vec2 t = trace(p, ray, 100.0);
    vec3 pos = p + ray * t.x;
    if (t.x > 100.0) {
        return vec3(0.0);
    }
    return max(vec3(0.2, 0.5, 0.8) * 7.0 * pow(pos.y, 4.0) * smoothstep(0.0, -1.0, length(pos.xz) - 5.0), vec3(0.0));
}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

float luminance(vec3 col)
{
    return dot(vec3(0.3, 0.6, 0.1), col);
}

vec3 acesFilm(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x * (a * x + b)) / (x * (c * x + d ) + e), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float t = iTime * 0.1;
    vec3 ro = vec3(cos(t) * 10.0, 5.5, sin(t) * 10.0);
    vec3 ta = vec3(0.0, 1.0, 0.0);
    mat3 c = camera(ro, ta, 0.0);
    vec3 ray = c * normalize(vec3(p, 2.5));
    vec3 col = getColor(ro, ray);

    vec3 lp = vec3(0.0, 6.0, 0.0), rd = ray;

    float s = 7.5, vol = 0.0;
    for(int i = 0; i < 60; i++) {
        vec3 pos = ro + rd*s;

        vec3 v = -normalize(lp - pos);

    	float tt = -(lp.y-2.) / v.y;

        vec3 ppos = lp + v * tt;
	    vol += pow(animHeight(ppos.xz), 3.0) * 0.05 * smoothstep(0.0, 1.5, pos.y) * smoothstep(-1.0, -4.0, length(ppos.xz) - 5.0);

        s += 0.1;
    }

    col += 1.6*vec3(0.3*vol, 0.5*vol, vol);

    col = acesFilm(col * 0.5);
    col = pow(col, vec3(1.0/2.2));

    fragColor = vec4(col,1.0);
}

/*
Fractal Penrose triangle aka Selmi triangle. I came across it when John Baez
shared it a couple of months ago, and I wanted to explore the concept with a
shader. Representations of it were first posted online by Nidhal Selmi and 
Akiyoshi Kitaoka in 2009-2010.

Regarding implementation, I didn't find any super elegant or efficient way of
generating it, and ended up manually placing overlapping isometric faces.
Everything is 2D. I'm basically treating the Sierpinski triangle as a tiling,
with the Penrose triangle being the prototile. Somewhat experimentally,
everything is generated in HSV color space and converted to RGB at the end,
which makes for easy color management.

Links:
http://math.ucr.edu/home/baez/diary/march_2019.html
https://www.deviantart.com/nydhalo/art/Selmi-Triangle-136342456
http://www.psy.ritsumei.ac.jp/~akitaoka/fukano2e.html
*/


// Sets of coordinates used for the current and adjacent tiles
struct TileSpace {
	vec2 p; // current point in tile coordinates
	vec2 to; // tile origin in global coordinates
};

// Existence of neighboring tiles (left/right/bottom)
struct Neighbors {
	bool L;
	bool BL;
	bool BR;
};

// Number of iterations being displayed
int level;
// Smoothed number of iterations and related values
float smoothLevel, tilesize, lineLightness;
// Colors in HSV space
const vec3 lightHSV = vec3(0., 0., 0.95);
const vec3 mediumHSV = vec3(0., 0., 0.4);
const vec3 darkHSV = vec3(0., 0., 0.14);
const vec3 lineHSV = vec3(0., 0., 0.);
vec3 bgHSV;

// These functions angle space for the placement of isometric cube faces
vec2 isoM(vec2 p) { return vec2(p.y, 0.5*p.y - p.x); }
vec2 isoL(vec2 p) { return vec2(p.x+p.y*0.5, p.y); }
vec2 isoR(vec2 p) { return vec2(p.x+p.y*0.5, 0.5*p.y - p.x); }

// These functions repeat space to draw cheap rows of faces
float range(float x, float xmin, float xmax) {
	return max(min(fract(x), x-xmin), x-xmax);
}
vec2 xRange(vec2 p, float xmin, float xmax) {
	p.x = range(p.x, xmin, xmax);
	return p;
}
vec2 yRange(vec2 p, float ymin, float ymax) {
	p.y = range(p.y, ymin, ymax);
	return p;
}

// Draw a unit square onto ret
void square(inout vec3 ret, vec2 p, vec3 col, float aaSize) {
	p = abs(p-0.5);
	float sqgrad = max(p.x, p.y)*2.;
	vec3 lc = mix(lineHSV, col, lineLightness);
	ret = mix (ret, lc, 1.-smoothstep(1., 1.+aaSize, sqgrad));
	ret = mix(col, ret, smoothstep(1.-aaSize, 1., sqgrad));
}

/*
Construct a Penrose triangle in 2D by manually placing a bunch of angled
squares. Nothing really magic.
*/
void drawPenrose(inout vec3 ret, vec2 p, float aaSize) {
	// L to T
	square(ret, xRange(isoR(p)+vec2(0., 1.), 1., 2.), lightHSV, aaSize);
	// R to L
	square(ret, yRange(isoM(p), -3., 0.), mediumHSV, aaSize);
	square(ret, xRange(isoL(p)+vec2(0., 1.), -1., 2.), darkHSV, aaSize);
	// R to T
	square(ret, yRange(isoL(p)-vec2(3., 0.), -1., 2.), darkHSV, aaSize);
	square(ret, yRange(isoR(p)-vec2(4., 0.), -5., -1.), lightHSV, aaSize);
	// L to T cover
	square(ret, isoR(p)+vec2(-3., 1.), lightHSV, aaSize);
	square(ret, xRange(isoM(p), 1., 4.), mediumHSV, aaSize);
}

/*
Construct a Penrose triangle and connect it to its neighbors by adding some more
angled squares.
*/
void drawSelmi(inout vec3 ret, TileSpace t, Neighbors n, float aaSize) {
	vec2 p = t.p*4.-vec2(2., 0.);
	p *= vec2(0.5, 0.578);
	p += vec2(3., 1.3);
	if (n.BL) {
		square(ret, yRange(isoR(p)+vec2(0., 0.), -3., -2.), lightHSV, aaSize);
	}
	if (n.BR) {
		square(ret, isoM(p)+vec2(2., 4.), mediumHSV, aaSize);
	}
	drawPenrose(ret, p, aaSize);
	if (n.L) {
		square(ret, isoL(p)+vec2(1., 0.), darkHSV, aaSize);
		square(ret, isoL(p)+vec2(2., 1.), darkHSV, aaSize);
		square(ret, isoR(p), lightHSV, aaSize);
	}
	if (n.BL) {
		square(ret, isoR(p)+vec2(1., 1.), lightHSV, aaSize);
		square(ret, isoM(p)+vec2(1., 0.), mediumHSV, aaSize);
	}
	if (n.BR) {
		square(ret, isoR(p)+vec2(-3., 5.), lightHSV, aaSize);
		square(ret, isoR(p)-vec2(4., -6.), lightHSV, aaSize);
		square(ret, isoM(p)+vec2(1., 4.), mediumHSV, aaSize);
	}
}

/*
Convert coordinates to "Sierpinski tile" coordinates. I originally rolled my own
Sierpinski function, but then I saw iq's smugly superior version so I
rage-deleted all my code and based my new version on his:
https://www.shadertoy.com/view/Md2GzR
which itself was probably inspired by Syntopia's Sierpinski code:
http://blog.hvidtfeldts.net/index.php/2011/08/distance-estimated-3d-fractals-iii-folding-space/
*/
const vec2 va = vec2(0.0, 1.73-0.85);
const vec2 vb = vec2(1.0, 0.00-0.85);
const vec2 vc = vec2(-1.0, 0.00-0.85);
float length2(vec2 p) { return dot(p,p); }
TileSpace sierpSpace(vec2 pin) {
	float a = 0.0;
	vec2 p = pin;
	vec2 c;
	float dist, d, t;
	for (int i = 0; i<7; i++) {
		if (i>=level) continue;
		d = length2(p-va);                 c = va; dist=d; t=0.0;
        d = length2(p-vb); if (d < dist) { c = vb; dist=d; t=1.0; }
        d = length2(p-vc); if (d < dist) { c = vc; dist=d; t=2.0; }
		p = c + 2.0*(p - c);
		a = t + a*3.0;
	}
	vec2 to = (pin - p*tilesize);
	return TileSpace(p, to);
}

/*
I'm resorting to an utterly barbaric way of determining if the tile has
neighbors to connect to: re-running down the entire fractal for each neighboring
side to check. There's got to be a way of getting all of this in a single pass,
but I couldn't figure it out. Oh well, at least I reduced it to 3 taps instead
of having to check all 6 possible neighbors, by offsetting the tile contents so
that it's only affected by the bottom-left connections.
*/
#define EPSILON 0.001
bool approx(vec2 a, vec2 b) { return all(lessThan(abs(a-b), vec2(EPSILON))); }
Neighbors getNeighbors(vec2 p, TileSpace t) {
	Neighbors n;
	float eps = tilesize*0.002;
    // check if the tile origin is equal to the tile origin of neighboring points
	n.L = !approx(t.to, sierpSpace(t.to-tilesize*vec2(1.1, 0.)).to);
	n.BL = !approx(t.to, sierpSpace(t.to-tilesize*vec2(0.92, 0.92)).to);
	n.BR = !approx(t.to, sierpSpace(t.to-tilesize*vec2(-0.92, 0.92)).to);
	return n;
}

/*
Equilateral triangle distance by iq, used for the big blurry shadow.
(Todo: project it onto the plane because right now it looks wrong.)
Also used for the transition effect between levels of iteration.
*/
float sdEquilateralTriangle(vec2 p)
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x + k*p.y > 0.0 ) p = vec2( p.x - k*p.y, -k*p.x - p.y )/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

// Smooth HSV: https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb_smooth(vec3 c)
{
	return c.z * (1.-c.y*smoothstep(2.,1., abs(mod(c.x*6.+vec3(0,4,2), 6.) -3.)));
}

// Convert to ground-plane coordinates
vec2 groundP(vec2 p)
{
	p.y += 0.184;
	p.x = 2.*(p.x/8.)/(.5-p.y);
	p.y = p.y / (p.y-.5);
	p.y += smoothLevel*0.46;
	p *= 7.;
	return p;
}

/*
Return a drawable grid on the ground-plane.
Todo: get the AA size with the derivative of groundP instead of this shitty
numerical approximation.
*/
float ground(vec2 pin)
{
	pin.x = abs(pin.x);
	vec2 p = groundP(pin);
	vec2 p2 = groundP(pin-vec2(1./iResolution));
	float aax = min(0.5, abs(p.x-p2.x)*1.5);
	float aay = min(0.5, abs(p.y-p2.y)*1.5);
	float ret = smoothstep(0.5-aax, 0.5, abs(fract(p.x)-0.5));
	ret = max(ret, smoothstep(0.5-aay, 0.5, abs(fract(p.y)-0.5)));
	return ret*(0.5-aay)*2.;
}

//  Dave_Hoskins hash, used for film grain effect
float hash13(vec3 p3)
{
	p3 = fract(p3 * .1031);
	p3 += dot(p3, p3.yzx + 19.19);
	return fract((p3.x + p3.y) * p3.z);
}

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 p = uv-0.5;
	p.x *= iResolution.x/iResolution.y;

	// Slowly increase and decrease the fractal's iteration count
	smoothLevel = 0.7+(4.5-0.7)*(0.5-0.5*cos(iTime*0.2));
	float trans = sdEquilateralTriangle(-0.2*(p-vec2(0., 0.33)))+0.66;
	level = int(smoothLevel-trans);
	tilesize = 1./pow(2.,float(level));
	lineLightness = smoothLevel*0.18;
	float zoom = pow(2.,smoothLevel)*1.09*tilesize;
	float aaSize = 22.*zoom*(1.0/length(iResolution))/tilesize;

	// Prepare some colors and background grid on plane
	bgHSV = vec3(float(level)*0.16+0.5, 0.6, 1.);
	vec3 bg2 = vec3(bgHSV.x+0.25, 0., 1.);
	bgHSV = mix(bgHSV, bg2, sqrt(uv.y));
	bgHSV.z *= 1.-ground(p)*0.75;
	vec3 ret = bgHSV;

	// Prepare coordinates for the object
	p = (p-vec2(0., 0.33-0.1/smoothLevel))*zoom+vec2(0.5, 0.94);
	TileSpace t = sierpSpace(p*2.-1.);
	Neighbors n = getNeighbors(p, t);

	// Shadow
	vec2 sp = (p-vec2(0.35, 0.2));
	sp.y *= 2.;
	sp.x += sp.y*0.3;
	float s = sdEquilateralTriangle(sp * 2.);
	float samp = 1.5/(max(sp.y+2.,0.)+1.);
	samp *= 2.2-1.2*smoothstep(-1., 4., float(level));
	ret.z *= 1. - samp/(max(s, 0.)+1.);

	// Draw the object
	drawSelmi(ret, t, n, aaSize);

	// Draw white transition triangle
	vec3 c2 = vec3(bgHSV.x, 0., 0.9);
	ret = mix(c2, ret, clamp(abs(fract(smoothLevel-trans+0.49)-0.5)*400.-4., 0., 1.));

	// Film grain effect
	float hash = hash13(vec3(fragCoord, iTime*1500.+50.));
	ret.z *= 1.-hash*0.13*(1.-ret.y);
	ret.x += hash*0.13*ret.y;

	// Convert to RGB
    fragColor = vec4(hsv2rgb_smooth(ret), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 p = (fragCoord.xy * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);

    float reso = 4.;

    vec2 cell = floor(p * reso);
    vec2 center = (cell + .5) / reso;

    float t = iTime + cell.x + cell.y;
    t *= 2.;

    float l = distance(p, center);
    float r = (.4 + sin(t) * .4) / reso;

    vec3 rgb = vec3((l - r) * reso);

    fragColor = vec4(rgb, 1.);

}

// curve matched using turingbot
vec3 linearToSrgb(const vec3 x){
    return 1.14374*(-0.126893*x+sqrt(x));
}


const float pi = radians(180.);
const float hpi = radians(90.);

float mulSign(float x,float y){
    return uintBitsToFloat(floatBitsToUint(x)^(floatBitsToUint(y)&0x80000000u));
}

//curve fitted with turingbot rms 0.000030
float fast_acos(float x){
    float a = abs(x);
    a=a*(a*(a*(0.151627-0.0354045*a)-0.387686)+1.27146);
    return mulSign(sqrt(1.-a)-1.,x)*hpi+hpi;
}
#define acos(x) fast_acos(x)
#define asin(x) ( pi * .5 - acos(x) )
#define saturate(x) clamp(x,0.,1.)

struct circle {
    float distance;
	vec2 direction;
	float angularRadius;
};

float getLighting(const circle light, const circle occluder){

    bool behindOccluder = occluder.distance < light.distance;

    float offset = acos(saturate(dot(light.direction,occluder.direction)));

    if(behindOccluder)
		return light.angularRadius - max(occluder.angularRadius - offset,0.);
    return light.angularRadius;
}

float dither(const vec2 fragCoord){
	return (texture(iChannel0,fragCoord/8.)-.5).x/255.;
}

circle createCircle(vec2 position,const float radius,const vec2 fragCoord){
	circle c;

    position -= fragCoord;
	float distanceSquared = dot(position,position);
	float inverseDistance = inversesqrt(distanceSquared);

    c.distance = inverseDistance * distanceSquared;
	c.direction = position * inverseDistance;
    c.angularRadius = asin(radius*inverseDistance);

    bool inside = c.distance < radius;

    if(inside) c.angularRadius = pi;

    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    circle l0 = createCircle(
        vec2((200.+cos(iTime)*100.),40.),
        25.,
        fragCoord
    );

    circle l1 = createCircle(
        vec2(30,100.+cos(iTime)*100.),
        10.,
        fragCoord
    );

    circle l2 = createCircle(
        iMouse.xy,
        30.,
        fragCoord
    );

    circle o0 = createCircle(
        vec2(150,160),
        15.,
        fragCoord
    );

    circle o1 = createCircle(
        vec2(350,130),
    	40.,
        fragCoord
    );

    fragColor.rgb = vec3(2,0,0) * min(getLighting(l0,o0),getLighting(l0,o1))+ 
    				vec3(0,2,0) * min(getLighting(l1,o0),getLighting(l1,o1))+
    				vec3(0,0,2) * min(getLighting(l2,o0),getLighting(l2,o1));

    fragColor.rgb /= pi;

    fragColor.rgb = linearToSrgb(fragColor.rgb) + dither(fragCoord);

    //fragColor.rgb=vec3(acos((fragCoord.x/iResolution.x)*2.-1.))/pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.x)*2.0;

    uv.x = abs(mod(uv.x,0.2)-0.1);
    uv.x+=iTime*0.1;    
    float s = 0.05;
    uv.y += step(mod(uv.x,s*2.0),s)*0.5;
    float k = mod(uv.x,s);
    float h = abs(mod(uv.y,s*2.0)-s);
    float g = smoothstep( k*0.95,k, h );
    fragColor = vec4(g);
}

#define time iTime


float random (float x) {
    return fract(sin(x)*1e4);
}

float noise2(vec3 p) {
  vec3 ip=floor(p);
  p=fract(p);
  p=smoothstep(0.0,1.0,p);
  vec3 st=vec3(7,37,289);
  vec4 pos=dot(ip,st) + vec4(0,st.y,st.z,st.y+st.z);
  vec4 val=mix(fract(sin(pos)*7894.552), fract(sin(pos+st.x)*7894.552), p.x);
  vec2 val2=mix(val.xz,val.yw, p.y);
  return mix(val2.x,val2.y, p.z);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise( vec3 p) {
    const vec3 step = vec3(110.0, 241.0, 171.0);

    vec3 i = floor(p);
    vec3 f = fract(p);

    // For performance, compute the base input to a
    // 1D random from the integer part of the
    // argument and the incremental change to the
    // 1D based on the 3D -> 1D wrapping
    float n = dot(i, step);

    vec3 u = f * f * (3.0 - 2.0 * f);
    return mix( mix(mix(random(n + dot(step, vec3(0.0,0.0,0.0))),
                        random(n + dot(step, vec3(1.0,0.0,0.0))),
                        u.x),
                    mix(random(n + dot(step, vec3(0.0,1.0,0.0))),
                        random(n + dot(step, vec3(1.0,1.0,0.0))),
                        u.x),
                u.y),
                mix(mix(random(n + dot(step, vec3(0.0,0.0,1.0))),
                        random(n + dot(step, vec3(1.0,0.0,1.0))),
                        u.x),
                    mix(random(n + dot(step, vec3(0.0,1.0,1.0))),
                        random(n + dot(step, vec3(1.0,1.0,1.0))),
                        u.x),
                u.y),
            u.z);
}

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}

float sphere( vec3 p, in vec3 centerPos, float radius) {
	return length(p-centerPos) - radius;
}

float fire( vec3 p, vec3 centerPos, float scale,float radius ) {
  float l=min(length(p-centerPos)/radius,1.0)*0.7+0.3;
  float nl = (1.0-l);
  float x=(noise((p+sin(time)*2.0*l)*0.2)*2.0-1.0)*15.0*nl;
  float y=(noise((p+sin(time+5.0)*2.0*l)*0.2)*2.0-1.0)*15.0*nl;
  float z=(noise((p+sin(time+3.5)*2.0*l)*0.2)*2.0-1.0)*15.0*nl;
  p+=vec3(x,y,z);
  return max((noise(p*scale)+noise(p*2.0*scale)*0.5+
              noise(p*3.0*scale)*0.33+
              noise(p*4.0*scale)*0.25)*0.4807-(l*l*l*l),0.0);
}

float flow(vec3 p,float scale){
  float l=length(p-vec3(0,-20,0));
  return mix(sin(l-time*10.0)+cos(l-time*10.0),
             0.0,
             min(1.0-(100.0-l)*0.01,1.0));
}
float map(vec3 p){
  return sphere(p,vec3(0.0,0.0,0.0),20.0);
  }

float map2(vec3 p){
  return sphere(p,vec3(0.0,0.0,0.0),10.0);
  }

float mapHyper(vec3 p){
  return fire(p,vec3(0.0,0.0,0.0),0.3,20.0);
  }  


float dot2(  vec3 v ) { return dot(v,v); }

float udQuad( vec3 p, vec3 a, vec3 b, vec3 c, vec3 d )
{

    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 dc = d - c; vec3 pc = p - c;
    vec3 ad = a - d; vec3 pd = p - d;
    vec3 nor = cross( ba, ad );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(dc,nor),pc)) +
     sign(dot(cross(ad,nor),pd))<3.0)
     ?
     min( min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(dc*clamp(dot(dc,pc)/dot2(dc),0.0,1.0)-pc) ),
     dot2(ad*clamp(dot(ad,pd)/dot2(ad),0.0,1.0)-pd) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

float map3(vec3 p){
return udQuad(p,
              vec3(10000, 20.0, 10000.0),
              vec3(-10000.0, 20.1, 10000.0), 
              vec3(-10000.0, 20.0, -10000.0),
              vec3(10000.0, 20.0, -10000.0));
  }

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);
  vec3 s=vec3(0,0,-100);
  float t2=(time*0.5+10.0);
  s.y = -abs(cos(t2*0.1)*100.0);
  s.xz *= rot(t2*0.1);

  vec3 t=vec3(0,0,0);
  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,1,0)));
  vec3 cy=normalize(cross(cz,cx));
  vec3 r=normalize(uv.x*cx+uv.y*cy+cz*0.7);

// real fireball/////////////////////////
  vec3 p=s;
  float dd=0.0;
  for(int i=0; i<60; ++i) {
    float d=map(p);
    if(d<0.0001) break;
    if(dd>500.0) {dd=500.0; break;}
    p+=d*r*0.8;
    dd+=d;
  }

  vec2 c= vec2(0,0);
  for(int i=0; i<400; ++i) {
    float d=map(p);
    float d2=map2(p);
    c+=vec2(mapHyper(p)*0.018,0);
    c.y=1.0;
    if(dd>=500.0) {dd=500.0;c.y=0.0; break;}
    if( d2<0.001 ){c.x+=0.5;c.y=2.0;break;}
    if( d>0.001 ){ break;}
    p+=r*(0.1);
    dd+=0.1;
  }

  //////////////////////////////////////////////////////////////////////////////////////////////

  //water
  p=s;
  dd=0.0;
  int hit = 1;
  for(int i=0; i<60; ++i) {
    float d=map3(p);
    if(d<0.0001) break;
    if(dd>500.0) {dd=500.0;hit =0; break;}
    p+=d*r*0.8;
    dd+=d;
  }
  // reflection on the water
  vec2 off=vec2(0.01,0);
  p.y=p.y+flow( p,0.1)*2.0;
  vec3 n=normalize(map3(p)-vec3(map3(p-off.xyy), map3(p-off.yxy), map3(p-off.yyx)));
  if(hit==1){r=reflect(p-s,n);s=p-r;r=normalize(r);}

  p=s;
  dd=0.0;
  for(int i=0; i<60; ++i) {
    float d=map(p);
    if(d<0.0001) break;
    if(dd>500.0) {dd=500.0; break;}
    p+=d*r*0.8;
    dd+=d;
  }

  //sky
  float dotr=(dot(r,normalize(vec3(0,-1,1))));

  vec3 sky = mix(vec3(0.2,0.1,0.1),
                 vec3(0.1,0.1,0.2),
                 min(dotr+0.2,1.0));

  vec3 star=vec3(dot(r,normalize(vec3(1.0,0.0,0.0))),
                 dot(r,normalize(vec3(0.0,1.0,0.0))),
                 dot(r,normalize(vec3(0.0,0.0,1.0))));

  float starPoint=noise(star*200.0)*30.0;

  vec3 b = mix(vec3(1.0,1.0,0.5),
               sky,
               min(starPoint+0.2,1.0));

  // fire ball reflection
  vec2 c2= vec2(0,0);
  if(hit==1){
    for(int i=0; i<400; ++i) {  
      float d2=map2(p);
      c2+=vec2(mapHyper(p)*0.018,0);
      c2.y=1.0;
      if(dd>=500.0) {dd=500.0;c2.y=0.0; break;}
      if( d2<0.001 ){c2.x+=0.5;c2.y=2.0;break;}
      p+=r*(0.1);
      dd+=0.1;
      }
  }

  //compositing
  vec3 col=vec3(0.0);
  if(c.y==1.0)col = mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,0.0),c.x);
  if(c.y==2.0)col = mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,0.0),c.x);

  vec3 col2=vec3(0.0);
  if(c2.y==1.0) col2 = mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,0.0),c2.x);
  if(c2.y==2.0) col2 = mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,0.0),c2.x);
  if(c2.y==0.0) col2 = b;
  else{
    if(c2.y!=2.0)col2 = mix(b,col2,c2.x);
  }
  if(c.y==0.0)col = col2;
  else {
    if(c.y!=2.0)col = mix(col2,col,c.x);
  }

  fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

  	float l = length(uv);
