bool isInside = false;

mat2 r2d (float deg) {
	float rad = radians (deg);
	float c = cos (rad);
	float s = sin (rad);
	return mat2 (c,s,-s,c);
}

float smin (float d1, float d2, float k) {
	float h = clamp (.5 + .5*(d2 - d1)/k, .0, 1.);
	return mix (d2, d1, h) - h*k*(1. - h);
}

float sdBox (in vec3 p, in vec3 size, in float r)
{
	vec3 d = abs(p) - size;
	return min (max (d.x, max (d.y,d.z)), .0) + length (max (d, .0)) - r;
}

float map (vec3 p, inout int id, inout vec3 pout) {
	float ground = p.y + 1.;
	float wall = p.z + 1.;
	vec3 pbox = p + vec3 (.0, -.4, -.2);
	pbox.xz *= r2d (14.*iTime);
	pbox.yz *= r2d (26.*iTime);
	float r = .75 + .1*(.5+.5*cos(4.*iTime + 9.*pbox.y));
	float box = sdBox (pbox, vec3 (.6), .05);
	p -= vec3 (2.5*cos (1.25*iTime), .75, .2);
	p.xz *= r2d (-60.*iTime);
	p.yz *= r2d (-90.*iTime);
	float ball = sdBox (p , vec3 (.4), .05);
	box = (isInside? -1. : 1.)*smin (box, ball, .5);
	float d = min (ground, min (wall, box));
	if (d == ground) {id = 1; pout = p;}
	if (d == wall) {id = 2; pout = p;}
	if (d == box) {id = 3; pout = p;}
    return d;
}

float march (vec3 ro, vec3 rd, inout int id, inout vec3 pout)
{
	float t = .0;
	float d = .0;
	for (int i = 0; i< 48; ++i) {
		vec3 p = ro+d*rd;
		t = map (p, id, pout);
		if (abs (t) < .00001*(1. + .125*t)) break;
		d += t*.75;
	}
	return d;
}

vec3 norm (vec3 p){
	int foo;
	vec3 bar;
	float d = map (p, foo, bar);
	vec2 e = vec2 (.001, .0);
	return normalize (vec3 (map (p+e.xyy, foo, bar),
                            map (p+e.yxy, foo, bar),
                            map (p+e.yyx, foo, bar))-d);
}

float sha (vec3 p, vec3 lp, vec3 n, vec3 ldir) {
	float d2l = distance (lp, p);
	int foo;
	vec3 bar;
	float d2w = march (p+.01*n, ldir, foo, bar);
	return d2l < d2w ? 1. : .1;
}

float ao (vec3 p, vec3 n, float stepsize, int iter, float i){
	float ao = .0;
	float dist = .0;
	int foo;
	vec3 bar;
	for (int a = 1; a <= iter; ++a) {
		dist = float (a)*stepsize;
		ao += max (.0, (dist - map (p+n*dist, foo, bar))/dist);
	}
	return 1. - ao*i;
}

vec3 shade (vec3 ro,
			vec3 rd,
			float d,
			vec3 n,
			vec3 lp,
			vec3 lc,
			float li,
			int id,
			vec3 pout) {
    vec3 p = ro + d*rd;
	float ld = distance (p, lp); 
	vec3 ldir = normalize (lp - p);
	float att = 5. / (ld*ld);
	vec3 mat = vec3 (.2);
	if (id == 1) {
		mat = mix (vec3 (.0, .0, .0),
				   vec3 (.5, .5, .5),
                   smoothstep(.4, .6, cos (5.*p.x) * sin(5.*p.z+5.*iTime)));
	}
	if (id == 2) {
		mat = mix (vec3 (.0, .0, .0),
				   vec3 (.5, .5, .5),
				   smoothstep (.0, .9, sin (25.*p.y + 12.*iTime)));
	}
	if (id == 3) {
		mat = vec3 (.9, .5, .2);
	}
	float s = sha (p, lp, n, ldir);
	float diff = max (.0, dot (n, ldir));
	vec3 h = normalize (-rd + ldir);
	float shiny = 100.;
	float lf = .0;
	float hf = .0;
    float fac = 1.;
    if (id == 1) {
		lf = texture (iChannel0, .5*p.xz+vec2 (.0, .5*iTime)).r;
		hf = texture (iChannel0, 2.*p.xz+vec2 (.0, 2.*iTime)).r;
		fac = lf + hf;
	}
    if (id == 2) {
		lf = texture (iChannel0, .5*p.xy+vec2(.0, .5*iTime)).r;
		hf = texture (iChannel0, 2.*p.xy+vec2(.0, 2.*iTime)).r;
		fac = lf + hf;
	}
	shiny *= fac;
	float sp = pow (max (.0, dot (n, h)), shiny);
	vec3 am = vec3 (.05);
	float ao = ao (p, n, .1, 8, .1);
	ao *= (isInside ? 1.25 : 1.);

	return ao*att*s*(am + diff*lc*li*mat + sp*vec3 (1.));
}

vec3 cam (vec2 uv, vec3 ro, vec3 aim, float zoom) {
	vec3 f =normalize (aim - ro);
	vec3 wu = vec3 (.0, 1., .0);
	vec3 r = normalize (cross (wu, f));
	vec3 u = normalize (cross (f, r));
	vec3 c = ro + f*zoom;
	return normalize (c + r*uv.x+u*uv.y - ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uvRaw = fragCoord.xy/iResolution.xy;
	vec2 uv = uvRaw*2. - 1.;
	uv.x *= iResolution.x/iResolution.y;
	uv *= 1. + .25*length (uv);

	vec3 ro = vec3 (cos (iTime), 1. + .125*(.5+.5*cos(5.*iTime)), 2.5);
	vec3 rd = cam (uv, ro, vec3 (.0), 1.7);
	int id = 0;
	vec3 pout = vec3 (.0);
	isInside = false;
	float d = march (ro, rd, id, pout);
	vec3 p = ro + d*rd;
	vec3 n = norm (p);
	vec3 col = vec3 (.0);

	vec3  lp1 = vec3 (2., 1., 2.);
	vec3  lc1 = vec3 (.9, .8, .7);
	float li1 = 3.;
	vec3  lp2 = vec3 (.7, 3., .0);
	vec3  lc2 = vec3 (.2, .2, .9);
	float li2 = 6.;
	vec3  lp3 = vec3 (-2., 2., .5);
	vec3  lc3 = vec3 (.9, .3, .2);
	float li3 = 3.;

	float fac = 1.;
	if (id == 3) {
		fac = .025;
	}

	col = fac*shade (ro, rd, d, n, lp1, lc1, li1, id, pout);
	col += fac*shade (ro, rd, d, n, lp2, lc2, li2, id, pout);
	col += fac*shade (ro, rd, d, n, lp3, lc3, li3, id, pout);
	if (id == 3) {
		n = normalize (n + texture (iChannel0, .125*p.xy).r);
		ro = p - .05*n;
		float ior = .7;
		rd = normalize (refract (rd, n, ior));
		isInside = true;
		d = march (ro, rd, id, pout);
		p = ro + d*rd;
		n = norm (p);
		col += shade (ro, rd, d, n, lp1, lc1, li1, id, pout);
		col += shade (ro, rd, d, n, lp2, lc2, li2, id, pout);
		col += shade (ro, rd, d, n, lp3, lc3, li3, id, pout);

		ro = p - .01*n;
		rd = normalize (refract (rd, n, ior));
		isInside = false;
		d = march (ro, rd, id, pout);
		p = ro + d*rd;
		n = norm (p);
		col += shade (ro, rd, d, n, lp1, lc1, li1, id, pout);
		col += shade (ro, rd, d, n, lp2, lc2, li2, id, pout);
		col += shade (ro, rd, d, n, lp3, lc3, li3, id, pout);
	}

	col = col / (1. + col);
	col *= 1. - .5*length(uvRaw*2.-1.);
	col = pow (col, vec3 (1./2.2));

    fragColor = vec4(col, 1.);
}

float circle(vec2 uv,float distanceAa, float distanceAb,float ditanceBa,float distanceBb,float size)
{

    float val = distance(uv, vec2(distanceAa, distanceAb)) * distance(uv, vec2(ditanceBa, distanceBb));

	val = step(val, size);

    return val;
}

float rand(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898,78.233)))*1000.*iTime);
	//return fract(sin(dot(uv, iMouse.xy))*10000.);
	//return (fract(sin(dot(uv, vec2(12., 70.)))*100000.));
    //return (fract(sin(dot(uv, vec2(12., 70.)))*43758.5453123));
}

vec2 hash( vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float gnoise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

// Simplex noise
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i); // Avoid truncation effects in permutation
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
        + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}

float noise(in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}


