#define PI 3.141596
#define PHI (sqrt(5)*0.5 + 0.5)

// simple 2d rotation matrix based on angle.
mat2 rot(float a) {
  float s = sin(a);
  float c = cos(a);

  return mat2(c, s, -s, c);
}

// From http://mercury.sexy/hg_sdf/, modulate around a circle.
vec2 modPolar(vec2 p, float reps) {
  float angle = 2.0*PI/reps;

  float a = mod(atan(p.y, p.x) + angle/2.0, angle) - angle/2.0;
  float r = length(p);

  return r*vec2(cos(a), sin(a));
}

vec2 opU(vec2 a, vec2 b) {
  return a.x < b.x ? a : b;
}

// simple box sdf.
float box(vec3 p, vec3 r) {
  vec3 q = abs(p) - r;
  return max(max(q.x, q.y), q.z);
}

// box wrapped in a 
vec2 shape(vec3 p) {
  vec3 op = p;
  p.xz = abs(p.xz) - vec2(1); // this mirrors space along a diagnal in the xz plane.  

  float a = box(p + vec3(1, 0, 0), vec3(1, 0.05, 0.1)); // stripe running from left to right.
  float b = box(p + vec3(0, 0, 1), vec3(0.1, 0.05, 1)); // running from back to front.
  float c = box(p, vec3(0.1, 1, 0.1)); // running from top to bottom.
  float d = box(op, vec3(1)); // big cube in the middle.

  vec2 s = vec2(min(a, min(b, c)), 1.0); // the gold stripes.
  vec2 t = vec2(d, 2.0); // the purplish box.

  return opU(s, t);
}

vec2 de(vec3 p) {

  vec3 op = p;

  p.zy *= rot(iTime); // rotating space, adds to the drunk feel.
  p.zy = abs(p.yz) - vec2(1.6, 1.3); // mirror space to get the fract to fill up more of the scene.
  p.x = mod(p.x + 1.0, 2.0) - 1.0; // repeat space along x axis.

  float s = 1.2;
  vec4 q = vec4(p*s, 1);

  // typical IFS coordinate deformation setup.
  for(int i = 0; i < 8; i++) {
    q.xyz = abs(q.xyz) - vec3(0.5, 0.1, 0.7); // Mirror.
    q.xz *= rot(1.1); // rotate
    q.xy *= rot(0.7); // again.

    q *= 1.4; // scale.
  }

  vec2 f = shape(q.xyz)/vec2(q.w*s, 1);

  return f;
}


// not sure where this was from, but it's a nice way of mapping a 2d texture unto a 3d object.
// Tri-Planar blending function.
vec3 mat(in vec3 p, in vec3 n, sampler2D s){

    // Tweaked to suit your needs.
    n = max(abs(n) - .2, .001); // n = max(n*n - .1, 0.001), etc.
    //n /= (n.x + n.y + n.z); // Rough normalization... I think? 
    n /= length(n); // Normalizing.

    vec3 x = texture(s, p.yz).xyz;
    vec3 y = texture(s, p.zx).xyz;
    vec3 z = texture(s, p.xy).xyz;

    return x*x*n.x + y*y*n.y + z*z*n.z;

}


// luminosity grey scale.
vec3 grey = vec3(0.212, 0.715, 0.072);

// bump mapping, perturbing normals to add some detail to geometry.
vec3 bump(vec3 p, vec3 n, sampler2D s) {
    vec2 h = vec2(0.005, 0.0);
    vec3 b = normalize(mat3(
        mat(p + h.xyy, n, s) - mat(p - h.xyy, n, s),
        mat(p + h.yxy, n, s) - mat(p - h.yxy, n, s),
        mat(p + h.yyx, n, s) - mat(p - h.yyx, n, s))*grey);

    b -= n*dot(n, b); // have no idea what this term does, but it helps smooth out the bumps around corners.

    return normalize(n + 0.1*b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord/iResolution.xy - 0.5; // converting gl_FragCoord.xy from [0, 1] to [-1, 1]
  uv.x *= iResolution.x/iResolution.y; // stretch based on resolution.

  vec3 col, bg;
  col = bg = vec3(1.0)*(1.0 - (length(uv) - 0.2)); // vinetting (darker cornders).


  float a = -3.0;
  float at = 0.3*iTime;

  vec3 ro = vec3(at, 0, 0); // camera position.
  vec3 ww = normalize(vec3(at + 1.0, 0.5*sin(at), cos(at))-ro); // camera looking at vector.
  vec3 uu = normalize(cross(vec3(sin(iTime), cos(iTime), 0), ww)); // the roll vector for the "right vector" is causing most of the drunken fly feel.
  vec3 vv = normalize(cross(ww, uu)); // up vector.
  vec3 rd = normalize(mat3(uu, vv, ww)*vec3(uv, 1.0)); // combine the 3 vectors representing the camera to get the ray direction.

  // sphere tracing, get intersection distance from pixel to geometry.
  float t = 0.0, m = -1.0, mx = 50.0;
  for(int i = 0; i < 200; i++) {
    vec2 d = de(ro + rd*t);
    if(d.x < 0.001 || t >= mx) break;
    t += d.x*0.5; // small bit of fudging to get better results.
    m = d.y;
  }

  vec3 ld = normalize(vec3(-0.5, 0, 0)); // light direction.
  vec2 e = vec2(1, -1)*0.001;

  if(t < mx) {
    vec3 p = ro + rd*t;
    vec3 n = normalize(
      e.xxx*de(p + e.xxx).x +
      e.xyy*de(p + e.xyy).x +
      e.yxy*de(p + e.yxy).x +
      e.yyx*de(p + e.yyx).x); // tetrahedral derivative to get normal.

    vec3 al = vec3(1); // al = albeido, which is color of object.
	float spo = 16.0; // spo = specular/gloss map.

    float aot = t/50.0;
    float ao = exp2(-2.0*pow(max(0.0, 1.0 - de(p + n*aot).x/aot), 2.0)); // nice fake for occulsion by using a wave-paket 2^(-x^2) to approximate a falloff at distances.
    float dif = max(0.0, dot(ld, n)); // diffuse lighting.

    p.zy *= rot(iTime); // rotate the space so the textures don't swim (more accuratly so the geometry spins while the texture mapping stands still).

    if(m < 1.5) {
      // albeido and gloss map for gold stripes.
      al = vec3(4)*mat(p, n, iChannel1);
      spo = 4.0;
    } else if(m > 1.5) {
      // albeido, gloss and bump map for purplish box.
      al = vec3(0.6, 0.3, 1)*mat(p*2.0, n, iChannel0);
      //n = bump(p*2.0, n, iChannel1);
      spo = 3.0;
    }

    float sst = 3.4;
    float sss = smoothstep(0.0, 1.0, de(p + ld*sst).x/sst); // subsurface scattering, basically this terms adds some light that bleeds through geometry based on sst.

    float sp = pow(max(0.0, dot(reflect(-ld, n), -rd)), spo); // specular light.
	float fr = pow(1.0 + dot(rd, n), 2.0); // fresnel term. I don't understand this, but it works.


    col = mix(vec3(al*ao*(dif + sss + sp)), al, fr); // mix it all together, usually sp is outside the al*ao term, but Idk what I'm doing.
  }

  col = mix(col, bg, 1.0 - exp(-0.3*t)); // some fog, make the scene look bigger, and hide popin from.
  fragColor = vec4(pow(col, vec3(0.45)), 1); // the pow(col, vec3(0.45)) is gamma correction, used to make sure different monitors see roughly the same colors.
}

#define hue(h) abs(fract(h + vec3(3,2,1)/3.) * 6. - 3.) -1.

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

