float biToUni(float v)
{
    return (v + 1.0) * 0.5;
}

// Power curve
// http://www.iquilezles.org/www/articles/functions/functions.htm
float pcurve(in float x, in float a, in float b)
{
	float k = pow(a + b, a + b) / (pow(a , a) * pow(b , b));
	return k * pow(x, a) * pow(1.0 - x, b);
}

float maskIt(in float mask, in float v0, in float v1)
{
	return mask * v0 + (1.0 - mask) * v1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float nTiles = 3.0;
	float t = iTime * 0.125;
	vec2 st = gl_FragCoord.xy / iResolution.xy;

	// Create tile mask
	vec2 mst = fract(st * nTiles);
	float curve = biToUni(sin(iTime * TAU)) * 2.0 + 0.25;
	float vx = pcurve(mst.x, curve, curve);
	float vy = pcurve(mst.y, curve, curve);
	float mask = min(vx, vy);
	mask = step(0.5, mask);

	// Diagonal Stripes
	float rowst = biToUni(sin((st.x - st.y + t) * TAU * 4.0));
	rowst = step(0.5, rowst);
	float colst = biToUni(cos((st.y + st.x + t) * TAU * 8.0));
	colst = step(0.5, colst);
	float value = maskIt(mask, rowst, colst);

    // Output
	vec3 color = vec3(value);
	fragColor = vec4(color, 1.0);
}

#define E 2.71828

//ray data, used to get the pixel color by sending (or casting) a ray per pixel into the scene
struct ray
{
	vec3 origin;
    vec3 direction;
};

//material represents the way light bounces off the surface of a primitive
struct material
{
	float kSpecular;
    float kDiffuse;
    float glossiness;
};

//one-color sphere
struct sphere
{
	vec3 origin;
    vec3 color;
    float radius;
    material mat;
};

//point light
struct light
{
    vec3 origin;
  	float intensity;
};

struct directionalLight
{
    vec3 direction;
    float intensity;
};

struct intersection
{
    //distance from the origin of the ray to the intersection
    float dist;
    //color of the primitive at the intersection
    vec3 color;
    //normal of the primitive at the intersection
    vec3 normal;
    //material of the primitive at the intersection
    material mat;
};

const float renderDistance = 9999.0;

//return value if a ray misses a primitive
const intersection miss = intersection(renderDistance, vec3(.0), vec3(.0), material(.0, .0, .0));



const int sphereCount = 6;

const int lightCount = 3;

const float ambientLight = .1;

const vec3 WHITE = vec3(1.0);
const vec3 GREEN = vec3(.0, 1.0, .0);
const vec3 RED = vec3(1.0, .0,.0);
const vec3 TEAL = vec3(.0, 1.0, 1.0);
const vec3 YELLOW = vec3(1.0, 1.0, .0);


//gets the intersection between a ray and a sphere
//from : https://www.scratchapixel.com/lessons/3d-basic-rendering/minimal-ray-tracer-rendering-simple-shapes/minimal-ray-tracer-rendering-spheres
intersection sphereIntersect(in ray pRay, in sphere pSphere)
{
    float radius2 = pSphere.radius*pSphere.radius;
    vec3 L = pSphere.origin - pRay.origin; 
    float tca = dot(L, pRay.direction); 
    float d2 = dot(L,L) - tca * tca; 
    if (d2 > radius2) return miss; 
    float thc = sqrt(radius2 - d2); 
    float t0 = tca - thc;
    float t1 = tca + thc;
    float t = min(t0, t1);

    vec3 hit = pRay.origin + pRay.direction * t;
    vec3 coords = hit - pSphere.origin;
	vec2 uv = vec2(atan(coords.z, coords.x), acos(coords.y/pSphere.radius));

    vec3 color = texture(iChannel0, uv).rgb;

    //return the distance from the ray's origin to the point
    return intersection(t, color*pSphere.color, normalize(pSphere.origin - hit), pSphere.mat);
}

float lightAura(in ray pRay, in light pLight, sphere[sphereCount] spheres)
{
	//distance from the ray's origin to the light's origin
    float distanceToLightOrigin = distance(pLight.origin, pRay.origin);

    //projection of the ray on the light, aka the point on the ray which is the closest to the light's origin
    vec3 rayProjection = pRay.origin + pRay.direction * distanceToLightOrigin;

    return pLight.intensity /(distance(pLight.origin, rayProjection)*100.0);
}

//mixes two intersection
//Think of this as a more optimized way of saying :
//if(mixRatio = 0) return a, else return b
intersection mixIntersections(intersection a, intersection b, float mixRatio)
{
	return intersection
            (
            mix(a.dist, 	b.dist, 	mixRatio),
            mix(a.color, 	b.color, 	mixRatio),
            mix(a.normal, 	b.normal, 	mixRatio),
            material
                (
                mix(a.mat.kSpecular, 	b.mat.kSpecular, 	mixRatio),
                mix(a.mat.kDiffuse, 	b.mat.kDiffuse, 	mixRatio),
                mix(a.mat.glossiness, 	b.mat.glossiness, 	mixRatio)
				)
            );
}

float getDiffuse(intersection pIntersection, vec3 lightDirection)
{
    //clamp the dot product of the normal of the point and of the origin of the light
	return clamp(dot(pIntersection.normal, lightDirection), .0, 1.0) * pIntersection.mat.kDiffuse;
}


float getSpecular(vec3 point, intersection pIntersection, ray pRay, vec3 lightDirection)
{
    //reflection of the light on the surface
    vec3 reflection = reflect(lightDirection, pIntersection.normal);

    //specular term
    //Thanks to UglySwedishFish#3207 on discord for their help with this :
    //dot products range from -1 to 1, so the dot product of the reflection and the ray's direction
    //has to be clamped so that a negative value doesn't get squared into a positive value
    return pow(clamp(dot(normalize(reflection), pRay.direction), .0, 1.0), pIntersection.mat.glossiness) * pIntersection.mat.kSpecular;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv    = fragCoord.xy / iResolution.xy - vec2(0.5);
         uv.x *= iResolution.x / iResolution.y;

    directionalLight SUN = directionalLight(normalize(vec3(-.5, -1.5, 1.5)), 2.0);

    ray currentRay = ray(vec3(uv.xy, .0), normalize(vec3(uv, 1.))); 

    material mat = material(.7, .2, 32.);
    vec3 col = vec3(1.);

    vec2 path = vec2(.0);
    path.x += sin(time);
    path.x += sin(time*2.);
    path.x += 2.0*cos(time*.1);
    path.x *= sin(time);

    path.y += sin(time*.1);
    path.y += cos(time);


    //sphere definitions
    sphere spheres[sphereCount] = sphere[sphereCount]
	(
		sphere(vec3(cos(time), .2, 4.0), 	WHITE,	1., mat),
	    sphere(vec3(-path.x + 1.0, -path.y, 5.0), 		WHITE,	1.0,mat),
        sphere(vec3(sin(time)*2.0, 2.0, 4.0), 		TEAL,	1.5,mat),
        sphere(vec3(2.0, -1.0, 10.0), 				TEAL,	.8, mat),
        sphere(vec3(path.x, path.y, 10.0), 	GREEN,	1., mat),
        sphere(vec3(.0, .0, 8.0), 					RED,	1.0,mat)
	);

    //light definitions
    light lights[lightCount] = light[lightCount]
    (
        light(vec3(1.0, .2, 10.0 + abs(sin(iTime)*10.0)), 5.),
        light(vec3(.0, -2.0, 10.0), 7.),
        light(vec3(1.0, 1.0, .0), 20.)
    );    

    intersection finalIntersection = miss;

    //for every sphere, see whether the ray intersects it
    for(int i = 0; i < sphereCount; i++)
    {
        //sphere intersection returns the distance at which the sphere is intersected
        intersection currentIntersection = sphereIntersect(currentRay, spheres[i]);

        //if the current distance is smaller than the final distance
        //set the final intersection to be the current intersection
        float distanceSmaller = float(currentIntersection.dist < finalIntersection.dist);
        finalIntersection = mixIntersections(finalIntersection, currentIntersection, distanceSmaller);
    }

    //whether the ray missed or not
    bool rayMissed = finalIntersection.dist == miss.dist;

    //the point where the ray hit a primitive
    vec3 hit = currentRay.origin + currentRay.direction * finalIntersection.dist;

    vec3 finalCol = vec3(.0);
    vec3 aura = vec3(.0);

    //sun light
    finalCol += min(finalIntersection.color * 
        (
            getDiffuse(finalIntersection, SUN.direction) + 
            getSpecular(hit, finalIntersection, currentRay, SUN.direction)
            + ambientLight
        ) * 
        SUN.intensity ,1.0)*
        clamp(dot(finalIntersection.normal, SUN.direction), ambientLight, 1.0);

    for(int i = 0; i < lightCount; i++)
    {        
        //the light's intensity is its intensity variable divided by its attenuation over distance
        float lightIntensity = lights[i].intensity / pow(distance(hit, lights[i].origin), 2.0);                
        vec3 lightDirection = normalize(hit - lights[i].origin);

        //final color
    	finalCol +=
        	//color of the primitive
        	finalIntersection.color *
            //lighting terms
        	(
            getDiffuse(finalIntersection, lightDirection)
            + getSpecular(hit, finalIntersection, currentRay, lightDirection)
            + ambientLight
            ) *
            //intensity of the light
            lightIntensity *
            clamp(dot(finalIntersection.normal, lightDirection), ambientLight, 1.0);
        aura += lightAura(currentRay, lights[i], spheres)* float(finalIntersection.dist > distance(lights[i].origin, currentRay.origin));
    }

	//background color
	vec3 bgCol = vec3(ambientLight) + pow(E, 1.0 - 1.0/ pow(min(dot(currentRay.direction, SUN.direction), .0), 2.0)) * YELLOW;
    bgCol = clamp(bgCol, .0, 1.0);
    //select between the background color (if the ray missed) and the final color (if the ray hit)
    finalCol = mix(finalCol, bgCol, float(rayMissed));
    finalCol += aura;

    // Output to buffer
    fragColor = vec4(finalCol, 1.0);
}

#ifdef GL_ES
precision mediump float;
#endif

uniform vec2 u_resolution;
uniform vec2 u_mouse;

float sdcircle(vec2 st,vec2 origin,float r){
  return length(st-origin)-r;
}

vec2 floor2d(vec2 st){
  return vec2(floor(st.x),floor(st.y));
}

vec2 fract2d(vec2 st){
  return vec2(fract(st.x),fract(st.y));
}

vec2 random2d(vec2 st){
  float r1=fract(sin(dot(st.xy,vec2(127.9898,311.233)))*43758.5453123);
  float r2=fract(sin(dot(st.xy,vec2(269.5,183.3)))*43758.5453123);
  return 2.*vec2(r1,r2)-1.;
}

float noise(vec2 st){
  vec2 i=floor2d(st);
  vec2 f=fract2d(st);

  vec2 a=random2d(i);
  vec2 b=random2d(i+vec2(1,0));
  vec2 c=random2d(i+vec2(0,1));
  vec2 d=random2d(i+vec2(1,1));

  vec2 u=smoothstep(0.,1.,f);

  float e=mix(dot(a,f),dot(b,f-vec2(1,0)),u.x);
  float g=mix(dot(c,f-vec2(0,1)),dot(d,f-vec2(1,1)),u.x);
  float h=mix(e,g,u.y);
  return h;
}

float fbm(vec2 st){
  const int OCTAVES=10;
  // Initial values
  float value=0.;
  float amplitude=.5;
  //
  // Loop of octaves
  for(int i=0;i<OCTAVES;i++){
    value+=amplitude*abs(noise(st));
    st*=2.;
    amplitude*=.5;
  }
  return value;
}

vec2 distort(vec2 uv){
  return uv+fbm(uv+vec2(.24,.025)+iTime*.125);
}

vec2 distort2(vec2 uv){
  return uv+fbm(uv+vec2(.12,.52)+iTime*.324);
}

vec4 wave(vec2 uv){
  uv=uv*2.;
  vec2 p=distort(uv);
  p=distort(p);
  float c=fbm(p);
  c=c*4.-.2;
  vec4 color=vec4(vec3(c),1.);
  color=color*vec4(1.,.5,.3,1.);
  color=1.-color;
  color.w=1.;
  return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 uv=fragCoord/iResolution.xy-.5;
  uv=uv*1.;
  vec4 wave1=wave(uv);
  vec4 wave2=wave(vec2(uv.x,-uv.y)+1.5);
  vec4 wave3=wave(vec2(-uv.x,-uv.y)+3.);
  vec4 wave4=wave(vec2(-uv.x,-uv.y)+4.5);
  fragColor=.25*wave1+.25*wave2+.25*wave3+.25*wave4;
}

const float PI = 3.14150265359;

// hash() and noise() is from Inigo Quilez "Noise - value - 3D"
// https://www.shadertoy.com/view/4sfGzS
float hash(vec3 p)  // replace this by something better
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash(p+vec3(0,0,0)), 
                        hash(p+vec3(1,0,0)),f.x),
                   mix( hash(p+vec3(0,1,0)), 
                        hash(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(p+vec3(0,0,1)), 
                        hash(p+vec3(1,0,1)),f.x),
                   mix( hash(p+vec3(0,1,1)), 
                        hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}

float fbm(in vec3 p){
    float res = 0.;
    vec3 q = p;
    float amp = 1.0;//
    for(int i=0;i<5;i++){
		float fi = float(i);
        res += amp*noise(q);
        amp *= 0.5;//
        q*=2.0;
    }
    return res;
}

float sdPlane(in vec3 p, in float h){
    return p.y - h;
}
vec2 map(in vec3 p){
    float h = fbm(vec3(p.x, 0., p.z));
    return vec2(sdPlane(p, h), 0.);
}

vec2 castRay(in vec3 ro, in vec3 rd){
    float minD = 0.0;
    float maxD = 10.0;
    float thr = 0.00001;// 0.001 -> 0.00001

    float d=minD, m=0.0;
    for(int i=0;i<64;i++){
        vec3 pos = ro+rd*d;
        vec2 tmp = map(pos);
        if(tmp.x<thr || maxD<tmp.x) break;
        d += tmp.x*0.5;// d+=tmp.x; -> d+=tmp.x*0.5;
        m = tmp.y;
    }
    if(maxD<d) m=-1.0;
    return vec2(d, m);
}

vec3 calcNormal(in vec3 p){
    vec2 eps = vec2(1.0, -1.0)*0.001;
    return normalize(vec3(
    	eps.xyy*map(p+eps.xyy).x +
    	eps.yxy*map(p+eps.yxy).x +
    	eps.yyx*map(p+eps.yyx).x +
    	eps.xxx*map(p+eps.xxx).x 
    ));
}

vec3 render(in vec3 ro, in vec3 rd){
    vec2 res = castRay(ro, rd);
    float d = res.x, m=res.y;

    vec3 pos = ro + rd*d;
    vec3 nor = calcNormal(pos);

    vec3 lightDir = vec3(1.0);

    vec3 col = clamp(vec3(dot(lightDir, nor)), 0.0, 1.0);
	col *= vec3(0.3, 0.15, 0.02)*noise(pos);

    //col = mix(col, vec3(1.0), clamp(d/10.0, 0., 1.0));
    col = mix(col, vec3(1.0), 1.0-exp(-0.03*d*d));// use gaussian

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord*2.0-iResolution.xy)/min(iResolution.x, iResolution.y);

    vec3 eye = vec3(0., 2.0+0.1*sin(iTime), -iTime);
    vec3 dir = normalize(vec3(p, -1.0));
    float an = -PI*0.1;
    dir.yz *= mat2(cos(an), -sin(an), sin(an), cos(an));

    vec3 col = render(eye, dir);

	fragColor = vec4(col,1.0);
}

