    float cr1 = circle(cruv-offset, mix(.05, .06, r.x));

    float innerMask = mask-.017;
    mask = max(mask, cr1);
    mask = min(mask, -(muv.x-.5));

    float stripesMask = innerMask-mix(.005, .015, noise(uv*10.));
